    setNameSlug(account.app.name_slug)
  }

  const makeRequest = async() => {
    const headers = headersArray.reduce((acc, header) => {
      if (header.name && header.value) {
        acc[header.name] = header.value
      }
      return acc
    }, {})
    const resp = await makeAppRequest(apn, url, nameSlug, {
      method,
      headers,
      body,
    })
    setResponseBody(JSON.stringify(resp, null, 2))
  }

  const addHeader = () => {
    setHeadersArray([...headersArray, {}])
  }

  const inputStyle = {
    border: "1px solid #ccc",
    padding: "8px",
    borderRadius: "4px",
  }

  const onChangeHeaderName = (e) => {
    headersArray[e.target.id].name = e.target.value
    setHeadersArray([...headersArray])
  }

  const onChangeHeaderValue = (e) => {
    headersArray[e.target.id].value = e.target.value
    setHeadersArray([...headersArray])
  }


  return (
    <main className="p-5 flex flex-col gap-2 max-w-5xl">
      {
        externalUserId && 
        <div className="mb-48">
          {externalAccounts ?
            <div>
              <h1>Accounts List</h1>
              <table border="1" cellPadding="5">
                <thead>
                  <tr>
                    <th/>
                    <th>pd id</th>
                    <th>app</th>
                    <th>app slug</th>
                    <th>auth type</th>
                  </tr>
                </thead>
                <tbody>
                  {externalAccounts.map((account) => (
                    <tr key={account.id}>
                      <td>
                <input
                  type="radio"
                  name="userSelection"
                  value={account.id}
                  checked={apn === account.id}
                  onChange={() => handleRadioChange(account)}
                />
                      </td>
                      <td>{account.id}</td>
                      <td>{account.app.name}</td>
                      <td>{account.app.name_slug}</td>
                      <td>{account.app.auth_type}</td>
                    </tr>
                  ))} 
                </tbody>
              </table>
              <p>
                <span>selected account: </span>
                <span> {apn}</span>
              </p>
            {testRequestConfig && false &&
            <CodePanel
              language="typescript"
              code={
`// this is an example of the fetch request for the app
${testRequestConfig.authType === "bearer" ? "// use the oauth_access_token for your account in the authorization header": "use the given values for your account in the authorization header with Basic auth"}
const config = {
  method: "${testRequestConfig.config.method}",
  headers: ${JSON.stringify(testRequestConfig.config.headers, null, 2).replace(/\n/g, '\n  ')},
}
const resp = await fetch("${testRequestConfig.url}", config)
`}
            />}



      <h1>Fetch Request Builder</h1>
        <div style={{display: "flex", alightItems: "center", gap: "10px"}}>
          <label htmlFor="method">Method:</label>
          <select
            id="method"
            value={method}
            onChange={(e) => setMethod(e.target.value)}
            style={inputStyle}
          >
            <option value="GET">GET</option>
            <option value="POST">POST</option>
            <option value="PUT">PUT</option>
            <option value="DELETE">DELETE</option>
            <option value="PATCH">PATCH</option>
          </select>
          <label htmlFor="url">URL:</label>
          <input
            type="text"
            id="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            style={inputStyle}
          />
        </div>

        <div>
          <label htmlFor="headersArray">Headers:</label>
          <button className="bg-blue-500 hover:bg-blue-400 text-white py-2 px-4 rounded" onClick={addHeader}>Add Header</button>
          {headersArray.map((header, index) => (
            <div key={`header_${index}`}>
              <span class="mr-2">Name</span>
              <input key={`name_${index}`} id={index} value={headersArray[index].name} style={inputStyle} class="mr-2" onChange={onChangeHeaderName}/>
              <span class="mr-2">Value</span>
              <input key={`value_${index}`} id={index} value={headersArray[index].value} style={inputStyle} class="mr-2" onChange={onChangeHeaderValue}/>
            </div>
          ))}
        </div>



        {method !== "GET" && (
          <div>
            <label htmlFor="body">JSON Body:</label>
            <textarea
              id="body"
              value={body}
              onChange={(e) => setBody(e.target.value)}
              rows={5}
            />
          </div>
        )}

              <button className="bg-blue-500 hover:bg-blue-400 text-white py-2 px-4 rounded" onClick={makeRequest}>Make Request</button>
              {
                responseBody &&
                <div>
                  <span>Response Body:</span>
                  <pre>{responseBody}</pre>
                </div>
              }
            </div>
            : <div>
              <p className="mb-8">
              </p>
            </div>
          }
        </div>
      }
    </main>
  );
}

"use client"

import { AppResponse, BrowserClient } from "@pipedream/sdk/browser";
import { useSearchParams } from "next/navigation";
import { useEffect, useState } from "react";

import CodePanel from "./CodePanel";
import { getAppInfo, serverConnectTokenCreate } from "./server"


const frontendHost = process.env.NEXT_PUBLIC_PIPEDREAM_FRONTEND_HOST || "pipedream.com"

export default function Home() {
  const [externalUserId, setExternalUserId] = useState<string | null>(null);
  const [token, setToken] = useState<string | null>(null)
  const [connectLink, setConnectLink] = useState<string | null>(null)
  const [expiresAt, setExpiresAt] = useState<string | null>(null)
  const [apn, setAuthProvisionId] = useState<string | null>(null)
  const [selectedApp, setSelectedApp] = useState<AppResponse | null>(null);
  const [appSlug, setAppSlug] = useState<string>("");
  const [error, setError] = useState<string>("");
  const [pd, setPd] = useState<BrowserClient | null>(null);
  const [oauthAppId, setOauthAppId] = useState<string>("");
  const [isOAuthConfirmed, setIsOAuthConfirmed] = useState(false);

  useEffect(() => {
    // This code only runs in the browser
    async function loadClient() {
      const { createFrontendClient } = await import('@pipedream/sdk/browser');
      const client = createFrontendClient({ frontendHost });
      setPd(client);
    }

    loadClient();
  }, []);

  const searchParams = useSearchParams()

  const docsConnect = "https://pipedream.com/docs/connect/"
  const docsTokenCreate =
    "https://pipedream.com/docs/connect/quickstart#generate-a-short-lived-token"
  const frontendSDKDocs = "https://pipedream.com/docs/connect/quickstart#use-the-pipedream-sdk-in-your-frontend"
  const connectOauthDocs = "https://pipedream.com/docs/connect/oauth-clients"

  interface ConnectResult {
    id: string
  }

  interface ConnectConfig {
    app: string
    token: string
    oauthAppId?: string
    onSuccess: (result: ConnectResult) => void
  }

  const connectApp = (appSlug: string) => {
    if (!externalUserId) {
      throw new Error("External user ID is required.");
    }
    if (!token) {
      throw new Error("Token is required.");
    }
    setAppSlug(appSlug)

    // Only include oauthAppId if it's actually provided
    const connectConfig: ConnectConfig = {
      app: appSlug,
      token,
      onSuccess: ({ id }: ConnectResult) => {
        setAuthProvisionId(id)
      }
    }

    // Add oauthAppId to config only if it exists
    if (oauthAppId) {
      connectConfig.oauthAppId = oauthAppId;
    }

    if (!pd) {
      console.error("Pipedream SDK not loaded")
      return
    }
    pd.connectAccount(connectConfig)
  }

  const connectAccount = async () => {
    if (!selectedApp) return
    await connectApp(selectedApp.name_slug)
  }

  useEffect(() => {
    const uuid = searchParams.get("uuid") ? searchParams.get("uuid") : crypto.randomUUID()
    if (!uuid) {
      console.error("No external user ID provided")
      return
    }
    setExternalUserId(uuid);

    // Create token immediately after setting external user ID
    (async () => {
      try {
        const { token, connect_link_url, expires_at } = await serverConnectTokenCreate({
          external_user_id: uuid,
        })
        setToken(token)
        setConnectLink(connect_link_url)
        setExpiresAt(expires_at)
      } catch (error) {
        console.error("Error creating token:", error)
      }
    })()
  }, []);

  const handleAppSlugChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setAppSlug(e.target.value)
    setError("")
  }

  const normalizeAppSlug = (slug: string): string => {
    return slug.trim().replace(/-/g, '_')
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!appSlug.trim()) {
      setError("Please enter an app slug");
      return;
    }
    if (!token) {
      setError("No token available");
      return;
    }

    const normalizedSlug = normalizeAppSlug(appSlug);
    try {
      const response = await getAppInfo(normalizedSlug);
      // console.log('App Info:', response);
      setSelectedApp(response); // This is the key change - access the data property
    } catch (err) {
      console.error("Error:", err);
      setError(`Couldn't find the app slug, ${normalizedSlug}`);
    }
  }

  // Add handler for OAuth App ID input
  const handleOAuthAppIdChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    setOauthAppId(e.target.value);
    setError("");
  };

  // Add handler for OAuth app ID submission
  const handleOAuthSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setIsOAuthConfirmed(true);
  };

  // Reset OAuth confirmed state when app changes
  useEffect(() => {
    setIsOAuthConfirmed(false);
    setOauthAppId("");
  }, [selectedApp]);

  return (
    <main className="p-5 flex flex-col gap-2 max-w-6xl mb-48">
      {externalUserId && (
        <div>
          <h1 className="text-title mb-8">Pipedream Connect Example App</h1>
          <div className="mb-4 text-body">
            <p className="mb-4">Refer to the <a href={docsConnect} target="_blank" rel="noopener noreferrer" className="hover:underline text-blue-600">Pipedream Connect docs</a> for a full walkthrough of how to configure Connect for your site.</p>
            <p>When your customers connect accounts with Pipedream, you'll pass their unique user ID in your system — whatever you use to identify them. In this example, we generate a random external user ID for you:
              <span className="text-code font-bold"> {externalUserId}.</span>
            </p>
          </div>
          <div className="border border-b mb-4"></div>

          <div className="mb-8">
            <p className="text-body">In <code>server.ts</code>, the app calls <code>serverConnectTokenCreate</code> to create a short-lived token for the user. You&apos;ll use that token to initiate app connection requests from your site securely. <a href={docsTokenCreate} target="_blank" rel="noopener noreferrer" className="hover:underline text-blue-600">See docs</a>.</p>
          </div>
          <div className="mb-8">
            <CodePanel
              language="typescript"
              code={`import { serverConnectTokenCreate } from "@pipedream/sdk";

const { token, expires_at } = await serverConnectTokenCreate({
  external_user_id: "${externalUserId}", // The end user's ID in your system, this is an example
})`}
            />
          </div>

          {token && (
            <div className="mb-4 text-gray-600">
              <p>
                <span className="font-semibold">Connect Token:</span>
                <span className="font-code"> {token}; </span>
                <span className="font-semibold"> expiry: </span>
                <span className="font-code">{expiresAt}</span>
              </p>
            </div>
          )}

          <div className="py-2">
            <p className="text-subtitle pb-2">Enter an app name slug</p>
            <p className="text-body">Find the app you want to connect to here: <a target="_blank" rel="noopener noreferrer" className="hover:underline text-blue-600" href="https://pipedream.com/apps">https://pipedream.com/apps</a>, then copy the `name_slug` either from the <span className="font-semibold">Authentication</span> section of the app&apos;s page or from the URL (e.g., `google_sheets`).</p>
          <form onSubmit={handleSubmit} className="flex gap-2 max-w-md py-4">
            <input
              className="shadow appearance-none border rounded w-64 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              id="app-slug"
              type="text"
              placeholder="app_slug"
              value={appSlug}
              onChange={handleAppSlugChange}
            />
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white p-2 px-4 rounded"
            >
              Continue
            </button>
          </form>
          {error && <p className="text-error mt-2">{error}</p>}
        </div>

        {selectedApp && (selectedApp as AppResponse).auth_type === 'oauth' && (
          <div className="py-2">
            <form onSubmit={handleOAuthSubmit}>
              <p className="text-subtitle pb-2">Enter an OAuth App ID for {appSlug} <span className="font-light">(optional)</span></p>
              <p className="text-body">To use Pipedream&apos;s OAuth client, click <span className="font-semibold">Continue</span>. To use your own, <a target="_blank" rel="noopener noreferrer" className="hover:underline text-blue-600" href={connectOauthDocs}>configure it in the Pipedream UI</a> and paste the `oauth_app_id` below, then click <span className="font-semibold">Continue</span>.</p>
              <div className="flex gap-2 my-4 max-w-md">
                <input
                  className="shadow appearance-none border rounded w-64 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  id="oauth-app-id"
                  type="text"
                  placeholder="oa_xxxxxx"
                  value={oauthAppId}
                  onChange={handleOAuthAppIdChange}
                />
                <button
                  type="submit"
                  className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded"
                >
                  Continue
                </button>
              </div>
            </form>
          </div>
        )}

        {selectedApp && (
          (selectedApp as AppResponse).auth_type !== 'oauth' || isOAuthConfirmed
        ) && (
          <>
            <div className="border border-b my-2"></div>

              <div className="my-8">
                <h2 className="text-title mb-4">Connect your account</h2>
                <div className="my-4">
                  <p className="text-subtitle">Option 1: Connect Link</p>
                  <div className="text-body">
                    <span>Provide a hosted page via URL to your users to connect their account. This is useful if you aren&apos;t able to execute JavaScript or open an iFrame from your site. </span>
                    <span className="font-semibold">Note that this URL can only be used once, since Connect tokens are one-time use. </span>
                    <span><a target="_blank" rel="noopener noreferrer" className="hover:underline text-blue-600" href="https://pipedream.com/docs/connect/connect-link">See the docs</a> for more info.</span>
                  </div>
                  {connectLink && (
                    <a 
                      href={`${connectLink}&app=${selectedApp.name_slug}${oauthAppId ? `&oauthAppId=${oauthAppId}` : ''}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="font-mono hover:underline text-blue-600 block mt-2"
                    >
                      {connectLink}&app={selectedApp.name_slug}{oauthAppId ? `&oauthAppId=${oauthAppId}` : ''}
                    </a>
                  )}
                </div>
                <div className="mt-8">
                  <p className="text-subtitle">Option 2: Connect via SDK</p>
                  <p className="text-body">Use the SDK to open a Pipedream iFrame directly from your site (<a target="_blank" rel="noopener noreferrer" className="hover:underline text-blue-600" href={frontendSDKDocs}>see docs</a>).</p>
                  <button 
                    className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded mt-2"
                    onClick={connectAccount}
                  >
                    Connect your {selectedApp.name_slug} account
                  </button>
                </div>
                <p className="my-4 text-body">
                  You&apos;ll call <code>pd.connectAccount</code> with the token and the <code>app_slug</code> of the app you&apos;d like to connect:
                </p>
                <div className="mb-8">
                  <CodePanel
                    language="typescript"
                    code={`import { createFrontendClient } from "@pipedream/sdk/browser"

const pd = createFrontendClient();
pd.connectAccount({
  app: "${selectedApp.name_slug}", // The app name to connect to
  oauthAppId: ${oauthAppId ? `"${oauthAppId}",` : ''}, // ${oauthAppId ? 'Using custom OAuth client' : 'Defaults to Pipedream\'s OAuth client if omitted'}
  token: "${token || '[TOKEN]'}",
  onSuccess: ({ id: accountId }) => {
    console.log('Account successfully connected: ${apn || '{accountId}'}');
  }
})`}
                  />
                </div>
              </div>
            </>
          )}
        </div>
      )}
    </main>
  );
}

/**
 * Prisma middleware for multi-tenant data security
 * 
 * This middleware automatically applies tenant-based filtering to queries
 * to ensure data isolation between different organizations and users.
 * It enforces row-level security based on the user's context.
 */

import { Prisma } from '@prisma/client';

/**
 * Context interface for tenant filtering
 */
export interface TenantContext {
  userId?: string | null;
  organizationId?: string | null;
  teamId?: string | null;
  includePublic?: boolean;
  isAdmin?: boolean;
}

/**
 * Models that have tenant-specific fields
 */
export enum TenantModel {
  Workflow = 'Workflow',
  AIPrompt = 'AIPrompt',
  AITrainingData = 'AITrainingData',
  KnowledgeBase = 'KnowledgeBase',
  KnowledgeDocument = 'KnowledgeDocument',
  ApiKey = 'ApiKey',
  Comment = 'Comment',
  VoiceAnalytics = 'VoiceAnalytics',
  VoiceInteraction = 'VoiceInteraction'
}

/**
 * Mapping of models to their tenant-specific fields
 */
const MODEL_TENANT_FIELDS: Record<string, string[]> = {
  [TenantModel.Workflow]: ['organizationId', 'createdById', 'teamId'],
  [TenantModel.AIPrompt]: ['userId', 'organizationId'],
  [TenantModel.AITrainingData]: ['userId', 'organizationId'],
  [TenantModel.KnowledgeBase]: ['ownerId', 'teamId', 'organizationId'],
  [TenantModel.KnowledgeDocument]: ['createdById', 'knowledgeBaseId'],
  [TenantModel.ApiKey]: ['userId'],
  [TenantModel.Comment]: ['userId'],
  [TenantModel.VoiceAnalytics]: ['userId'],
  [TenantModel.VoiceInteraction]: ['userId']
};

/**
 * Mapping of models to their "public" field (if available)
 */
const MODEL_PUBLIC_FIELDS: Record<string, string> = {
  [TenantModel.Workflow]: 'isPublic',
  [TenantModel.AIPrompt]: 'isPublic',
  [TenantModel.KnowledgeBase]: 'isPublic'
};

/**
 * Creates the tenant filter middleware
 * 
 * @param tenantContext - The tenant context for filtering
 * @returns A Prisma middleware function
 */
export function createTenantMiddleware(tenantContext: TenantContext) {
  return async (
    params: Prisma.MiddlewareParams,
    next: (params: Prisma.MiddlewareParams) => Promise<any>,
  ): Promise<any> => {
    // Skip middleware for non-database operations or if admin
    if (params.action === 'queryRaw' || params.action === 'executeRaw' || tenantContext.isAdmin === true) {
      return next(params);
    }

    // Get the model name from the params
    const modelName = params.model;

    // If this is not a tenant-filtered model, proceed without filtering
    if (!modelName || !MODEL_TENANT_FIELDS[modelName]) {
      return next(params);
    }

    // Apply tenant filters based on operation type
    if (['findUnique', 'findFirst', 'findMany', 'count', 'aggregate'].includes(params.action)) {
      // Clone params for modification
      const modifiedParams = { ...params };

      // Create tenant filter conditions
      const tenantFilter = createTenantFilter(modelName, tenantContext);

      if (tenantFilter) {
        // For findUnique, convert to findFirst with added tenant conditions
        if (params.action === 'findUnique') {
          modifiedParams.action = 'findFirst';
          modifiedParams.args.where = {
            AND: [
              params.args.where,
              tenantFilter
            ]
          };
        } else {
          // For other read operations, add tenant filter to existing where clause
          modifiedParams.args.where = modifiedParams.args.where || {};
          modifiedParams.args.where = {
            AND: [
              modifiedParams.args.where,
              tenantFilter
            ]
          };
        }
      }

      // Execute with modified params
      return next(modifiedParams);
    }

    // For create operations, ensure tenant IDs are set
    if (params.action === 'create') {
      // Ensure organization ID is set
      if (MODEL_TENANT_FIELDS[modelName].includes('organizationId') && tenantContext.organizationId) {
        params.args.data.organizationId = params.args.data.organizationId || tenantContext.organizationId;
      }

      // Ensure user ID is set
      if (MODEL_TENANT_FIELDS[modelName].includes('userId') || 
          MODEL_TENANT_FIELDS[modelName].includes('createdById')) {
        const field = MODEL_TENANT_FIELDS[modelName].includes('userId') ? 'userId' : 'createdById';
        if (tenantContext.userId) {
          params.args.data[field] = params.args.data[field] || tenantContext.userId;
        }
      }

      // Ensure team ID is set if applicable
      if (MODEL_TENANT_FIELDS[modelName].includes('teamId') && tenantContext.teamId) {
        params.args.data.teamId = params.args.data.teamId || tenantContext.teamId;
      }
    }

    // For update/delete operations, ensure tenant isolation
    if (['update', 'updateMany', 'delete', 'deleteMany'].includes(params.action)) {
      const tenantFilter = createTenantFilter(modelName, tenantContext);

      if (tenantFilter) {
        // Clone params for modification
        const modifiedParams = { ...params };

        // For single record operations, convert to many with added tenant conditions
        if (params.action === 'update') {
          modifiedParams.action = 'updateMany';
          modifiedParams.args.where = {
            AND: [
              { id: params.args.where.id },
              tenantFilter
            ]
          };
        } else if (params.action === 'delete') {
          modifiedParams.action = 'deleteMany';
          modifiedParams.args.where = {
            AND: [
              { id: params.args.where.id },
              tenantFilter
            ]
          };
        } else {
          // For many record operations, add tenant filter
          modifiedParams.args.where = {
            AND: [
              modifiedParams.args.where || {},
              tenantFilter
            ]
          };
        }

        // Execute with modified params
        const result = await next(modifiedParams);

        // For converted operations, adjust the return format
        if (params.action === 'update' || params.action === 'delete') {
          if (result.count === 0) {
            throw new Error(`No ${modelName} record found with the specified ID or you don't have permission to access it`);
          }

          // For update, fetch the updated record to return
          if (params.action === 'update') {
            const updatedRecord = await (params.model as any).findUnique({
              where: { id: params.args.where.id },
              ...params.args.select ? { select: params.args.select } : {},
              ...params.args.include ? { include: params.args.include } : {}
            });

            if (!updatedRecord) {
              throw new Error(`No ${modelName} record found after update`);
            }

            return updatedRecord;
          }

          // For delete, return 1 deleted count
          return { count: 1 };
        }

        return result;
      }
    }

    return next(params);
  };
}

/**
 * Creates a tenant filter condition for queries
 * 
 * @param modelName - The model being queried
 * @param context - The tenant context
 * @returns A where condition object or null
 */
function createTenantFilter(modelName: string, context: TenantContext): any {
  const { userId, organizationId, teamId, includePublic } = context;
  const tenantFields = MODEL_TENANT_FIELDS[modelName];
  const publicField = MODEL_PUBLIC_FIELDS[modelName];

  // No context provided, no filtering
  if (!userId && !organizationId && !teamId) {
    return null;
  }

  // Build OR conditions for tenant access
  const conditions = [];

  // User ownership condition
  if (userId && (tenantFields.includes('userId') || tenantFields.includes('createdById'))) {
    if (tenantFields.includes('userId')) {
      conditions.push({ userId });
    }
    if (tenantFields.includes('createdById')) {
      conditions.push({ createdById: userId });
    }
  }

  // Organization ownership condition
  if (organizationId && tenantFields.includes('organizationId')) {
    conditions.push({ organizationId });
  }

  // Team ownership condition
  if (teamId && tenantFields.includes('teamId')) {
    conditions.push({ teamId });
  }

  // Public access condition (if requested)
  if (includePublic && publicField) {
    conditions.push({ [publicField]: true });
  }

  // KnowledgeDocument special case - check ownership through knowledgeBase
  if (modelName === TenantModel.KnowledgeDocument) {
    const knowledgeBaseConditions = [];

    if (userId) {
      knowledgeBaseConditions.push({ knowledgeBase: { ownerId: userId } });
    }

    if (organizationId) {
      knowledgeBaseConditions.push({ knowledgeBase: { organizationId } });
    }

    if (teamId) {
      knowledgeBaseConditions.push({ knowledgeBase: { teamId } });
    }

    if (includePublic) {
      knowledgeBaseConditions.push({ knowledgeBase: { isPublic: true } });
    }

    if (knowledgeBaseConditions.length > 0) {
      conditions.push({ OR: knowledgeBaseConditions });
    }
  }

  // If we have conditions, create an OR filter
  if (conditions.length > 0) {
    return { OR: conditions };
  }

  return null;
}

/**
 * Bundle size checker script for Optiflow
 * 
 * This script examines the bundle sizes produced by Next.js build process
 * and fails if any bundle exceeds the specified size limits.
 * 
 * To run manually:
 * 1. Run `ANALYZE=true pnpm build` to generate build stats
 * 2. Run `node scripts/check-bundle-size.js`
 * 
 * Usage in CI or as postbuild step.
 */

const fs = require('fs');
const path = require('path');
const zlib = require('zlib');
const chalk = require('chalk') || { red: (t) => t, yellow: (t) => t, green: (t) => t, bold: (t) => t };

// Configuration
const config = {
  // Maximum size for any standard JS chunk in KB (gzipped)
  maxStandardChunkSize: 250,
  // Maximum size for LiveKit + speech chunks in KB (gzipped)
  maxVoiceChunkSize: 300,
  // Path to Next.js build output
  buildDir: path.join(process.cwd(), '.next'),
  // Path to client chunks directory
  clientChunksDir: path.join(process.cwd(), '.next/static/chunks'),
  // Special voice-related modules to check
  voiceRelatedModules: [
    'livekit', 
    'voice', 
    'speech', 
    'audio', 
    'recognition'
  ]
};

/**
 * Calculate gzipped size of a file
 * @param {string} filePath - Path to the file
 * @returns {number} - Size in kilobytes
 */
function getGzippedSize(filePath) {
  try {
    const content = fs.readFileSync(filePath);
    const gzipped = zlib.gzipSync(content);
    return Math.round((gzipped.length / 1024) * 100) / 100; // KB with 2 decimal places
  } catch (err) {
    console.error(`Error reading ${filePath}:`, err.message);
    return 0;
  }
}

/**
 * Check if a file is related to voice functionality
 * @param {string} filename - Name of the file to check
 * @returns {boolean} - True if the file is related to voice
 */
function isVoiceRelated(filename) {
  return config.voiceRelatedModules.some(term => filename.toLowerCase().includes(term));
}

/**
 * Main function to check bundle sizes
 */
function checkBundleSizes() {
  console.log(chalk.bold('\n📦 Checking bundle sizes...'));

  // Ensure build directory exists
  if (!fs.existsSync(config.buildDir)) {
    console.error(chalk.red('❌ Build directory not found. Run `ANALYZE=true next build` first.'));
    process.exit(1);
  }

  // Ensure client chunks directory exists
  if (!fs.existsSync(config.clientChunksDir)) {
    console.error(chalk.red('❌ Client chunks directory not found. Run `ANALYZE=true next build` first.'));
    process.exit(1);
  }

  const jsFiles = fs.readdirSync(config.clientChunksDir)
    .filter(file => file.endsWith('.js'))
    .map(file => ({
      name: file,
      path: path.join(config.clientChunksDir, file),
      isVoiceRelated: isVoiceRelated(file),
      sizeKB: 0
    }));

  // Calculate sizes
  jsFiles.forEach(file => {
    file.sizeKB = getGzippedSize(file.path);
  });

  // Sort by size (largest first)
  jsFiles.sort((a, b) => b.sizeKB - a.sizeKB);

  // Check for oversized bundles
  const oversized = [];
  jsFiles.forEach(file => {
    const sizeLimit = file.isVoiceRelated ? config.maxVoiceChunkSize : config.maxStandardChunkSize;
    if (file.sizeKB > sizeLimit) {
      oversized.push({
        ...file,
        overBy: Math.round((file.sizeKB - sizeLimit) * 100) / 100
      });
    }
  });

  // Print results
  console.log(chalk.bold('\n🔍 Bundle size report:'));
  console.log(`Found ${jsFiles.length} JavaScript bundles, ${oversized.length} oversized\n`);

  // Print top 5 largest bundles
  console.log(chalk.bold('Top 5 largest bundles:'));
  jsFiles.slice(0, 5).forEach(file => {
    const sizeLimit = file.isVoiceRelated ? config.maxVoiceChunkSize : config.maxStandardChunkSize;
    const color = file.sizeKB > sizeLimit ? chalk.red : chalk.green;
    console.log(`${color(file.name)}: ${file.sizeKB} KB (limit: ${sizeLimit} KB)`);
  });

  if (oversized.length > 0) {
    console.log(chalk.bold('\n⚠️ Oversized bundles:'));
    oversized.forEach(file => {
      console.log(chalk.red(`${file.name}: ${file.sizeKB} KB (over by ${file.overBy} KB)`));
    });
    console.log(chalk.red(`\n❌ Found ${oversized.length} oversized bundles. Build failed.`));
    console.log('Suggestions:');
    console.log('- Review dynamic imports and code splitting');
    console.log('- Remove unused dependencies');
    console.log('- Use tree-shaking compatible imports');
    console.log('- Consider lazy-loading components');

    // Uncomment this line in production to make the check fail the build
    // process.exit(1);
  } else {
    console.log(chalk.green('\n✅ All bundles are within size limits!'));
  }
}

// Run the check
checkBundleSizes();

#!/usr/bin/env node

/**
 * This script checks if all required environment variables are set
 * Run with: node scripts/check-env.js
 */

const fs = require('fs');
const path = require('path');

const requiredEnvVars = [
  'NODE_ENV',
  'NEXTAUTH_URL',
  'NEXTAUTH_SECRET',
  'DATABASE_URL',
  'GOOGLE_CLIENT_ID',
  'GOOGLE_CLIENT_SECRET',
  'ENCRYPTION_KEY',
  'JWT_SECRET',
];

const optionalEnvVars = [
  'NEXT_PUBLIC_PIPEDREAM_CLIENT_ID',
  'PIPEDREAM_CLIENT_SECRET',
  'NEXT_PUBLIC_PIPEDREAM_PROJECT_ID',
  'NEXT_PUBLIC_PIPEDREAM_TOKEN',
  'MCP_SERVICE_URL',
  'ANALYTICS_SERVICE_URL',
  'OPENAI_API_KEY',
  'AWS_ACCESS_KEY_ID',
  'AWS_SECRET_ACCESS_KEY',
  'AWS_BUCKET_NAME',
  'AWS_REGION',
];

function loadEnvFile(filename) {
  try {
    const envPath = path.resolve(process.cwd(), filename);
    if (fs.existsSync(envPath)) {
      const envContent = fs.readFileSync(envPath, 'utf8');
      const envVars = {};

      envContent.split('\n').forEach(line => {
        const match = line.match(/^([^=]+)=(.*)$/);
        if (match) {
          const [, key, value] = match;
          envVars[key.trim()] = value.trim();
        }
      });

      return envVars;
    }
  } catch (error) {
    console.error(`Error loading ${filename}:`, error);
  }
  return {};
}

function checkEnvVars() {
  const nodeEnv = process.env.NODE_ENV || 'development';
  const envFile = nodeEnv === 'production' ? '.env.production' : '.env';
  const envVars = {
    ...loadEnvFile('.env'),
    ...loadEnvFile(envFile),
    ...process.env
  };

  console.log(`Checking environment variables for ${nodeEnv} environment...`);

  const missingRequired = [];
  const missingOptional = [];

  // Check required variables
  requiredEnvVars.forEach(varName => {
    if (!envVars[varName]) {
      missingRequired.push(varName);
    }
  });

  // Check optional variables
  optionalEnvVars.forEach(varName => {
    if (!envVars[varName]) {
      missingOptional.push(varName);
    }
  });

  // Report results
  if (missingRequired.length > 0) {
    console.error('\n❌ Missing required environment variables:');
    missingRequired.forEach(varName => {
      console.error(`   - ${varName}`);
    });
    process.exit(1);
  }

  if (missingOptional.length > 0) {
    console.warn('\n⚠️  Missing optional environment variables:');
    missingOptional.forEach(varName => {
      console.warn(`   - ${varName}`);
    });
  }

  // Validate URL format
  const urlVars = ['NEXTAUTH_URL', 'MCP_SERVICE_URL', 'ANALYTICS_SERVICE_URL'];
  urlVars.forEach(varName => {
    if (envVars[varName]) {
      try {
        new URL(envVars[varName]);
      } catch (error) {
        console.error(`\n❌ Invalid URL format for ${varName}: ${envVars[varName]}`);
        process.exit(1);
      }
    }
  });

  // Validate secrets length
  const secretVars = ['NEXTAUTH_SECRET', 'JWT_SECRET', 'ENCRYPTION_KEY'];
  secretVars.forEach(varName => {
    if (envVars[varName] && envVars[varName].length < 32) {
      console.warn(`\n⚠️  Warning: ${varName} should be at least 32 characters long`);
    }
  });

  console.log('\n✅ Environment validation completed successfully');
}

checkEnvVars();

import { exec } from 'child_process';
import * as fs from 'fs';
import * as path from 'path';
import readline from 'readline';
import { promisify } from 'util';

import * as dotenv from 'dotenv';

const execAsync = promisify(exec);

interface EnvVar {
  name: string;
  description: string;
  required: boolean;
}

const REQUIRED_ENV_VARS: EnvVar[] = [
  {
    name: 'DEEPGRAM_API_KEY',
    description: 'API key for Deepgram voice recognition service',
    required: true,
  },
  {
    name: 'PIPEDREAM_API_KEY',
    description: 'API key for Pipedream workflow integration',
    required: true,
  },
  {
    name: 'DATABASE_URL',
    description: 'PostgreSQL database connection string',
    required: true,
  },
  {
    name: 'OPENAI_API_KEY',
    description: 'API key for OpenAI services',
    required: true,
  },
  {
    name: 'ANTHROPIC_API_KEY',
    description: 'API key for Anthropic services',
    required: true,
  },
  {
    name: 'NEXT_PUBLIC_DEFAULT_LANGUAGE',
    description: 'Default language for voice recognition (default: en-US)',
    required: false,
  },
  {
    name: 'NEXT_PUBLIC_MAX_CONVERSATION_LENGTH',
    description: 'Maximum conversation history length (default: 10)',
    required: false,
  },
  {
    name: 'NEXT_PUBLIC_TIMEOUT_MS',
    description: 'Timeout for voice recognition in milliseconds (default: 30000)',
    required: false,
  },
];

async function checkLocalEnv(): Promise<string[]> {
  const envPath = path.join(process.cwd(), '.env.local');
  const missingVars: string[] = [];

  if (fs.existsSync(envPath)) {
    dotenv.config({ path: envPath });
  }

  for (const envVar of REQUIRED_ENV_VARS) {
    if (envVar.required && !process.env[envVar.name]) {
      missingVars.push(envVar.name);
    }
  }

  return missingVars;
}

async function checkVercelEnv(): Promise<string[]> {
  try {
    const { stdout } = await execAsync('vercel env ls');
    const vercelEnvVars = stdout.split('\n').map(line => line.trim());
    const missingVars: string[] = [];

    for (const envVar of REQUIRED_ENV_VARS) {
      if (envVar.required && !vercelEnvVars.includes(envVar.name)) {
        missingVars.push(envVar.name);
      }
    }

    return missingVars;
  } catch (error) {
    console.error('Error checking Vercel environment variables:', error);
    return [];
  }
}

async function addEnvToVercel(envVar: string): Promise<void> {
  try {
    const { stdout } = await execAsync(`vercel env add ${envVar}`);
    console.log(`Added ${envVar} to Vercel environment variables`);
  } catch (error) {
    console.error(`Error adding ${envVar} to Vercel:`, error);
  }
}

async function main() {
  console.log('🔍 Checking environment variables...\n');

  // Check local environment
  const missingLocalVars = await checkLocalEnv();
  if (missingLocalVars.length > 0) {
    console.log('❌ Missing local environment variables:');
    missingLocalVars.forEach(varName => {
      const envVar = REQUIRED_ENV_VARS.find(v => v.name === varName);
      console.log(`  - ${varName}: ${envVar?.description}`);
    });
    console.log('\nTo add these variables to your local environment:');
    console.log('1. Create or edit .env.local file');
    console.log('2. Add the missing variables in the format:');
    console.log('   VARIABLE_NAME=your_value_here\n');
  } else {
    console.log('✅ All required local environment variables are set\n');
  }

  // Check Vercel environment
  const missingVercelVars = await checkVercelEnv();
  if (missingVercelVars.length > 0) {
    console.log('❌ Missing Vercel environment variables:');
    missingVercelVars.forEach(varName => {
      const envVar = REQUIRED_ENV_VARS.find(v => v.name === varName);
      console.log(`  - ${varName}: ${envVar?.description}`);
    });
    console.log('\nTo add these variables to Vercel:');
    console.log('1. Run the following commands:');
    missingVercelVars.forEach(varName => {
      console.log(`   vercel env add ${varName}`);
    });
    console.log('\n2. Enter the values when prompted');
  } else {
    console.log('✅ All required Vercel environment variables are set\n');
  }

  // Offer to add missing variables to Vercel
  if (missingVercelVars.length > 0) {
    const rl = readline.createInterface({
      input: process.stdin,
      output: process.stdout,
    });

    const answer = await new Promise<string>((resolve) => {
      rl.question('Would you like to add the missing variables to Vercel now? (y/n) ', resolve);
    });

    rl.close();

    if (answer.toLowerCase() === 'y') {
      for (const varName of missingVercelVars) {
        await addEnvToVercel(varName);
      }
    }
  }
}

main().catch(console.error);

/**
 * Pipedream Connection Troubleshooter
 * 
 * This script verifies your Pipedream environment configuration and helps fix common issues
 * that prevent connections from working properly.
 */

const { execSync } = require('child_process');
const fs = require('fs');
const https = require('https');
const path = require('path');
const readline = require('readline');

const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

// Check for required environment variables
const requiredEnvVars = [
  'NEXT_PUBLIC_PIPEDREAM_CLIENT_ID',
  'PIPEDREAM_CLIENT_SECRET',
  'PIPEDREAM_PROJECT_ID',
  'PIPEDREAM_PROJECT_ENVIRONMENT'
];

// Additional recommended variables
const recommendedEnvVars = [
  'NEXT_PUBLIC_APP_URL',
  'NEXT_PUBLIC_PIPEDREAM_REDIRECT_URI'
];

// Paths to check for environment variables
const envPaths = [
  '.env',
  '.env.local',
  '.env.development',
  '.env.production'
];

console.log('🔍 Pipedream Connection Troubleshooter');
console.log('======================================\n');

// Check environment files
console.log('Checking environment files...');
const existingEnvFiles = envPaths.filter(file => fs.existsSync(file));

if (existingEnvFiles.length === 0) {
  console.log('❌ No environment files found. Creating .env.local...');
  fs.writeFileSync('.env.local', '# Pipedream Environment Variables\n\n');
  existingEnvFiles.push('.env.local');
} else {
  console.log('✅ Found environment files:', existingEnvFiles.join(', '));
}

// Function to get value from any env file
function getEnvValue(key) {
  for (const envFile of existingEnvFiles) {
    if (!fs.existsSync(envFile)) continue;

    const content = fs.readFileSync(envFile, 'utf8');
    const regex = new RegExp(`${key}="?([^"\n]*)"?`, 'i');
    const match = content.match(regex);

    if (match && match[1]) {
      return match[1].trim();
    }
  }

  return null;
}

// Function to add or update a value in .env.local
function setEnvValue(key, value) {
  const envFile = '.env.local';
  let content = '';

  if (fs.existsSync(envFile)) {
    content = fs.readFileSync(envFile, 'utf8');

    // Check if key already exists
    const regex = new RegExp(`^${key}=.*$`, 'm');
    if (regex.test(content)) {
      // Update existing key
      content = content.replace(regex, `${key}="${value}"`);
    } else {
      // Add new key
      content += `\n${key}="${value}"`;
    }
  } else {
    content = `# Pipedream Environment Variables\n\n${key}="${value}"\n`;
  }

  fs.writeFileSync(envFile, content);
  console.log(`✅ Updated ${key} in ${envFile}`);
}

// Check for required variables
console.log('\nChecking required environment variables...');
const missingVars = [];

for (const envVar of requiredEnvVars) {
  const value = getEnvValue(envVar);
  if (!value) {
    console.log(`❌ Missing: ${envVar}`);
    missingVars.push(envVar);
  } else {
    console.log(`✅ Found: ${envVar}`);
  }
}

// Check Pipedream service availability
console.log('\nChecking Pipedream service availability...');

function checkPipedreamService() {
  return new Promise((resolve) => {
    const req = https.get('https://api.pipedream.com/v1/health', (res) => {
      if (res.statusCode === 200) {
        resolve(true);
      } else {
        resolve(false);
      }
    });

    req.on('error', () => {
      resolve(false);
    });

    req.end();
  });
}

async function runDiagnostics() {
  const serviceAvailable = await checkPipedreamService();

  if (serviceAvailable) {
    console.log('✅ Pipedream service is available');
  } else {
    console.log('❌ Cannot connect to Pipedream service. Check your internet connection.');
    process.exit(1);
  }

  // Prompt for missing variables if needed
  if (missingVars.length > 0) {
    console.log('\n⚠️ Some required environment variables are missing.');
    console.log('Please visit https://pipedream.com/dashboard to get your API credentials.');

    const promptForVars = async () => {
      for (const envVar of missingVars) {
        await new Promise((resolve) => {
          rl.question(`Enter value for ${envVar}: `, (answer) => {
            if (answer.trim()) {
              setEnvValue(envVar, answer.trim());
            }
            resolve();
          });
        });
      }
    };

    await promptForVars();
  }

  // Check if NEXT_PUBLIC_APP_URL is set correctly
  const appUrl = getEnvValue('NEXT_PUBLIC_APP_URL');
  if (!appUrl) {
    console.log('\n⚠️ NEXT_PUBLIC_APP_URL is not set. This may cause callback issues.');
    const defaultUrl = 'http://localhost:3000';

    await new Promise((resolve) => {
      rl.question(`Enter your application URL (default: ${defaultUrl}): `, (answer) => {
        const url = answer.trim() || defaultUrl;
        setEnvValue('NEXT_PUBLIC_APP_URL', url);
        resolve();
      });
    });
  }

  // Ensure redirect URI is set
  const redirectUri = getEnvValue('NEXT_PUBLIC_PIPEDREAM_REDIRECT_URI');
  if (!redirectUri) {
    const newAppUrl = getEnvValue('NEXT_PUBLIC_APP_URL') || 'http://localhost:3000';
    const defaultRedirectUri = `${newAppUrl}/api/pipedream/callback`;

    await new Promise((resolve) => {
      rl.question(`Enter your redirect URI (default: ${defaultRedirectUri}): `, (answer) => {
        const uri = answer.trim() || defaultRedirectUri;
        setEnvValue('NEXT_PUBLIC_PIPEDREAM_REDIRECT_URI', uri);
        resolve();
      });
    });
  }

  // Verify configuration
  console.log('\n🔍 Verifying configuration...');
  const clientId = getEnvValue('NEXT_PUBLIC_PIPEDREAM_CLIENT_ID');
  const clientSecret = getEnvValue('PIPEDREAM_CLIENT_SECRET');
  const projectId = getEnvValue('PIPEDREAM_PROJECT_ID');

  if (!clientId || !clientSecret || !projectId) {
    console.log('❌ Still missing required environment variables. Please set them manually.');
  } else {
    console.log('✅ All required environment variables are set.');

    // Restart the development server if running locally
    console.log('\n🔄 You need to restart your development server to apply the changes.');
    await new Promise((resolve) => {
      rl.question('Do you want to restart the server now? (y/n): ', (answer) => {
        if (answer.toLowerCase() === 'y') {
          try {
            console.log('Restarting development server...');
            execSync('npm run dev', { stdio: 'inherit' });
          } catch (error) {
            console.error('Failed to restart server:', error);
          }
        }
        resolve();
      });
    });
  }

  rl.close();
}

runDiagnostics().catch(console.error);

#!/usr/bin/env node

/**
 * This script generates a secure random string to use as NEXTAUTH_SECRET
 * Run with: node scripts/generate-secret.js
 */

const crypto = require('crypto');
const fs = require('fs');
const path = require('path');

function generateSecret(length = 64) {
  return crypto.randomBytes(length).toString('base64');
}

function generateEncryptionKey() {
  return crypto.randomBytes(32).toString('hex');
}

function updateEnvFile(filename, updates) {
  const envPath = path.resolve(process.cwd(), filename);
  let envContent = '';

  try {
    if (fs.existsSync(envPath)) {
      envContent = fs.readFileSync(envPath, 'utf8');
    }
  } catch (error) {
    console.error(`Error reading ${filename}:`, error);
  }

  const envLines = envContent.split('\n');
  const updatedLines = [];
  const processedKeys = new Set();

  // Update existing variables
  for (const line of envLines) {
    const match = line.match(/^([^=]+)=/);
    if (match) {
      const key = match[1].trim();
      if (updates[key]) {
        updatedLines.push(`${key}=${updates[key]}`);
        processedKeys.add(key);
      } else {
        updatedLines.push(line);
      }
    } else if (line.trim()) {
      updatedLines.push(line);
    }
  }

  // Add new variables
  for (const [key, value] of Object.entries(updates)) {
    if (!processedKeys.has(key)) {
      updatedLines.push(`${key}=${value}`);
    }
  }

  try {
    fs.writeFileSync(envPath, updatedLines.join('\n') + '\n');
    console.log(`Updated ${filename} successfully`);
  } catch (error) {
    console.error(`Error writing to ${filename}:`, error);
    process.exit(1);
  }
}

function main() {
  const nodeEnv = process.env.NODE_ENV || 'development';
  const envFile = nodeEnv === 'production' ? '.env.production' : '.env';

  console.log(`Generating secrets for ${nodeEnv} environment...`);

  const updates = {
    NEXTAUTH_SECRET: generateSecret(),
    JWT_SECRET: generateSecret(),
    ENCRYPTION_KEY: generateEncryptionKey(),
  };

  // Log the generated secrets
  console.log('\nGenerated secrets:');
  Object.entries(updates).forEach(([key, value]) => {
    console.log(`${key}=${value}`);
  });

  // Update the environment file
  updateEnvFile(envFile, updates);

  console.log('\n✅ Secrets generated and updated successfully');
  console.log('⚠️  Make sure to keep these secrets safe and never commit them to version control');
}

main();

