    const { userId } = body;

    if (!userId) {
      return NextResponse.json(
        { error: 'Missing required parameter: userId' },
        { status: 400 }
      );
    }

    console.log(`Creating token for user: ${userId}`);

    // Create a token for the user using the direct OAuth flow approach
