import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from 'next-auth/next';

import { authOptions } from '@/lib/auth';
import { redis } from '@/lib/redis';

const RATE_LIMIT = 60; // requests
const RATE_LIMIT_WINDOW = 60; // seconds

async function checkRateLimit(userId: string) {
  const key = `ratelimit:presencecheck:${userId}`;
  const count = await redis.incr(key);
  if (count === 1) {
    await redis.expire(key, RATE_LIMIT_WINDOW);
  }
  return count > RATE_LIMIT;
}

export async function POST(req: Request) {
  try {
    const session = await getServerSession(authOptions);
    if (!session || !session.user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }
    const { userId } = await req.json();
    if (!userId || userId !== session.user.id) {
      return NextResponse.json({ error: 'Invalid userId' }, { status: 401 });
    }
    // Rate limiting
    const limited = await checkRateLimit(userId);
    if (limited) {
      return NextResponse.json({ error: 'Rate limit exceeded' }, { status: 429 });
    }
    const key = `user:${userId}:presence`;
    const presence = await redis.hgetall(key);
    const inactive = presence && presence.inactive === '1';
    return NextResponse.json({ inactive });
  } catch (error) {
    return NextResponse.json({ error: 'Failed to check presence' }, { status: 500 });
  }
}

