                          <div className="bg-[#111111] p-2 rounded font-mono text-[#E5E7EB] text-sm break-all">
                            ABCDEF123456GHIJKL
                          </div>
                        </div>
                      )}
                    </div>
                  </li>

                  <li className="flex">
                    <div className="flex-shrink-0 h-6 w-6 rounded-full bg-[#1E293B] flex items-center justify-center text-[#22D3EE] mr-3">
                      3
                    </div>
                    <div>
                      <p className="text-[#E5E7EB] font-medium">Enter verification code</p>
                      <p className="text-sm text-[#9CA3AF] mt-1 mb-2">
                        Enter the 6-digit code from your authenticator app to verify setup.
                      </p>

                      <form onSubmit={handleVerifyMfa}>
                        <div className="flex">
                          <input
                            type="text"
                            value={verificationCode}
                            onChange={(e) => setVerificationCode(e.target.value.replace(/\D/g, '').substring(0, 6))}
                            className="flex-1 px-3 py-2 bg-[#111111] border border-[#374151] rounded-l-md text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
                            placeholder="000000"
                            maxLength={6}
                            required
                          />
                          <button
                            type="submit"
                            className="px-4 py-2 bg-[#22D3EE] text-[#111111] font-medium rounded-r-md hover:bg-[#06B6D4] transition-colors"
                            disabled={verificationCode.length !== 6}
                          >
                            Verify
                          </button>
                        </div>
                      </form>
                    </div>
                  </li>
                </ol>
              </div>
            ) : (
              <div>
                <div className="bg-[#022c22] border border-[#10B981] rounded-lg p-4 flex items-start mb-6">
                  <CheckCircleIcon className="h-6 w-6 text-[#10B981] mr-3 mt-0.5" />
                  <div>
                    <p className="text-[#E5E7EB] font-medium">
                      Verification successful!
                    </p>
                    <p className="text-sm text-[#9CA3AF] mt-1">
                      Your MFA device has been verified. Save your recovery codes before completing setup.
                    </p>
                  </div>
                </div>

                <p className="text-[#E5E7EB] font-medium mb-2">Recovery Codes</p>
                <p className="text-sm text-[#9CA3AF] mb-4">
                  Save these recovery codes in a secure place. They allow you to access your account if you lose your authenticator device.
                </p>

                <div className="bg-[#111111] p-3 rounded-lg border border-[#374151] mb-4">
                  <div className="grid grid-cols-2 gap-2 font-mono text-sm">
                    <div className="bg-[#1E293B] p-2 rounded">ABCD-EFGH-1234</div>
                    <div className="bg-[#1E293B] p-2 rounded">IJKL-MNOP-5678</div>
                    <div className="bg-[#1E293B] p-2 rounded">QRST-UVWX-9012</div>
                    <div className="bg-[#1E293B] p-2 rounded">YZAB-CDEF-3456</div>
                    <div className="bg-[#1E293B] p-2 rounded">GHIJ-KLMN-7890</div>
                    <div className="bg-[#1E293B] p-2 rounded">OPQR-STUV-1234</div>
                  </div>
                </div>

                <div className="flex items-center mb-6">
                  <input 
                    type="checkbox" 
                    id="codesBackup" 
                    className="h-4 w-4 text-[#22D3EE] bg-[#111111] border-[#374151] rounded focus:ring-[#22D3EE]" 
                  />
                  <label htmlFor="codesBackup" className="ml-2 text-sm text-[#9CA3AF]">
                    I have saved these recovery codes in a secure location
                  </label>
                </div>

                <div className="flex justify-end">
                  <button
                    onClick={() => {
                      setShowMfaSetup(false);
                      setMfaStep(1);
                      setVerificationCode('');
                      alert('MFA has been successfully set up!');
                    }}
                    className="px-4 py-2 bg-[#22D3EE] text-[#111111] font-medium rounded-md hover:bg-[#06B6D4] transition-colors"
                  >
                    Complete Setup
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { 
  CheckCircleIcon, 
  EnvelopeIcon, 
  ExclamationCircleIcon, 
  EyeIcon, 
  EyeSlashIcon,
  LockClosedIcon,
  UserIcon 
} from '@heroicons/react/24/outline';
import Cookies from 'js-cookie';
import Image from 'next/image';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { useState } from 'react';

import { SocialProvider } from '@/lib/auth';
import { useUserStore } from '@/lib/userStore';


interface PasswordStrength {
  score: number;
  feedback: string;
  criteria: {
    hasMinLength: boolean;
    hasUppercase: boolean;
    hasLowercase: boolean;
    hasNumber: boolean;
    hasSpecialChar: boolean;
  };
}

export default function Signup(): JSX.Element {
  const router = useRouter();
  const [formData, setFormData] = useState({ email: '',
    password: '',
    confirmPassword: '',
    name: '',
      });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const setCurrentUser = useUserStore((state) => state.setCurrentUser);
  const [fullName, setFullName] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [agreeToTerms, setAgreeToTerms] = useState(false);
  const [isEmailVerificationSent, setIsEmailVerificationSent] = useState(false);

  // Form validation states
  const [passwordStrength, setPasswordStrength] = useState<PasswordStrength>({
    score: 0,
    feedback: 'Password is too weak',
    criteria: {
      hasMinLength: false,
      hasUppercase: false,
      hasLowercase: false,
      hasNumber: false,
      hasSpecialChar: false
    }
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // Check if passwords match
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setIsLoading(false);
      return;
    }

    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json',
            },
        body: JSON.stringify({
  email: formData.email,
          password: formData.password,
          name: formData.name || undefined,
            }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Registration failed');
      }

      // Set user in store
      setCurrentUser({ id: data.user.id,
        email: data.user.email,
        name: data.user.name,
          });

      // Set authentication cookie
      Cookies.set('user-token', data.user.id, { expires: 7, 
        path: '/',
        sameSite: 'strict'
          });

      router.push('/dashboard');
    } catch (error) { console.error('Registration error:', error);
      setError(error instanceof Error ? error.message : 'Registration failed');
        } finally {
      setIsLoading(false);
    }
  };

  const handleSocialLogin = async (provider: string) => {
    setIsLoading(true);
    setError('');
    console.log(`Signing up with ${provider}...`);

    try {
      if (provider.toLowerCase() === 'github') {
        window.location.href = '/api/auth/signin/github';
        return;
      } else if (provider.toLowerCase() === 'gmail') {
        window.location.href = '/api/auth/signin/google';
        return;
      }
    } catch (error) {
      console.error(`${provider} signup error:`, error);
      setError(error instanceof Error ? error.message : `Registration with ${provider} failed`);
      setIsLoading(false);
    }
  };

  const validatePassword = (password: string) => {
    const criteria = {
      hasMinLength: password.length >= 8,
      hasUppercase: /[A-Z]/.test(password),
      hasLowercase: /[a-z]/.test(password),
      hasNumber: /[0-9]/.test(password),
      hasSpecialChar: /[^A-Za-z0-9]/.test(password)
    };

    const passedCriteria = Object.values(criteria).filter(Boolean).length;

    let score = 0;
    let feedback = 'Password is too weak';

    if (passedCriteria === 0 || passedCriteria === 1) {
      score = 1;
      feedback = 'Password is too weak';
    } else if (passedCriteria === 2 || passedCriteria === 3) {
      score = 2;
      feedback = 'Password could be stronger';
    } else if (passedCriteria === 4) {
      score = 3;
      feedback = 'Password is strong';
    } else if (passedCriteria === 5) {
      score = 4;
      feedback = 'Password is very strong';
    }

    setPasswordStrength({
      score,
      feedback,
      criteria
    });
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newPassword = e.target.value;
    setFormData({ ...formData, password: newPassword });
    validatePassword(newPassword);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const toggleConfirmPasswordVisibility = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  return (
    <div className="min-h-screen bg-[#111111] flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <Link href="/" className="flex justify-center">
          <Image
            src="/images/optiflow-logo.svg"
            alt="Optiflow"
            width={180}
            height={48}
            className="h-12 w-auto"
          />
        </Link>
        <h2 className="mt-6 text-center text-3xl font-bold text-[#E5E7EB]">
          Create Your Optiflow Account
        </h2>
        <p className="mt-2 text-center text-sm text-[#9CA3AF]">
          Already have an account?{' '}
          <Link href="/login" className="font-medium text-[#22D3EE] hover:text-[#06B6D4]">
            Log in
          </Link>
        </p>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-[#18181B] py-8 px-4 shadow-xl sm:rounded-lg sm:px-10">
          {error && (
            <div className="mb-4 bg-[#371520] border border-[#F87171] text-[#F87171] px-4 py-3 rounded-md text-sm">
              {error}
            </div>
          )}

          {!isEmailVerificationSent ? (
            <>
              <form className="space-y-6" onSubmit={handleSubmit}>
                <div>
                  <label htmlFor="full-name" className="block text-sm font-medium text-[#9CA3AF]">
                    Full Name
                  </label>
                  <div className="mt-1 relative rounded-md shadow-sm">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <UserIcon className="h-5 w-5 text-[#4B5563]" aria-hidden="true" />
                    </div>
                    <input
                      id="full-name"
                      name="full-name"
                      type="text"
                      autoComplete="name"
                      required
                      value={fullName}
                      onChange={(e) => setFullName(e.target.value)}
                      className="block w-full pl-10 pr-3 py-2 bg-[#111111] border border-[#374151] rounded-md text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
                      placeholder="John Doe"
                      aria-label="Your full name"
                      title="Your full name"
                    />
                  </div>
                </div>

                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-[#9CA3AF]">
                    Work Email
                  </label>
                  <div className="mt-1 relative rounded-md shadow-sm">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <EnvelopeIcon className="h-5 w-5 text-[#4B5563]" aria-hidden="true" />
                    </div>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      autoComplete="email"
                      required
                      value={formData.email}
                      onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                      className="block w-full pl-10 pr-3 py-2 bg-[#111111] border border-[#374151] rounded-md text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
                      placeholder="you@example.com"
                      aria-label="Your email address"
                      title="Your email address"
                    />
                  </div>
                </div>

                <div>
                  <label htmlFor="password" className="block text-sm font-medium text-[#9CA3AF]">
                    Password
                  </label>
                  <div className="mt-1 relative rounded-md shadow-sm">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <LockClosedIcon className="h-5 w-5 text-[#4B5563]" aria-hidden="true" />
                    </div>
                    <input
                      id="password"
                      name="password"
                      type={showPassword ? "text" : "password"}
                      autoComplete="new-password"
                      required
                      value={formData.password}
                      onChange={handlePasswordChange}
                      className="block w-full pl-10 pr-10 py-2 bg-[#111111] border border-[#374151] rounded-md text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
                      placeholder="••••••••"
                      aria-label="Create password"
                      title="Create password"
                    />
                    <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                      <button
                        type="button"
                        onClick={togglePasswordVisibility}
                        className="text-[#4B5563] hover:text-[#9CA3AF] focus:outline-none"
                        aria-label={showPassword ? "Hide password" : "Show password"}
                        title={showPassword ? "Hide password" : "Show password"}
                      >
                        {showPassword ? (
                          <EyeSlashIcon className="h-5 w-5" aria-hidden="true" />
                        ) : (
                          <EyeIcon className="h-5 w-5" aria-hidden="true" />
                        )}
                      </button>
                    </div>
                  </div>

                  {/* Password strength indicator */}
                  <div className="mt-2">
                    <div className="flex justify-between items-center mb-1">
                      <span className="text-xs text-[#9CA3AF]">Password strength</span>
                      <span className={`text-xs ${
                        passwordStrength.score <= 1 ? 'text-[#F87171]' : 
                        passwordStrength.score === 2 ? 'text-[#F59E0B]' : 
                        'text-[#10B981]'
                      }`}>
                        {passwordStrength.feedback}
                      </span>
                    </div>
                    <div className="h-1 w-full bg-[#1E293B] rounded-full overflow-hidden">
                      <div className={`h-full ${
                        passwordStrength.score <= 1 ? 'bg-[#F87171] w-1/4' : 
                        passwordStrength.score === 2 ? 'bg-[#F59E0B] w-2/4' : 
                        passwordStrength.score === 3 ? 'bg-[#10B981] w-3/4' : 
                        'bg-[#10B981] w-full'
                      }`}></div>
                    </div>

                    {/* Password requirements */}
                    <div className="mt-3 grid grid-cols-2 gap-2">
                      <div className="flex items-center text-xs">
                        {passwordStrength.criteria.hasMinLength ? (
                          <CheckCircleIcon className="h-4 w-4 text-[#10B981] mr-1" />
                        ) : (
                          <ExclamationCircleIcon className="h-4 w-4 text-[#9CA3AF] mr-1" />
                        )}
                        <span className={passwordStrength.criteria.hasMinLength ? 'text-[#E5E7EB]' : 'text-[#9CA3AF]'}>
                          8+ characters
                        </span>
                      </div>
                      <div className="flex items-center text-xs">
                        {passwordStrength.criteria.hasUppercase ? (
                          <CheckCircleIcon className="h-4 w-4 text-[#10B981] mr-1" />
                        ) : (
                          <ExclamationCircleIcon className="h-4 w-4 text-[#9CA3AF] mr-1" />
                        )}
                        <span className={passwordStrength.criteria.hasUppercase ? 'text-[#E5E7EB]' : 'text-[#9CA3AF]'}>
                          Uppercase letter
                        </span>
                      </div>
                      <div className="flex items-center text-xs">
                        {passwordStrength.criteria.hasLowercase ? (
                          <CheckCircleIcon className="h-4 w-4 text-[#10B981] mr-1" />
                        ) : (
                          <ExclamationCircleIcon className="h-4 w-4 text-[#9CA3AF] mr-1" />
                        )}
                        <span className={passwordStrength.criteria.hasLowercase ? 'text-[#E5E7EB]' : 'text-[#9CA3AF]'}>
                          Lowercase letter
                        </span>
                      </div>
                      <div className="flex items-center text-xs">
                        {passwordStrength.criteria.hasNumber ? (
                          <CheckCircleIcon className="h-4 w-4 text-[#10B981] mr-1" />
                        ) : (
                          <ExclamationCircleIcon className="h-4 w-4 text-[#9CA3AF] mr-1" />
                        )}
                        <span className={passwordStrength.criteria.hasNumber ? 'text-[#E5E7EB]' : 'text-[#9CA3AF]'}>
                          Number
                        </span>
                      </div>
                      <div className="flex items-center text-xs">
                        {passwordStrength.criteria.hasSpecialChar ? (
                          <CheckCircleIcon className="h-4 w-4 text-[#10B981] mr-1" />
                        ) : (
                          <ExclamationCircleIcon className="h-4 w-4 text-[#9CA3AF] mr-1" />
                        )}
                        <span className={passwordStrength.criteria.hasSpecialChar ? 'text-[#E5E7EB]' : 'text-[#9CA3AF]'}>
                          Special character
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                <div>
                  <label htmlFor="confirm-password" className="block text-sm font-medium text-[#9CA3AF]">
                    Confirm Password
                  </label>
                  <div className="mt-1 relative rounded-md shadow-sm">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <LockClosedIcon className="h-5 w-5 text-[#4B5563]" aria-hidden="true" />
                    </div>
                    <input
                      id="confirm-password"
                      name="confirm-password"
                      type={showConfirmPassword ? "text" : "password"}
                      autoComplete="new-password"
                      required
                      value={formData.confirmPassword}
                      onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                      className={`block w-full pl-10 pr-10 py-2 bg-[#111111] border ${
                        formData.confirmPassword && formData.password !== formData.confirmPassword
                          ? 'border-[#F87171] focus:ring-[#F87171]'
