const StatusBadge = ({ status }: { status: ExecutionStatus }) => {
  const bgColor = 
    status === 'success' ? 'bg-green-100 text-green-800' : 
    status === 'failed' ? 'bg-red-100 text-red-800' : 
    status === 'running' ? 'bg-blue-100 text-blue-800' : 
    'bg-gray-100 text-gray-800';

  const Icon = 
    status === 'success' ? CheckCircleIcon : 
    status === 'failed' ? XCircleIcon : 
    status === 'running' ? ArrowPathIcon : 
    StopIcon;

  return (
    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${bgColor}`}>
      <Icon className="mr-1 h-4 w-4" />
      {status.charAt(0).toUpperCase() + status.slice(1)}
    </span>
  );
};

export default function WorkflowLogsPage({ params }: { params: { id: string } }) {
  const router = useRouter();
  const [isLoading, setIsLoading] = useState(true);
  const [workflow, setWorkflow] = useState<Workflow | null>(null);
  const [logs, setLogs] = useState<ExecutionLog[]>([]);
  const [statusFilter, setStatusFilter] = useState<ExecutionStatus | 'all'>('all');
  const [dateRangeFilter, setDateRangeFilter] = useState<'24h' | '7d' | '30d' | 'all'>('7d');
  const [expandedLogId, setExpandedLogId] = useState<string | null>(null);

  useEffect(() => {
    // Simulate API call to fetch workflow and logs
    setTimeout(() => {
      setWorkflow(getMockWorkflow(params.id));
      setLogs(getMockExecutionLogs(params.id));
      setIsLoading(false);
    }, 1000);
  }, [params.id]);

  // Format date
  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM d, yyyy h:mm:ss a');
    } catch (e) {
      return dateString;
    }
  };

  // Format time ago
  const formatTimeAgo = (dateString: string) => {
    try {
      return formatDistance(new Date(dateString), new Date(), { addSuffix: true });
    } catch (e) {
      return dateString;
    }
  };

  // Filter logs
  const filteredLogs = logs.filter(log => {
    // Filter by status
    if (statusFilter !== 'all' && log.status !== statusFilter) {
      return false;
    }

    // Filter by date range
    if (dateRangeFilter !== 'all') {
      const now = new Date();
      const logDate = new Date(log.startTime);
      const diffDays = Math.floor((now.getTime() - logDate.getTime()) / (1000 * 60 * 60 * 24));

      if (dateRangeFilter === '24h' && diffDays >= 1) return false;
      if (dateRangeFilter === '7d' && diffDays >= 7) return false;
      if (dateRangeFilter === '30d' && diffDays >= 30) return false;
    }

    return true;
  });

  if (isLoading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-[#111111]">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-[#22D3EE]"></div>
      </div>
    );
  }

  if (!workflow) {
    return (
      <div className="min-h-screen bg-[#111111] text-[#E5E7EB] flex flex-col items-center justify-center">
        <h1 className="text-2xl font-bold mb-4">Workflow not found</h1>
        <Link 
          href="/workflows" 
          className="text-[#22D3EE] hover:text-[#06B6D4] transition-colors"
        >
          Return to workflows
