        ) : (
          <div className="space-y-4">
            {filteredLogs.map((log) => (
              <div 
                key={log.id} 
                className="bg-[#18181B] border border-[#374151] rounded-lg overflow-hidden"
              >
                {/* Log Header */}
                <div 
                  className={`p-4 flex flex-col md:flex-row md:items-center md:justify-between cursor-pointer ${
                    expandedLogId === log.id ? 'border-b border-[#374151]' : ''
                  }`}
                  onClick={() => setExpandedLogId(expandedLogId === log.id ? null : log.id)}
                >
                  <div className="flex items-center">
                    {expandedLogId === log.id ? 
                      <ChevronDownIcon className="h-5 w-5 text-[#9CA3AF] mr-2" /> : 
                      <ChevronRightIcon className="h-5 w-5 text-[#9CA3AF] mr-2" />
