                    <div className="flex items-center text-[#9CA3AF] text-sm mr-4">
                      <ClockIcon className="h-4 w-4 mr-1" />
                      {log.duration ? `${log.duration / 1000}s` : 'Running...'}
                    </div>
                    <div className="flex items-center text-[#9CA3AF] text-sm">
                      <CalendarIcon className="h-4 w-4 mr-1" />
                      {formatTimeAgo(log.startTime)}
                    </div>
                  </div>
                </div>

                {/* Log Details */}
                {expandedLogId === log.id && (
                  <div className="p-4">
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                      <div>
                        <div className="text-sm text-[#9CA3AF] mb-1">Execution ID</div>
                        <div className="text-[#E5E7EB] font-mono text-sm">{log.id}</div>
                      </div>
                      <div>
                        <div className="text-sm text-[#9CA3AF] mb-1">Start Time</div>
                        <div className="text-[#E5E7EB]">{formatDate(log.startTime)}</div>
                      </div>
                      <div>
                        <div className="text-sm text-[#9CA3AF] mb-1">End Time</div>
                        <div className="text-[#E5E7EB]">
                          {log.endTime ? formatDate(log.endTime) : 'In progress...'}
                        </div>
                      </div>
                      <div>
                        <div className="text-sm text-[#9CA3AF] mb-1">Duration</div>
                        <div className="text-[#E5E7EB]">
                          {log.duration ? `${log.duration / 1000} seconds` : 'Running...'}
                        </div>
                      </div>
                      <div>
                        <div className="text-sm text-[#9CA3AF] mb-1">Status</div>
                        <div className="text-[#E5E7EB]">
                          <StatusBadge status={log.status} />
                        </div>
                      </div>
                      <div>
                        <div className="text-sm text-[#9CA3AF] mb-1">Credits Consumed</div>
                        <div className="text-[#E5E7EB]">{log.creditsConsumed}</div>
                      </div>
                    </div>

                    {/* Step Execution Timeline */}
                    <div className="mb-4">
                      <h3 className="text-lg font-medium text-[#E5E7EB] mb-3">Execution Steps</h3>
                      <div className="space-y-4">
                        {log.steps.map((step, index) => (
                          <div key={step.id} className="bg-[#1E293B] rounded-lg p-4">
                            <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-3">
                              <div className="flex items-center">
                                <div className="flex items-center justify-center h-6 w-6 rounded-full bg-[#374151] text-[#E5E7EB] text-xs mr-2">
                                  {index + 1}
                                </div>
                                <div>
                                  <div className="text-[#E5E7EB] font-medium">{step.name}</div>
                                  <div className="text-xs text-[#9CA3AF]">{step.type}</div>
                                </div>
                              </div>

                              <div className="flex items-center mt-2 md:mt-0">
                                <div className={`px-2 py-0.5 rounded-full text-xs font-medium ${
                                  step.status === 'success' ? 'bg-green-100 text-green-800' :
                                  step.status === 'failed' ? 'bg-red-100 text-red-800' :
                                  step.status === 'running' ? 'bg-blue-100 text-blue-800' :
                                  step.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                                  'bg-gray-100 text-gray-800'
                                }`}>
                                  {step.status}
                                </div>
                                <div className="ml-3 text-xs text-[#9CA3AF]">
                                  {step.duration ? `${step.duration / 1000}s` : '...'}
                                </div>
                              </div>
                            </div>

                            {/* Step Details (Input/Output/Error) */}
                            <div className="grid grid-cols-1 lg:grid-cols-2 gap-3 mt-3">
                              {step.input && (
                                <div>
                                  <div className="text-xs text-[#9CA3AF] mb-1">Input</div>
                                  <pre className="text-xs bg-[#111111] p-2 rounded-md overflow-auto max-h-32 text-[#E5E7EB]">
                                    {JSON.stringify(step.input, null, 2)}
                                  </pre>
                                </div>
                              )}

                              {step.output && (
                                <div>
                                  <div className="text-xs text-[#9CA3AF] mb-1">Output</div>
                                  <pre className="text-xs bg-[#111111] p-2 rounded-md overflow-auto max-h-32 text-[#E5E7EB]">
                                    {JSON.stringify(step.output, null, 2)}
                                  </pre>
                                </div>
                              )}

                              {step.error && (
                                <div className="lg:col-span-2">
                                  <div className="text-xs text-[#F87171] mb-1">Error</div>
                                  <pre className="text-xs bg-[#371520] p-2 rounded-md overflow-auto max-h-32 text-[#F87171]">
                                    {step.error}
                                  </pre>
                                </div>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="flex justify-end space-x-3 mt-6">
                      {log.status === 'failed' && (
                        <button 
                          className="px-4 py-2 bg-[#22D3EE] text-[#111111] rounded-md hover:bg-[#06B6D4] transition-colors"
                          onClick={() => console.log('Retry execution', log.id)}
                        >
                          Retry Execution
                        </button>
                      )}
                      <button 
                        className="px-4 py-2 border border-[#374151] text-[#E5E7EB] rounded-md hover:bg-[#1E293B] transition-colors"
                        onClick={() => console.log('Export log', log.id)}
                      >
                        Export Log
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

