import React, { useRef, useState } from 'react';

interface VoiceOrbProps { onTranscript: (transcript: string) => void;
    }

export default function VoiceOrb({ onTranscript }: VoiceOrbProps): JSX.Element {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const recognitionRef = useRef<any>(null);

  const startListening = () => {
    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Speech recognition is not supported in this browser.');
      return;
    }
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = 'en-US';
    recognitionRef.current.onresult = (event: any) => { const t = Array.from(event.results).map((r: any) => r[0].transcript).join('');
      setTranscript(t);
      onTranscript(t);
      setIsListening(false);
        };
    recognitionRef.current.onerror = () => setIsListening(false);
    recognitionRef.current.onend = () => setIsListening(false);
    recognitionRef.current.start();
    setIsListening(true);
    setTranscript('');
  };

  return (
    <div className="fixed bottom-8 right-8 z-[9999] flex flex-col items-end">
      <button
        onClick={startListening}
        className={`w-20 h-20 rounded-full flex items-center justify-center shadow-2xl transition-all duration-300 bg-gradient-to-br from-[#3CDFFF] to-[#4AFFD4] hover:scale-110 ${ isListening ? 'ring-4 ring-[#3CDFFF]/50 animate-pulse' : ''    }`}
        title="Speak to Jarvis"
        aria-label="Speak to Jarvis"
      >
        <svg className="w-10 h-10 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 18v4m0 0h4m-4 0H8m8-8a4 4 0 10-8 0 4 4 0 008 0z" />
        </svg>
      </button>
      {transcript && (
        <div className="mt-4 bg-white/90 rounded-lg shadow-lg px-4 py-2 text-slate-800 max-w-xs">
          <span className="font-medium">You said:</span> <span>{transcript}</span>
        </div>
      )}
      {isListening && (
        <div className="mt-2 text-xs text-[#3CDFFF] animate-pulse">Listening...</div>
      )}
    </div>
  );
}

'use client';

import React, { useCallback, useState } from 'react';
import { toast } from 'sonner';

import { parseCommand } from '@/lib/workflow/commandParser';
import { CommandType, WorkflowCommand, WorkflowState } from '@/types/workflow';

import { VoiceCommandInput } from './VoiceCommandInput';
import { FlowEditor } from './workflow/FlowEditor';


export default function VoiceWorkflowBuilder(): JSX.Element {
  const [workflowState, setWorkflowState] = useState<WorkflowState>({ currentWorkflow: null,
    selectedNode: null,
    isRunning: false,
    error: null,
      });

  const handleCommand = useCallback((transcript: string) => {
    const command = parseCommand(transcript);
    if (!command) {
      toast.error('Command not recognized');
      return;
    }

    try {
      switch (command.type) {
        case CommandType.CREATE_NODE:
          if (!command.nodeType) break;
          // Add node logic
          toast.success(`Creating ${command.nodeType} node`);
          break;

        case CommandType.CONNECT_NODES:
          if (!command.sourceNode || !command.targetNode) break;
          // Connect nodes logic
          toast.success(`Connecting ${command.sourceNode} to ${command.targetNode}`);
          break;

        case CommandType.DELETE_NODE:
          if (!command.nodeName) break;
          // Delete node logic
          toast.success(`Deleting ${command.nodeName} node`);
          break;

        case CommandType.RENAME_NODE:
          if (!command.nodeName || !command.newName) break;
          // Rename node logic
          toast.success(`Renaming ${command.nodeName} to ${command.newName}`);
          break;

        case CommandType.CONFIGURE_NODE:
          if (!command.nodeName) break;
          // Configure node logic
          toast.success(`Configuring ${command.nodeName} node`);
          break;

        case CommandType.SAVE_WORKFLOW:
          // Save workflow logic
          toast.success('Saving workflow');
          break;

        case CommandType.LOAD_WORKFLOW:
          if (!command.workflowName) break;
          // Load workflow logic
          toast.success(`Loading workflow ${command.workflowName}`);
          break;

        case CommandType.RUN_WORKFLOW:
          // Run workflow logic
          setWorkflowState(prev => ({ ...prev, isRunning: true     }));
          toast.success('Running workflow');
          break;

        case CommandType.STOP_WORKFLOW:
          // Stop workflow logic
          setWorkflowState(prev => ({ ...prev, isRunning: false     }));
          toast.success('Stopping workflow');
          break;

        default:
          toast.error('Unknown command');
      }
    } catch (error) {
      toast.error('Error executing command');
      setWorkflowState(prev => ({ ...prev, error: error instanceof Error ? error.message : 'Unknown error'     }));
    }
  }, []);

  const handleError = useCallback((error: string) => {
    toast.error(`Voice recognition error: ${error}`);
    setWorkflowState(prev => ({ ...prev, error }));
  }, []);

  return (
    <div className="flex flex-col h-screen">
      <div className="flex-none p-4 bg-gray-100 dark:bg-gray-800">
        <VoiceCommandInput
          onCommand={handleCommand}
          onError={handleError}
          disabled={workflowState.isRunning}
        />
      </div>

      <div className="flex-grow">
        <FlowEditor
          workflow={workflowState.currentWorkflow}
          isRunning={workflowState.isRunning}
          selectedNode={workflowState.selectedNode}
          onNodeSelect={(nodeId) => setWorkflowState(prev => ({ ...prev, selectedNode: nodeId     }))}
        />
      </div>

      {workflowState.error && (
        <div className="flex-none p-4 bg-red-100 dark:bg-red-900">
          <p className="text-red-600 dark:text-red-300">{workflowState.error}</p>
        </div>
      )}
    </div>
  );
}

import React, { useCallback, useEffect } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  BackgroundVariant,
  Connection,
  Controls,
  Edge,
  MarkerType,
  Node,
  Panel,
  useEdgesState,
  useNodesState,
} from 'reactflow';

import 'reactflow/dist/style.css';
import { useWorkflowStore } from '@/lib/workflowStore';

interface WorkflowCanvasProps { isAdvancedMode: boolean;
    }

const initialNodes: Node[] = [
  {
    id: 'input',
    type: 'input',
    data: { label: 'Input Data'     },
    position: { x: 250, y: 25     },
  },
  {
    id: 'processor',
    data: { label: 'Data Processor'     },
    position: { x: 250, y: 125     },
  },
  {
    id: 'validator',
    data: { label: 'Data Validator'     },
    position: { x: 100, y: 225     },
  },
  {
    id: 'exporter',
    type: 'output',
    data: { label: 'Data Exporter'     },
    position: { x: 250, y: 325     },
  },
];

const initialEdges: Edge[] = [
  {
    id: 'input-processor',
    source: 'input',
    target: 'processor',
    animated: true,
    style: { stroke: 'url(#edge-gradient)'     },
  },
  {
    id: 'processor-validator',
    source: 'processor',
    target: 'validator',
    animated: true,
    style: { stroke: 'url(#edge-gradient)'     },
  },
  {
    id: 'validator-exporter',
    source: 'validator',
    target: 'exporter',
    animated: true,
    style: { stroke: 'url(#edge-gradient)'     },
  },
];

const WorkflowCanvas: React.FC<WorkflowCanvasProps> = ({ isAdvancedMode }) => {
  const { nodes: storeNodes,
    edges: storeEdges,
    updateNodes,
    updateEdges,
    validateWorkflow,
    saveWorkflow,
    isLoading,
    error,
      } = useWorkflowStore();

  const [nodes, setNodes, onNodesChange] = useNodesState(storeNodes.length ? storeNodes : initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(storeEdges.length ? storeEdges : initialEdges);

  useEffect(() => {
    if (storeNodes.length) {
      setNodes(storeNodes);
    }
  }, [storeNodes, setNodes]) // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    if (storeEdges.length) {
      setEdges(storeEdges);
    }
  }, [storeEdges, setEdges]) // eslint-disable-line react-hooks/exhaustive-deps

  const onConnect = useCallback(
    (params: Connection) => {
      setEdges((eds) => {
        const newEdges = addEdge({
          ...params,
          animated: true,
          style: { stroke: 'url(#edge-gradient)'     },
        }, eds);
        updateEdges(newEdges);
        return newEdges;
      });
    },
    [setEdges, updateEdges]
  ) // eslint-disable-line react-hooks/exhaustive-deps

  const onNodesDelete = useCallback(
    (nodesToDelete: Node[]) => {
      updateNodes(nodes.filter((node) => !nodesToDelete.find((n) => n.id === node.id)));
    },
    [nodes, updateNodes]
  ); // eslint-disable-line react-hooks/exhaustive-deps

  const onEdgesDelete = useCallback(
    (edgesToDelete: Edge[]) => {
      updateEdges(edges.filter((edge) => !edgesToDelete.find((e) => e.id === edge.id)));
    },
    [edges, updateEdges]
  ); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <div className="h-full w-full">
      <svg style={{ position: 'absolute', width: 0, height: 0     }}>
        <defs>
          <linearGradient id="edge-gradient" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="var(--edge-stop-1)" />
            <stop offset="100%" stopColor="var(--edge-stop-2)" />
          </linearGradient>
        </defs>
      </svg>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodesDelete={onNodesDelete}
        onEdgesDelete={onEdgesDelete}
        fitView
        fitViewOptions={{ padding: 0.2     }}
        defaultViewport={{ x: 0, y: 0, zoom: 1.5     }}
      >
        <Background variant={BackgroundVariant.Dots} gap={12} size={1} color="rgba(60, 223, 255, 0.3)" />
        <Controls />
        <Panel position="top-right" className="space-x-2">
          <button
            onClick={() => validateWorkflow()}
            className="px-4 py-2 text-sm font-medium text-white bg-gradient-to-r from-primary to-secondary rounded-md hover:from-primary-dark hover:to-secondary-dark focus:outline-none focus:ring-2 focus:ring-primary/50 disabled:opacity-50 transition-all duration-200"
            disabled={isLoading}
          >
            Validate
          </button>
          <button
            onClick={() => saveWorkflow()}
            className="px-4 py-2 text-sm font-medium text-white bg-gradient-to-r from-secondary to-primary rounded-md hover:from-secondary-dark hover:to-primary-dark focus:outline-none focus:ring-2 focus:ring-secondary/50 disabled:opacity-50 transition-all duration-200"
            disabled={isLoading}
          >
            Save
          </button>
        </Panel>
        {error && (
          <Panel position="bottom-center" className="bg-red-100 text-red-700 px-4 py-2 rounded-md">
            {error}
          </Panel>
        )}
      </ReactFlow>
    </div>
  );
};

