'use client';

import { useRouter } from 'next/navigation';
import { useEffect, useState } from 'react';

import { useUserStore } from '@/lib/userStore';
import { mcpService } from '@/services/mcp/MCPService';

interface ToolStatus { name: string;
  connected: boolean;
  lastCheck: string;
  type: string;
    }

export default function ToolStatus(): JSX.Element {
  const router = useRouter();
  const { currentUser } = useUserStore();
  const [tools, setTools] = useState<ToolStatus[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkTools = async () => {
      if (!currentUser) return;

      try {
        const response = await mcpService.sendRequest(
          currentUser.id,
          'aora',
          'check_tools',
          { tools: ['clay', 'lindyai', 'n8n']
              }
        );

        if (response.results) {
          const toolStatuses = response.results.map((result: any) => ({ name: result.tool,
            connected: result.connected,
            lastCheck: new Date().toISOString(),
            type: result.tool === 'clay' ? 'Data Processing' :
                  result.tool === 'lindyai' ? 'AI Assistant' :
                  'Workflow Automation'
              }));
          setTools(toolStatuses);
        }
      } catch (error) { console.error('Failed to check tool status:', error);
          } finally {
        setIsLoading(false);
      }
    };

    checkTools();
    const interval = setInterval(checkTools, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, [currentUser]) // eslint-disable-line react-hooks/exhaustive-deps

  if (isLoading) {
    return (
      <div className="bg-dark-50/30 backdrop-blur-md rounded-lg border border-primary/20 shadow-neon p-6">
        <h2 className="text-xl font-semibold text-white mb-4">Tool Status</h2>
        <div className="text-white/60 animate-pulse">Checking tool status...</div>
      </div>
    );
  }

  return (
    <div className="bg-dark-50/30 backdrop-blur-md rounded-lg border border-primary/20 shadow-neon p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-semibold text-white">Tool Status</h2>
        <button
          onClick={() => router.push('/tools')}
          className="text-primary hover:text-primary-dark transition-colors text-sm"
        >
          Manage Tools
        </button>
      </div>
      <div className="space-y-4">
        {tools.map((tool) => (
          <div
            key={tool.name}
            className="p-4 bg-dark-100/50 rounded-lg border border-primary/10"
          >
            <div className="flex items-center justify-between mb-2">
              <div>
                <h3 className="text-white font-medium capitalize">{tool.name}</h3>
                <div className="text-white/60 text-sm">{tool.type}</div>
              </div>
              <div className="flex items-center space-x-2">
                <div
                  className={`w-2 h-2 rounded-full ${ tool.connected ? 'bg-green-400' : 'bg-red-400'
                      } animate-pulse`}
                />
                <span className="text-white/60 text-sm">
                  { tool.connected ? 'Connected' : 'Disconnected'    }
                </span>
              </div>
            </div>
            <div className="text-white/40 text-xs">
              Last checked: {new Date(tool.lastCheck).toLocaleString()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

'use client';

import React, { ReactNode } from 'react';

import { isSafari } from '@/utils/browserDetection';

interface BackdropFilterProps {
  children: ReactNode;
  className?: string;
  intensity?: 'sm' | 'md' | 'lg' | 'xl';
  fallbackBgColor?: string;
  style?: React.CSSProperties;
}

/**
 * A component that applies backdrop-filter with cross-browser compatibility
 * Uses -webkit-backdrop-filter for Safari and falls back to a background color
 * if backdrop-filter is not supported
 */
export default function BackdropFilter({
  children,
  className = '',
  intensity = 'md',
  fallbackBgColor = 'rgba(0, 0, 0, 0.7)',
  style = {},
}: BackdropFilterProps) {
  const intensityValues = { sm: '4px', md: '8px', lg: '12px', xl: '16px' };

  const blurValue = intensityValues[intensity];
  const isClientSafari = typeof window !== 'undefined' && isSafari();

  // Combine styles with backdrop-filter properties
  const combinedStyle: React.CSSProperties = {
    ...style,
  };

  if (typeof window !== 'undefined') {
    combinedStyle.backdropFilter = `blur(${blurValue})`;
    combinedStyle.WebkitBackdropFilter = `blur(${blurValue})`;

    // For browsers that don't support backdrop-filter at all
    if (
      !CSS.supports('backdrop-filter', 'blur(8px)') &&
      !CSS.supports('-webkit-backdrop-filter', 'blur(8px)')
    ) {
      combinedStyle.backgroundColor = fallbackBgColor;
    }
  } else {
    // Server-side rendering - apply both for hydration
    combinedStyle.backdropFilter = `blur(${blurValue})`;
    combinedStyle.WebkitBackdropFilter = `blur(${blurValue})`;
  }

  return (
    <div className={className} style={combinedStyle}>
      {children}
    </div>
  );
}

import { HTMLMotionProps, motion } from 'framer-motion';
import { forwardRef } from 'react';

import { Loading } from './Loading';

interface ButtonProps
  extends Omit<HTMLMotionProps<'button'>, 'ref' | 'children'> {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost';
  size?: 'sm' | 'md' | 'lg';
  loading?: boolean;
  icon?: React.ReactNode;
  iconPosition?: 'left' | 'right';
  children?: React.ReactNode;
}

const variants = {
  primary:
    'bg-gradient-to-r from-blue-500 to-purple-500 text-white hover:opacity-90',
  secondary: 'bg-white/10 text-white hover:bg-white/20',
  outline: 'border border-white/20 text-white hover:bg-white/10',
  ghost: 'text-white hover:bg-white/10',
};

const sizes = {
  sm: 'px-3 py-1.5 text-sm',
  md: 'px-4 py-2',
  lg: 'px-6 py-3 text-lg',
};

export const Button = forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      children,
      variant = 'primary',
      size = 'md',
      loading = false,
      disabled = false,
      icon,
      iconPosition = 'left',
      className = '',
      ...props
    },
    ref
  ) => {
    const isDisabled = disabled || loading;

    return (
      <motion.button
        ref={ref}
        whileTap={{ scale: isDisabled ? 1 : 0.98 }}
        className={`
          relative flex items-center justify-center gap-2 rounded-lg font-medium
          transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed
          ${variants[variant]} ${sizes[size]} ${className}
        `}
        disabled={isDisabled}
        {...props}
      >
        {loading && (
          <div className="absolute inset-0 flex items-center justify-center bg-inherit rounded-lg">
            <Loading variant="spinner" size="sm" />
          </div>
        )}
        <span
          className={`flex items-center gap-2 ${loading ? 'invisible' : ''}`}
        >
          {icon && iconPosition === 'left' && icon}
          {children}
          {icon && iconPosition === 'right' && icon}
        </span>
      </motion.button>
    );
  }
);

