                        <Disclosure.Panel className="px-4 pt-4 pb-2 text-sm text-gray-200">
                          <div className="space-y-4">
                            {/* Max Tokens */}
                            <div>
                              <label htmlFor="maxTokens" className="block text-sm font-medium text-gray-200 mb-1">
                                Max Tokens
                              </label>
                              <input
                                type="number"
                                id="maxTokens"
                                name="maxTokens"
                                value={config.maxTokens}
                                onChange={handleInputChange}
                                className="w-full px-3 py-2 bg-dark-100 border border-dark-200 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                              />
                            </div>

                            {/* System Message */}
                            <div>
                              <label htmlFor="systemMessage" className="block text-sm font-medium text-gray-200 mb-1">
                                System Message
                              </label>
                              <textarea
                                id="systemMessage"
                                name="systemMessage"
                                value={config.systemMessage}
                                onChange={handleInputChange}
                                className="w-full px-3 py-2 bg-dark-100 border border-dark-200 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                                rows={4}
                                placeholder="Define system-level instructions..."
                              />
                            </div>
                          </div>
                        </Disclosure.Panel>
                      </>
                    )}
                  </Disclosure>
                </div>

                <div className="mt-6 flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={onClose}
                    className="px-4 py-2 text-sm font-medium text-white bg-dark-200 rounded-md hover:bg-dark-300 focus:outline-none focus-visible:ring-2 focus-visible:ring-white"
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    onClick={handleSave}
                    className="px-4 py-2 text-sm font-medium text-dark-50 bg-gradient-to-r from-primary to-secondary rounded-md hover:from-primary-dark hover:to-secondary-dark focus:outline-none focus-visible:ring-2 focus-visible:ring-primary"
                  >
                    Save Changes
                  </button>
                </div>
              </Dialog.Panel>
            </Transition.Child>
          </div>
        </div>
      </Dialog>
    </Transition>
  );
}

'use client';

import React, { useEffect, useRef, useState } from 'react';
import { Edge, Node } from 'reactflow';


import { Spinner } from '@/components/ui/Spinner';
import { Toast } from '@/components/ui/Toast';
import { validateWorkflow } from '@/utils/workflowValidation';

import { WorkflowNodeCreator } from './WorkflowNodeCreator';

interface ConversationalWorkflowBuilderProps {
  onWorkflowUpdate: (nodes: Node[], edges: Edge[]) => void;
  agentId: string;
  apiKey: string;
}

interface Message {
  role: 'user' | 'assistant';
  content: string;
  timestamp: number;
  status?: 'sending' | 'sent' | 'error';
  workflowStep?: {
    id: string;
    title: string;
    description: string;
    type: string;
    parameters: Record<string, unknown>;
  };
}

export const ConversationalWorkflowBuilder: React.FC<ConversationalWorkflowBuilderProps> = ({
  onWorkflowUpdate,
  agentId,
  apiKey
}) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showToast, setShowToast] = useState(false);
  const [selectedStep, setSelectedStep] = useState<string | null>(null);
  const [nodes, setNodes] = useState<Node[]>([]);
  const [edges, setEdges] = useState<Edge[]>([]);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]) // eslint-disable-line react-hooks/exhaustive-deps

  const handleEditStep = async (stepId: string) => {
    const message = messages.find(m => m.workflowStep?.id === stepId);
    if (!message?.workflowStep) return;

    setInputText(`Edit step &ldquo;${message.workflowStep.title}&rdquo;: `);
    setSelectedStep(stepId);
  };

  const handleSendMessage = async () => {
    if (!inputText.trim() || isProcessing) return;

    setIsProcessing(true);
    setError(null);
    setShowToast(false);

    try {
      // Add user message to the chat with 'sending' status
      const userMessage = { role: 'user' as const,
        content: inputText,
        timestamp: Date.now(),
        status: 'sending' as const
      };

      setMessages(prev => [...prev, userMessage]);

      // Check if this is an edit request
      const isEdit = selectedStep !== null;

      // Send request to our API
      const response = await fetch('/api/workflow-conversation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: inputText,
          agentId,
          apiKey,
          conversationHistory: messages,
          editStepId: selectedStep
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to process message');
      }

      const data = await response.json();

      // Update user message status to 'sent'
      setMessages(prev => prev.map(msg => 
        msg === userMessage ? { ...msg, status: 'sent' as const } : msg
      ));

      // Add assistant message to the chat
      setMessages(data.conversationHistory);

      // Validate workflow before converting and updating
      if (data.workflow) {
        const validationResult = validateWorkflow(data.workflow);

        if (!validationResult.isValid) {
          const errorMessages = validationResult.errors.map(err => 
            `${err.message} (at ${err.path.join('.')})`
          ).join('\n');

          setError(`Invalid workflow structure:\n${errorMessages}`);
          setShowToast(true);
          return;
        }

        const { nodes: newNodes, edges: newEdges } = convertWorkflowToReactFlow(validationResult.validatedData!);
        setNodes(newNodes);
        setEdges(newEdges);
        onWorkflowUpdate(newNodes, newEdges);
      }

      // Clear input and selected step
      setInputText('');
      setSelectedStep(null);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to send message';
      setError(errorMessage);
      setShowToast(true);

      // Update user message status to 'error'
      setMessages(prev => prev.map(msg => 
        msg === prev[prev.length - 1] ? { ...msg, status: 'error' as const } : msg
      ));

      console.error('Send message error:', err);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Convert workflow to ReactFlow nodes and edges
  const convertWorkflowToReactFlow = (workflow: any) => {
    const nodes: Node[] = [];
    const edges: Edge[] = [];

    // Calculate layout grid
    const GRID_SPACING_X = 300;
    const GRID_SPACING_Y = 150;
    const MAX_NODES_PER_COLUMN = 3;

    // Create nodes with improved positioning
    workflow.steps.forEach((step: any, index: number) => {
      const column = Math.floor(index / MAX_NODES_PER_COLUMN);
      const row = index % MAX_NODES_PER_COLUMN;

      nodes.push({
        id: step.id,
        type: step.type.toLowerCase(),
        position: {
          x: column * GRID_SPACING_X + 100,
          y: row * GRID_SPACING_Y + 100
        },
        data: {
          label: step.title,
          description: step.description,
          config: step.parameters
        }
      });
    });

    // Create edges
    workflow.connections.forEach((connection: any) => {
      edges.push({
        id: `${connection.source}-${connection.target}`,
        source: connection.source,
        target: connection.target,
        animated: true,
        style: { stroke: '#6366f1' }
      });
    });

    return { nodes, edges };
  };

  return (
    <div className="flex flex-col h-full">
      <div className="flex-1 relative">
        <WorkflowNodeCreator
          initialNodes={nodes}
          initialEdges={edges}
          onNodesChange={setNodes}
          onEdgesChange={setEdges}
        />
      </div>

      <div className="border-t border-gray-200 dark:border-gray-800 p-4">
        <div className="flex items-center space-x-2">
          <textarea
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Describe your workflow step..."
            className="flex-1 min-h-[80px] p-2 rounded-md border border-gray-300 dark:border-gray-700 bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100"
          />
          <button
            onClick={handleSendMessage}
            disabled={isProcessing || !inputText.trim()}
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 disabled:opacity-50"
          >
            { isProcessing ? <Spinner size="sm" /> : 'Send' }
          </button>
        </div>
      </div>

      {showToast && error && (
        <Toast
          title="Error"
          description={error}
          variant="destructive"
          onClose={() => setShowToast(false)}
        />
      )}

      <div ref={messagesEndRef} />
    </div>
  );
};

'use client';

import { TrashIcon } from '@heroicons/react/24/outline';
import { useState } from 'react';
import { getBezierPath, getMarkerEnd } from 'reactflow';
import type { EdgeProps, MarkerType } from 'reactflow';

import styles from './CustomEdge.module.css';

export default function CustomEdge(props: EdgeProps) {
  const { id, sourceX, sourceY, sourcePosition, targetX, targetY, targetPosition, markerEnd, data, style } = props;
  const [isHovered, setIsHovered] = useState(false);

  const [edgePath] = getBezierPath({
    sourceX,
    sourceY,
    sourcePosition,
    targetX,
    targetY,
    targetPosition,
  });

  const markerEndId = getMarkerEnd(markerEnd as MarkerType);

  // Label for the edge, can be customized based on the connection type
  const edgeLabel = data?.label || '';

  return (
    <g
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      className="react-flow__edge-custom"
    >
      <path
        id={id}
        className={`react-flow__edge-path ${styles['edgePath']} ${isHovered ? styles['edgePathHovered'] : ''} ${data?.dashed ? styles['dashed'] : ''}`}
        d={edgePath}
        strokeWidth={isHovered ? 3 : 2}
        markerEnd={markerEndId}
      />

      {/* Optional edge label */}
      {edgeLabel && (
        <foreignObject
          width={100}
          height={40}
          x={(sourceX + targetX) / 2 - 50}
          y={(sourceY + targetY) / 2 - 20}
          className={`react-flow__edge-label ${styles['edgeLabel']}`}
        >
          <div
            className={`px-2 py-1 text-xs rounded-md bg-dark-100 text-white text-center border border-gray-700 shadow-md ${styles['labelDiv']} ${isHovered ? styles['labelDivHovered'] : ''}`}
          >
            {edgeLabel}
          </div>
        </foreignObject>
      )}

      {/* Delete button that appears on hover */}
      {isHovered && (
        <foreignObject
          width={24}
          height={24}
          x={(sourceX + targetX) / 2 - 12}
          y={(sourceY + targetY) / 2 - 12 + (edgeLabel ? 30 : 0)}
          className={`react-flow__edge-button ${styles['edgeButton']}`}
        >
          <button
            title="Delete edge"
            aria-label="Delete edge"
            className="flex items-center justify-center w-6 h-6 rounded-full bg-red-600 text-white hover:bg-red-700 shadow-md"
            onClick={(event) => {
              event.stopPropagation();
              if (data?.onDelete) {
                data.onDelete(id);
              }
            }}
          >
            <TrashIcon className="w-3 h-3" />
          </button>
        </foreignObject>
      )}
    </g>
  );
}

'use client';

import { CheckIcon, CogIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { useState } from 'react';
import { Handle, Position } from 'reactflow';

import DefaultNodeConfig, { DefaultNodeData } from './DefaultNodeConfig';

interface DefaultNodeProps {
  data: DefaultNodeData;
  selected: boolean;
  isConnectable: boolean;
  id: string;
}

// Inline edit field component for direct editing without modal
interface InlineEditFieldProps {
  name: string;
  value: any;
  type: string;
  onChange: (name: string, value: any) => void;
  onSave: () => void;
  onCancel: () => void;
}

function InlineEditField({ name, value, type, onChange, onSave, onCancel }: InlineEditFieldProps) {
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => { const newValue = e.target.type === 'checkbox' 
      ? (e.target as HTMLInputElement).checked 
      : e.target.value;
    onChange(name, newValue);
      };

  return (
    <div className="flex items-center space-x-2">
      {type === 'textarea' ? (
        <textarea
          className="flex-1 bg-dark-100 border border-primary text-white px-2 py-1 text-xs rounded"
          value={value || ''}
          onChange={handleChange}
          rows={2}
          title="Edit value"
          placeholder="Enter value"
        />
      ) : type === 'select' ? (
        <select
          className="flex-1 bg-dark-100 border border-primary text-white px-2 py-1 text-xs rounded"
          value={value || ''}
          onChange={handleChange}
          title="Select option"
        >
          <option value="">Select...</option>
          {/* Options would be dynamically populated based on field type */}
        </select>
      ) : type === 'checkbox' ? (
        <input
          type="checkbox"
          checked={value || false}
          onChange={handleChange}
          className="h-4 w-4 rounded text-primary focus:ring-primary"
          title="Toggle option"
        />
      ) : (
        <input
          type={type}
          className="flex-1 bg-dark-100 border border-primary text-white px-2 py-1 text-xs rounded"
          value={value || ''}
          onChange={handleChange}
          title="Edit value"
          placeholder="Enter value"
        />
      )}
      <button 
        onClick={onSave} 
        className="p-1 rounded bg-green-800 hover:bg-green-700"
        title="Save"
      >
        <CheckIcon className="h-3 w-3 text-white" />
      </button>
      <button 
        onClick={onCancel} 
        className="p-1 rounded bg-red-800 hover:bg-red-700"
        title="Cancel"
      >
        <XMarkIcon className="h-3 w-3 text-white" />
      </button>
    </div>
  );
}

export default function DefaultNode({ data, selected, isConnectable, id }: DefaultNodeProps) {
  const [isConfigOpen, setIsConfigOpen] = useState(false);
  const [nodeData, setNodeData] = useState<DefaultNodeData>({
    id,
    type: data.type || 'default',
    label: data.label,
    description: data.description,
    settings: data.settings || {},
  });

  // Track which field is being edited inline
  const [editingField, setEditingField] = useState<string | null>(null);
  const [tempEditValue, setTempEditValue] = useState<any>(null);

  const handleConfigure = () => {
    setIsConfigOpen(true);
  };

  const handleConfigSave = (updatedData: DefaultNodeData) => {
    setNodeData(updatedData);

    // If the component has an onConfigChange prop, call it
    if (data.onConfigChange) {
      data.onConfigChange(updatedData);
    }
  };

  // Start editing a specific field
  const startEditingField = (key: string, value: any) => {
    setEditingField(key);
    setTempEditValue(value);
  };

  // Handle inline field change
  const handleInlineFieldChange = (name: string, value: any) => {
    setTempEditValue(value);
  };

  // Save inline edit
  const saveInlineEdit = () => {
    if (editingField !== null) {
      const updatedSettings = { ...nodeData.settings,
        [editingField]: tempEditValue
          };

      const updatedData = { ...nodeData,
        settings: updatedSettings
          };

      setNodeData(updatedData);

      // If the component has an onConfigChange prop, call it
      if (data.onConfigChange) {
        data.onConfigChange(updatedData);
      }

      // Reset editing state
      setEditingField(null);
      setTempEditValue(null);
    }
  };

  // Cancel inline edit
  const cancelInlineEdit = () => {
    setEditingField(null);
    setTempEditValue(null);
  };

  return (
    <>
      <div 
        className={`
          relative p-4 min-w-[220px] 
          ${ selected ? 'shadow-neon' : ''    }
          transition-all duration-300
        `}
        style={{ backgroundColor: 'transparent',
            }}
      >
        <div className={`
          absolute inset-0 rounded-xl
          ${ selected ? 'border-2 border-primary' : 'border border-gray-700'    }
          bg-dark-50
          `}
          style={{ backgroundColor: 'rgba(17, 24, 39, 0.95)',
            backdropFilter: 'blur(5px)',
            WebkitBackdropFilter: 'blur(5px)'
              }}
        />

        <Handle
          type="target"
          position={Position.Top}
          className="w-3 h-3 bg-blue-400 z-10"
          isConnectable={isConnectable}
        />

        <div className="relative z-10">
          <div className="flex items-center justify-between">
            <div className="flex-1">
              <p className="text-sm font-medium text-white">
                {nodeData.label}
              </p>
              {nodeData.description && (
                <p className="text-xs text-gray-400 mt-1">
                  {nodeData.description}
                </p>
              )}
            </div>
            <button
              className="p-1 rounded-md hover:bg-dark-200 transition-colors ml-2"
              onClick={handleConfigure}
              title="Configure node"
            >
              <CogIcon className="h-4 w-4 text-gray-400" />
            </button>
          </div>

          {/* Show some settings summary if available */}
          {nodeData.settings && Object.keys(nodeData.settings).length > 0 && (
            <div className="mt-2 pt-2 border-t border-gray-700">
              <div className="flex flex-col gap-2">
                {Object.entries(nodeData.settings).map(([key, value]) => (
                  <div key={key}>
                    {editingField === key ? (
                      <InlineEditField
                        name={key}
                        value={tempEditValue}
                        type="text" // Would be determined by field type in a real implementation
                        onChange={handleInlineFieldChange}
                        onSave={saveInlineEdit}
                        onCancel={cancelInlineEdit}
                      />
                    ) : (
                      <div 
                        className="flex items-center justify-between text-xs px-2 py-1 rounded-md bg-dark-200 text-gray-300 cursor-pointer hover:bg-dark-300"
                        onClick={() => selected && startEditingField(key, value)}
                      >
                        <span className="font-medium">{key}:</span>
                        <span className="truncate max-w-[120px]">
                          { typeof value === 'boolean' 
                            ? (value ? 'Yes' : 'No')
                            : String(value).length > 15 
                              ? String(value).substring(0, 12) + '&hellip;'
                              : String(value)
                              }
                        </span>
                      </div>
                    )}
                  </div>
                ))}
                {selected && Object.keys(nodeData.settings).length > 0 && (
                  <div className="text-xs text-gray-400 italic mt-1">
                    Click on a setting to edit
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <Handle
          type="source"
          position={Position.Bottom}
          className="w-3 h-3 bg-blue-400 z-10"
          isConnectable={isConnectable}
        />
      </div>

      {isConfigOpen && (
        <DefaultNodeConfig
          isOpen={isConfigOpen}
          onClose={() => setIsConfigOpen(false)}
          onSave={handleConfigSave}
          nodeData={nodeData}
        />
      )}
    </>
  );
}

