  const [edges, setEdges, onEdgesChange] = useEdgesState(initialFlow?.edges || []);
  const [connectedApps, setConnectedApps] = useState<string[]>([]);
  const [selectedNode, setSelectedNode] = useState<Node | null>(null);
  const [showSidebar, setShowSidebar] = useState(true);
  const [activeCategory, setActiveCategory] = useState('triggers');
  const reactFlowWrapper = useRef<HTMLDivElement>(null);
  const [reactFlowInstance, setReactFlowInstance] = useState<any>(null);
  const [edgeCustomization, setEdgeCustomization] = useState<EdgeCustomization>({ 
    type: 'default',
    color: '#6366f1',
    width: 2,
    animated: false
  });

  const { currentUser } = useUserStore();
  const userId = currentUser?.id ?? '';

  // Load connected apps on mount
  useEffect(() => {
    const fetchConnectedApps = async () => {
      try {
        if (!userId) return;
        const response = await fetch(`/api/livekit/connections?userId=${userId}`);
        if (!response.ok) throw new Error('Failed to fetch connections');
        const connections = await response.json();
        const appIds = connections.map((conn: any) => conn.app);
        setConnectedApps(appIds);
      } catch (error) {
        console.error('Error loading connected apps:', error);
      }
    };
    fetchConnectedApps();
  }, [userId]);

  // Enhanced connection validation
  const onConnect = (connection: Connection) => {
    if (!connection.source || !connection.target) return;

    const sourceNode = nodes.find((node) => node.id === connection.source);
    const targetNode = nodes.find((node) => node.id === connection.target);

    if (!sourceNode?.type || !targetNode?.type) return;

    const sourceType = sourceNode.type as keyof typeof connectionRules;
    const rules = connectionRules[sourceType];
    if (!rules) return;

    // Validate connection
    if (!rules.allowedTargets.includes(targetNode.type) && !rules.allowedTargets.includes('*')) {
      console.warn(`Connection not allowed: ${sourceType} -> ${targetNode.type}`);
      return;
    }

    const existingConnections = edges.filter(
      (edge) => edge.source === connection.source
    );

    if (existingConnections.length >= rules.maxConnections) {
      console.warn(`Maximum connections reached for ${sourceType}`);
      return;
    }

    // Create new edge with customization
    const newEdge: Edge = {
      id: `${connection.source}-${connection.target}`,
      source: connection.source,
      target: connection.target,
      type: edgeCustomization.type,
      data: {
        label: edgeCustomization.label || '',
        color: edgeCustomization.color,
        width: edgeCustomization.width,
        animated: edgeCustomization.animated
      },
    };

    setEdges((eds: Edge[]) => [...eds, newEdge])
  };

  // Handle connecting an app
  const handleConnectApp = async (appId: string) => {
    if (isAppConnected(appId, connectedApps)) {
      toast.success(`Already connected to ${appId}`);
      return;
    }
    toast.success(`Connecting to ${appId}...`);
    try {
      if (!userId) throw new Error('User not authenticated');
      const app = availableApps.find(a => a.id === appId);
      const response = await fetch('/api/livekit/connections', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId,
          app: appId,
          app_name: app?.name || appId,
        }),
      });
      if (!response.ok) throw new Error('Failed to add connection');
      setConnectedApps(prev => [...prev, appId]);
      toast.success(`Successfully connected to ${appId}!`);
    } catch (error) {
      console.error('Error connecting to app:', error);
      toast.error(`Failed to connect to ${appId}`);
    }
  };

  // Handle drag over for node drag & drop
  const onDragOver = useCallback((event: React.DragEvent) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  // Handle drop for node drag & drop
  const onDrop = useCallback(
    (event: React.DragEvent) => {
      event.preventDefault();

      if (reactFlowWrapper.current && reactFlowInstance) {
        const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
        const templateId = event.dataTransfer.getData('application/reactflow/template');

        if (templateId) {
          const template = nodeTemplates.find(t => t.label === templateId);

          if (template) {
            const position = reactFlowInstance.screenToFlowPosition({ 
              x: event.clientX - reactFlowBounds.left,
              y: event.clientY - reactFlowBounds.top,
            });

            const newNode: Node = {
              id: `${template.type}-${Date.now()}`,
              type: template.type,
              position,
              data: {
                label: template.label,
                description: template.description,
                icon: template.icon,
                category: template.category,
                model: template.model,
                config: {}
              },
            };

            setNodes((nds) => nds.concat(newNode));
          }
        }
      }
    },
    [reactFlowInstance, setNodes, nodeTemplates]
  ) // eslint-disable-line react-hooks/exhaustive-deps

  // Handle node selection
  const onNodeClick = useCallback((event: React.MouseEvent, node: Node) => {
    setSelectedNode(node);
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  // Handle save flow
  const handleSaveFlow = useCallback(() => {
    if (onSave) {
      onSave({ nodes, edges });
    }
    toast.success('Workflow saved successfully!');
  }, [nodes, edges, onSave]) // eslint-disable-line react-hooks/exhaustive-deps

  // Handle drag start for node templates
  const onDragStart = (event: React.DragEvent, templateId: string) => {
    event.dataTransfer.setData('application/reactflow/template', templateId);
    event.dataTransfer.effectAllowed = 'move';
  };

  // Get filtered templates based on active category
  const getFilteredTemplates = () => {
    return nodeTemplates.filter(template => template.category === activeCategory);
  };

  // Add edge customization controls
  const EdgeCustomizationPanel = () => (
    <div className="absolute bottom-4 right-4 bg-dark-100 p-4 rounded-lg shadow-lg">
      <h3 className="text-white text-sm font-medium mb-2">Edge Style</h3>
      <div className="space-y-2">
        <select
          value={edgeCustomization.type}
          onChange={(e) => setEdgeCustomization(prev => ({ ...prev, type: e.target.value as any }))}
          className="w-full px-2 py-1 bg-dark-200 text-white rounded"
          title="Edge Type"
        >
          <option value="default">Default</option>
          <option value="animated">Animated</option>
          <option value="dashed">Dashed</option>
          <option value="dotted">Dotted</option>
        </select>
        <input
          type="color"
          value={edgeCustomization.color}
          onChange={(e) => setEdgeCustomization(prev => ({ ...prev, color: e.target.value }))}
          className="w-full"
          title="Edge Color"
        />
        <input
          type="number"
          value={edgeCustomization.width}
          onChange={(e) => setEdgeCustomization(prev => ({ ...prev, width: parseInt(e.target.value) }))}
          min="1"
          max="5"
          className="w-full px-2 py-1 bg-dark-200 text-white rounded"
          title="Edge Width"
          placeholder="Edge Width"
        />
        <input
          type="text"
          value={edgeCustomization.label || ''}
          onChange={(e) => setEdgeCustomization(prev => ({ ...prev, label: e.target.value }))}
          placeholder="Edge label"
          className="w-full px-2 py-1 bg-dark-200 text-white rounded"
          title="Edge Label"
        />
        <label className="flex items-center text-white">
          <input
            type="checkbox"
            checked={edgeCustomization.animated}
            onChange={(e) => setEdgeCustomization(prev => ({ ...prev, animated: e.target.checked }))}
            className="mr-2"
            title="Animate Edge"
          />
          Animate
        </label>
      </div>
    </div>
  );

  return (
    <div className="flex h-[calc(100vh-64px)]">
      {/* Sidebar with node templates and connected apps */}
      {showSidebar && (
        <div className="w-64 bg-dark-50 border-r border-dark-100 overflow-y-auto flex flex-col">
          {/* Node categories */}
          <div className="p-4 border-b border-dark-100">
            <div className="flex flex-wrap gap-2">
              {nodeCategories.map((category) => (
                <button
                  key={category.id}
                  className={`px-3 py-1 text-xs rounded-full flex items-center gap-1 ${
                    activeCategory === category.id
                      ? 'bg-primary text-white'
                      : 'bg-dark-100 text-white/80 hover:bg-dark-200'
                  }`}
                  onClick={() => setActiveCategory(category.id)}
                >
                  <span>{category.icon}</span>
                  <span>{category.name}</span>
                </button>
              ))}
            </div>
          </div>

          {/* Node templates */}
          <div className="p-4 overflow-y-auto">
            <h3 className="text-white/80 text-sm font-semibold mb-2">Templates</h3>
            <div className="space-y-2">
              {getFilteredTemplates().map((template) => (
                <div
                  key={template.label}
                  className="bg-dark-100 hover:bg-dark-200 cursor-move p-3 rounded-md text-white/90"
                  draggable
                  onDragStart={(e) => onDragStart(e, template.label)}
                >
                  <div className="flex items-center space-x-2">
                    <span className="text-lg">{template.icon}</span>
                    <span>{template.label}</span>
                  </div>
                  {template.description && (
                    <p className="text-white/60 text-xs mt-1">{template.description}</p>
                  )}
                  {template.model && (
                    <div className="mt-1 text-xs bg-dark-200 text-primary rounded-full px-2 py-0.5 inline-block">
                      {template.model}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Connected apps */}
          <div className="p-4 border-t border-dark-100 mt-auto">
            <h3 className="text-white/80 text-sm font-semibold mb-2">Connected Apps</h3>
            <div className="space-y-2">
              {availableApps
                .filter(app => activeCategory === 'ai' ? app.category === 'ai' : app.category !== 'ai')
                .map((app) => (
                <div
                  key={app.id}
                  className="flex items-center justify-between bg-dark-100 p-2 rounded-md"
                >
                  <div className="flex items-center space-x-2">
                    <span className="text-lg">{app.icon}</span>
                    <span className="text-white/90 text-sm">{app.name}</span>
                  </div>
                  {connectedApps.includes(app.id) ? (
                    <span className="text-green-500 text-xs">✓ Connected</span>
                  ) : (
                    <button
                      onClick={() => handleConnectApp(app.id)}
                      className="text-xs bg-primary/20 hover:bg-primary/30 text-primary rounded px-2 py-1"
                    >
                      Connect
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Flow Editor Canvas */}
      <div className="flex-1 flex flex-col h-full">
        <div className="flex justify-between items-center p-2 bg-dark-100 border-b border-dark-200">
          <div className="flex items-center space-x-2">
            <button 
              onClick={() => setShowSidebar(!showSidebar)}
              className="text-white/80 hover:text-white p-1"
            >
              { showSidebar ? '◀' : '▶' }
            </button>
            <h2 className="text-white font-medium">Workflow Editor</h2>
          </div>
          <div className="flex items-center space-x-2">
            <button
              onClick={handleSaveFlow}
              className="text-sm bg-primary hover:bg-primary-dark text-white px-3 py-1 rounded transition-colors"
            >
              Save
            </button>
          </div>
        </div>

        <div className="flex-1" ref={reactFlowWrapper}>
          <ReactFlowProvider>
            <ReactFlow
              nodes={nodes}
              edges={edges}
              onNodesChange={onNodesChange}
              onEdgesChange={onEdgesChange}
              onConnect={onConnect}
              onInit={setReactFlowInstance}
              onDrop={onDrop}
              onDragOver={onDragOver}
              onNodeClick={onNodeClick}
              nodeTypes={nodeTypes}
              edgeTypes={edgeTypes}
              fitView
            >
              <Controls className="bg-dark-100 text-white border-dark-200" />
              <MiniMap
                nodeColor={(node) => {
                  switch (node.type) { 
                    case 'trigger':
                    case 'scheduleTrigger': 
                    case 'webhookTrigger':
                    case 'eventTrigger':
                      return '#FF7F50'; // Coral
                    case 'action':
                    case 'email':
                    case 'httpRequest':
                    case 'slack':
                      return '#06b6d4'; // Cyan
                    case 'wait':
                    case 'delay':
                      return '#f59e0b'; // Amber
                    case 'conditional':
                    case 'condition':
                    case 'switch':
                    case 'loop':
                      return '#a855f7'; // Purple
                    case 'textGeneration':
                    case 'chatCompletion':
                    case 'summarization':
                    case 'translation':
                    case 'imageGeneration':
                    case 'textToSpeech':
                    case 'documentExtraction':
                      return '#8b5cf6'; // Violet
                    default:
                      return '#6b7280'; // Gray
                  }
                }}
                maskColor="rgba(0, 0, 0, 0.1)"
                className="bg-dark-100 border-dark-200"
              />
              <Background gap={20} size={1} />
              <EdgeCustomizationPanel />
              {selectedNode && (
                <Panel position="top-right" className="bg-dark-100 p-4 rounded-lg shadow-lg max-w-md">
                  <h3 className="text-white font-medium mb-2">Node Properties</h3>
                  <div className="text-white/80 text-sm">
                    <p><strong>Type:</strong> {selectedNode.type}</p>
                    <p><strong>Label:</strong> {selectedNode.data.label}</p>
                    {selectedNode.data.description && (
                      <p><strong>Description:</strong> {selectedNode.data.description}</p>
                    )}
                    {selectedNode.data.model && (
                      <p><strong>Model:</strong> {selectedNode.data.model}</p>
                    )}
                  </div>
                  <button
                    className="mt-2 bg-dark-200 text-white/80 hover:text-white px-2 py-1 rounded text-xs"
                    onClick={() => setSelectedNode(null)}
                  >
                    Close
                  </button>
                </Panel>
              )}
            </ReactFlow>
          </ReactFlowProvider>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useEffect, useRef, useState } from 'react';
import { HiOutlineMicrophone, HiOutlineStop } from 'react-icons/hi';

import MicrophonePermission from '@/components/MicrophonePermission';

interface InteractiveVoiceAgentProps { onWorkflowGenerated: (workflow: any) => void;
    }

declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

const WaveformAnimation = () => {
  return (
    <div className="flex items-center justify-center gap-1 h-8">
      {[...Array(5)].map((_, i) => (
        <div
          key={i}
          className="w-1 bg-white rounded-full"
        />
      ))}
    </div>
  );
};

const PulsingCircle = () => {
  return (
    <div className="relative">
      <div
        className="absolute inset-0 rounded-full bg-white/20"
      />
      <div
        className="absolute inset-0 rounded-full bg-white/40"
      />
    </div>
  );
};

export const InteractiveVoiceAgent: React.FC<InteractiveVoiceAgentProps> = ({ onWorkflowGenerated }) => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showPermissionDialog, setShowPermissionDialog] = useState(false);
  const [audioSrc, setAudioSrc] = useState<string | null>(null);
  const [isExpanded, setIsExpanded] = useState(false);

  const recognitionRef = useRef<any>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const chunksRef = useRef<Blob[]>([]);
  const audioRef = useRef<HTMLAudioElement>(null);

  useEffect(() => {
    // Initialize speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setError('Speech recognition is not supported in this browser');
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = true;

    recognitionRef.current.onresult = (event: any) => { const transcript = Array.from(event.results)
        .map((result: any) => result[0].transcript)
        .join('');
      setTranscript(transcript);
        };

    recognitionRef.current.onerror = (event: any) => {
      console.error('Speech recognition error:', event.error);
      setError(`Speech recognition error: ${event.error}`);
      setIsListening(false);
    };

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (mediaRecorderRef.current) {
        mediaRecorderRef.current.stop();
      }
    };
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  const startListening = async () => {
    setError(null);
    setTranscript('');
    setShowPermissionDialog(true);
  };

  const handlePermissionGranted = async () => {
    setShowPermissionDialog(false);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true     });

      // Initialize MediaRecorder
      mediaRecorderRef.current = new MediaRecorder(stream);
      chunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(chunksRef.current, { type: 'audio/wav'     });
        await processAudioData(audioBlob);
      };

      // Start recording
      mediaRecorderRef.current.start();
      recognitionRef.current?.start();
      setIsListening(true);
    } catch (err) { console.error('Error starting voice recording:', err);
      setError('Failed to start voice recording. Please check your microphone settings.');
        }
  };

  const handlePermissionDenied = () => {
    setShowPermissionDialog(false);
    setError('Microphone access is required for voice commands. Please allow microphone access and try again.');
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    setIsListening(false);
  };

  const processAudioData = async (audioBlob: Blob) => {
    if (!audioBlob) return;

    setProcessing(true);
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob);

      const response = await fetch('/api/elevenlabs-convai', { method: 'POST',
        body: formData,
          });

      if (!response.ok) {
        throw new Error('Failed to process audio');
      }

      const data = await response.json();
      console.log('Processed audio data:', data);

      // Handle the response data as needed
      if (data.workflow) {
        const confirmationText = `I've created a workflow based on your request: "${data.workflow.name}". ${data.workflow.description}`;
        console.log('InteractiveVoiceAgent: Generating voice feedback:', confirmationText);

        const audioResponse = await fetch('/api/elevenlabs', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            action: 'textToSpeech',
            text: confirmationText
          })
        });

        console.log('InteractiveVoiceAgent: Text-to-speech response status:', audioResponse.status, audioResponse.statusText);

        if (audioResponse.ok) {
          const audioData = await audioResponse.json();
          console.log('InteractiveVoiceAgent: Received audio data of length:', audioData?.audio?.length || 0);
          setAudioSrc(`data:audio/mpeg;base64,${audioData.audio}`);
        } else { const errorData = await audioResponse.json().catch(() => null);
          console.error('Failed to generate speech:', errorData);
            }

        onWorkflowGenerated(data.workflow);
      } else { console.warn('InteractiveVoiceAgent: No workflow data received');
          }
    } catch (err) { console.error('Error processing audio:', err);
      setError('Failed to process audio. Please try again.');
        } finally {
      setProcessing(false);
    }
  };

  useEffect(() => {
    if (audioSrc && audioRef.current) {
      audioRef.current.play().catch(console.error);
    }
  }, [audioSrc]) // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <div className="relative">
      {showPermissionDialog && (
        <MicrophonePermission
          onPermissionGranted={handlePermissionGranted}
          onPermissionDenied={handlePermissionDenied}
        />
      )}
      <div className="fixed bottom-8 right-8 z-[9999]">
        <div
          className={`flex items-center gap-4 ${ isExpanded ? 'bg-slate-800/95 p-4 rounded-2xl shadow-xl backdrop-blur-lg voice-agent-panel' : ''    }`}
        >
          <button
            onClick={ isListening ? stopListening : startListening    }
            disabled={processing}
            className={`relative flex items-center justify-center w-16 h-16 rounded-full shadow-lg transition-colors duration-300 ${ isListening
                ? 'bg-red-500 shadow-red-500/50 voice-agent-listening'
                : processing
                  ? 'bg-amber-500 shadow-amber-500/50'
                  : 'bg-gradient-to-r from-violet-500 to-fuchsia-500 shadow-violet-500/50 voice-agent-button'
                } hover:scale-105 active:scale-95`}
          >
            {isListening && <PulsingCircle />}
            <div className="relative z-10">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-8 w-8 text-white"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z"
                />
              </svg>
            </div>
          </button>

          {isExpanded && (
            <div className="text-white overflow-hidden">
              <div className="w-80">
                {error && (
                  <div className="bg-red-500/20 p-3 rounded-lg mb-3 text-sm">
                    {error}
                  </div>
                )}

                {isListening && (
                  <div className="mb-4">
                    <WaveformAnimation />
                    <div className="text-sm text-center mt-2">Listening...</div>
                  </div>
                )}

                {transcript && (
                  <div className="mb-4">
                    <div className="text-xs text-slate-400 mb-1">Transcript:</div>
                    <div className="bg-white/10 p-3 rounded-lg text-sm">
                      {transcript}
                    </div>
                  </div>
                )}

                {!isListening && transcript && (
                  <button
                    onClick={() => {
                      const audioBlob = new Blob(chunksRef.current, { type: 'audio/wav'     });
                      processAudioData(audioBlob);
                    }}
                    disabled={processing}
                    className={`w-full py-2 px-4 rounded-lg text-sm font-medium transition-colors ${ processing
                        ? 'bg-slate-500 cursor-not-allowed'
                        : 'bg-gradient-to-r from-violet-500 to-fuchsia-500 hover:from-violet-600 hover:to-fuchsia-600'
                        }`}
                  >
                    { processing ? (
                      <div className="flex items-center justify-center gap-2">
                        <div className="w-4 h-4 border-2 border-white/20 border-t-white rounded-full animate-spin" />
                        Processing...
                      </div>
                    ) : (
                      'Create Workflow'
                    )    }
                  </button>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      {audioSrc && (
        <audio ref={audioRef} src={audioSrc} className="hidden" />
      )}
    </div>
  );
};

import { useEffect, useState } from 'react';
import { toast } from 'react-hot-toast';
import { Handle, Position } from 'reactflow';

import PipedreamManagedConnector from './PipedreamManagedConnector';

interface AppAction {
  id: string;
  name: string;
  description: string;
  input_schema: Record<string, any>;
  output_schema: Record<string, any>;
}

interface PipedreamAppNodeProps {
  id: string;
  data: {
    appSlug?: string;
    accountId?: string;
    selectedAction?: string;
    actionConfig?: Record<string, any>;
    onConnect?: (accountId: string) => void;
    onActionSelect?: (actionId: string, config: Record<string, any>) => void;
  };
}

export default function PipedreamAppNode({ id, data }: PipedreamAppNodeProps) {
  const [selectedApp, setSelectedApp] = useState<string | null>(data.appSlug || null);
  const [isConfiguring, setIsConfiguring] = useState(false);
  const [availableApps, setAvailableApps] = useState<Array<{ slug: string, name: string    }>>([]);
  const [availableActions, setAvailableActions] = useState<AppAction[]>([]);
  const [selectedAction, setSelectedAction] = useState<string | null>(data.selectedAction || null);
  const [actionConfig, setActionConfig] = useState<Record<string, any>>(data.actionConfig || {});
  const [isTestingConnection, setIsTestingConnection] = useState(false);

  useEffect(() => {
    // Fetch available apps from Pipedream
    async function fetchApps() {
      try {
        const response = await fetch('/api/pipedream/apps');
        const apps = await response.json();
        setAvailableApps(apps);
      } catch (error) { console.error('Error fetching Pipedream apps:', error);
        toast.error('Failed to load available apps');
          }
    }
    fetchApps();
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    // Fetch available actions for the selected app
    async function fetchActions() {
      if (!selectedApp) return;

      try {
        const response = await fetch(`/api/pipedream/apps/${selectedApp}/actions`);
        const actions = await response.json();
        setAvailableActions(actions);
      } catch (error) { console.error('Error fetching app actions:', error);
        toast.error('Failed to load app actions');
          }
    }

    if (selectedApp) {
      fetchActions();
    }
  }, [selectedApp]) // eslint-disable-line react-hooks/exhaustive-deps

  const handleAppSelect = (appSlug: string) => {
    setSelectedApp(appSlug);
    setSelectedAction(null);
    setActionConfig({});
    setIsConfiguring(true);
  };

  const handleActionSelect = (actionId: string) => {
    setSelectedAction(actionId);
    const action = availableActions.find(a => a.id === actionId);
    if (action) {
      // Initialize config with default values from input schema
      const defaultConfig = Object.entries(action.input_schema).reduce((acc, [key, schema]) => {
        acc[key] = schema.default || '';
        return acc;
      }, {} as Record<string, any>);
      setActionConfig(defaultConfig);
    }
  };

  const handleConfigChange = (field: string, value: any) => {
    setActionConfig(prev => ({ ...prev,
      [field]: value
        }));
  };

  const handleConnectSuccess = async (accountId: string) => {
    if (data.onConnect) {
      data.onConnect(accountId);
    }

    // Save the action configuration
    if (selectedAction && data.onActionSelect) {
      data.onActionSelect(selectedAction, actionConfig);
    }

    setIsConfiguring(false);
    toast.success('App connected and configured successfully!');
  };

  const handleTestConnection = async () => {
    setIsTestingConnection(true);
    try {
      const response = await fetch('/api/pipedream/test-connection', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json',
            },
        body: JSON.stringify({
          appSlug: selectedApp,
          actionId: selectedAction,
          config: actionConfig,
        }),
      });

      if (!response.ok) {
        throw new Error('Connection test failed');
      }

      const result = await response.json();
      toast.success('Connection test successful!');
    } catch (error) { console.error('Connection test failed:', error);
      toast.error('Connection test failed. Please check your configuration.');
        } finally {
      setIsTestingConnection(false);
    }
  };

  const selectedActionDetails = selectedAction 
    ? availableActions.find(a => a.id === selectedAction)
    : null;

  return (
    <div className="px-4 py-2 shadow-md rounded-md bg-white border-2 border-gray-200">
      <Handle type="target" position={Position.Top} className="w-2 h-2" />

      <div className="font-bold text-lg mb-2">Connect App</div>

      {!selectedApp ? (
        <div className="space-y-2">
          <select 
            className="w-full p-2 border rounded"
            onChange={(e) => handleAppSelect(e.target.value)}
            value={selectedApp || ''}
            title="Select an app"
          >
            <option value="">Select an app...</option>
            {availableApps.map(app => (
              <option key={app.slug} value={app.slug}>
                {app.name}
              </option>
            ))}
          </select>
        </div>
      ) : !data.accountId ? (
        <div className="space-y-2">
          <PipedreamManagedConnector
            appSlug={selectedApp}
            onSuccess={handleConnectSuccess}
            onError={(error) => {
              toast.error(`Failed to connect: ${error.message}`);
              setIsConfiguring(false);
            }}
            buttonText="Connect"
            className="w-full"
          />
          <button
            onClick={() => {
              setSelectedApp(null);
              setSelectedAction(null);
              setActionConfig({});
            }}
            className="w-full p-2 text-sm text-gray-600 hover:text-gray-800"
          >
            Cancel
          </button>
        </div>
      ) : (
        <div className="space-y-4">
          <div className="text-sm text-gray-600">
            Connected to: {selectedApp}
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-700">
              Select Action
            </label>
            <select
              className="w-full p-2 border rounded"
              value={selectedAction || ''}
              onChange={(e) => handleActionSelect(e.target.value)}
              title="Select an action"
            >
              <option value="">Choose an action...</option>
              {availableActions.map(action => (
                <option key={action.id} value={action.id}>
                  {action.name}
                </option>
              ))}
            </select>
          </div>

          {selectedActionDetails && (
            <div className="space-y-4">
              <div className="text-sm text-gray-500">
                {selectedActionDetails.description}
              </div>

              {Object.entries(selectedActionDetails.input_schema).map(([field, schema]) => (
                <div key={field} className="space-y-1">
                  <label className="block text-sm font-medium text-gray-700">
                    {schema.title || field}
                  </label>
                  <input
                    type="text"
                    value={actionConfig[field] || ''}
                    onChange={(e) => handleConfigChange(field, e.target.value)}
                    placeholder={schema.description}
                    className="w-full p-2 border rounded text-sm"
                  />
                </div>
              ))}

              <div className="flex space-x-2">
                <button
                  onClick={handleTestConnection}
                  disabled={isTestingConnection}
                  className="flex-1 p-2 text-sm bg-blue-50 text-blue-600 rounded hover:bg-blue-100"
                >
                  { isTestingConnection ? 'Testing...' : 'Test Connection'    }
                </button>
                <button
                  onClick={() => setIsConfiguring(true)}
                  className="flex-1 p-2 text-sm bg-gray-50 text-gray-600 rounded hover:bg-gray-100"
                >
                  Reconfigure
                </button>
              </div>
            </div>
          )}
        </div>
      )}

      <Handle type="source" position={Position.Bottom} className="w-2 h-2" />
    </div>
  );
}

'use client';

import { createFrontendClient } from '@pipedream/sdk/browser';
import { useEffect, useState } from 'react';
import { toast } from 'react-hot-toast';

import { getAppInfo, serverConnectTokenCreate } from '@/lib/pipedream/server';
import { useUserStore } from '@/lib/userStore';


export interface ConnectResult { id: string;
    }

interface PipedreamManagedConnectorProps { appSlug: string;
  onSuccess?: (accountId: string) => void;
  onError?: (error: Error) => void;
  className?: string;
  buttonText?: string;
  oauthAppId?: string;
    }

export default function PipedreamManagedConnector(props: PipedreamManagedConnectorProps) {
  const { appSlug, onSuccess, onError, className, buttonText, oauthAppId } = props;
  const { currentUser } = useUserStore();
  const userId = currentUser?.id || '';

  const [isLoading, setIsLoading] = useState(false);
  const [token, setToken] = useState<string | null>(null);
  const [pdClient, setPdClient] = useState<any>(null);
  const [appInfo, setAppInfo] = useState<any>(null);
  const [customButtonText, setCustomButtonText] = useState(buttonText || '');

  // Initialize the Pipedream frontend client
  useEffect(() => {
    async function loadClient() {
      try {
        const { createFrontendClient } = await import('@pipedream/sdk/browser');
        setPdClient(createFrontendClient());
      } catch (error) { console.error('Error initializing Pipedream client:', error);
        if (onError) onError(new Error('Failed to initialize Pipedream client'));
          }
    }
    loadClient();
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  // Get app info when app slug changes
  useEffect(() => {
    if (!appSlug) return;

    async function fetchAppInfo() {
      try {
        setIsLoading(true);
        const info = await getAppInfo(appSlug);
        setAppInfo(info);

        // Set default button text if not provided
        if (!buttonText) {
          setCustomButtonText(`Connect ${info.display_name || appSlug}`);
        }
      } catch (error) {
        console.error(`Error fetching app info for ${appSlug}:`, error);
        if (onError) onError(new Error(`Failed to get information for ${appSlug}`));
      } finally {
        setIsLoading(false);
      }
    }

    fetchAppInfo();
  }, [appSlug, buttonText]) // eslint-disable-line react-hooks/exhaustive-deps

  // Get a token for the current user
  useEffect(() => {
    if (!userId) return;

    async function createToken() {
      try {
        setIsLoading(true);
        const { token } = await serverConnectTokenCreate({ external_user_id: userId,
            });
        setToken(token);
      } catch (error) { console.error('Error creating token:', error);
        if (onError) onError(new Error('Failed to create authentication token'));
          } finally {
        setIsLoading(false);
      }
    }

    createToken();
  }, [userId, onError]) // eslint-disable-line react-hooks/exhaustive-deps

  const handleConnect = async () => {
    if (!pdClient || !token || !appSlug) { toast.error('Unable to connect: client not initialized or missing token');
      return;
        }

    try {
      setIsLoading(true);

      // Prepare connect config
      const connectConfig: any = {
        app: appSlug,
        token,
        onSuccess: ({ id }: ConnectResult) => {
          setIsLoading(false);
          toast.success(`Successfully connected ${appInfo?.display_name || appSlug}`);
          if (onSuccess) onSuccess(id);
        }
      };

      // Add OAuth app ID if provided
      if (oauthAppId) {
        connectConfig.oauthAppId = oauthAppId;
      }

      // Launch Pipedream Connect flow
      await pdClient.connectAccount(connectConfig);
    } catch (error) {
      setIsLoading(false);
      console.error('Error connecting account:', error);
      toast.error(`Error connecting to ${appInfo?.display_name || appSlug}`);
      if (onError && error instanceof Error) onError(error);
    }
  };

  // Determine button disabled state
  const isDisabled = isLoading || !pdClient || !token || !appSlug;

  return (
    <button
      onClick={handleConnect}
      disabled={isDisabled}
      className={`px-4 py-2 bg-primary text-white rounded-md hover:bg-primary-dark transition-colors disabled:opacity-50 ${className}`}
    >
      { isLoading ? 'Connecting...' : customButtonText    }
    </button>
  );
}

'use client';

import { flip, offset, shift, useFloating, useHover, useInteractions } from '@floating-ui/react';
import { LiveKitRoom, useConnectionState, useRoomContext, createLocalTracks, ConnectionState } from '@livekit/components-react';
import React from 'react';
import { useEffect, useRef, useState } from 'react';

import MicrophonePermission from '@/components/MicrophonePermission';
import { useUserStore } from '@/lib/userStore';


interface VoiceAgentProps { onWorkflowGenerated: (workflow: unknown) => void;
    }

declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

export const VoiceAgent: React.FC<VoiceAgentProps> = ({ onWorkflowGenerated }) => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);
  const [audioSrc, setAudioSrc] = useState<string | null>(null);
  const [showPermissionDialog, setShowPermissionDialog] = useState(false);

  const recognitionRef = useRef<unknown>(null);
  const audioRef = useRef<HTMLAudioElement>(null);

  const { refs, floatingStyles, context } = useFloating({ placement: 'bottom-end',
    middleware: [offset(10), flip(), shift()],
      });

  const hover = useHover(context);
  const { getReferenceProps, getFloatingProps } = useInteractions([hover]);

  // Add LiveKit connection state
  const [livekitConnected, setLivekitConnected] = useState(false);
  const [livekitConnecting, setLivekitConnecting] = useState(false);
  const [livekitRoom, setLivekitRoom] = useState<Room | null>(null);
  const [roomName, setRoomName] = useState('demo-room');
  const [userName, setUserName] = useState('user-' + Math.floor(Math.random() * 10000));
  const [showConnectModal, setShowConnectModal] = useState(false);
  const [livekitError, setLivekitError] = useState<string | null>(null);

  const LIVEKIT_URL = process.env.NEXT_PUBLIC_LIVEKIT_URL || '';

  useEffect(() => {
    // Check if browser supports speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setError('Speech recognition is not supported in this browser. Please use Chrome, Edge, or Safari.');
      console.error('Speech recognition not supported');
      return;
    }

    try {
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onstart = () => {
        console.log('Speech recognition started');
        setIsListening(true);
        setError(null);
      };

      recognitionRef.current.onend = () => {
        console.log('Speech recognition ended');
        setIsListening(false);
      };

      recognitionRef.current.onresult = (event: any) => { console.log('Speech recognition result received', event);
        const transcriptResult = Array.from(event.results)
          .map((result: any) => result[0].transcript)
          .join('');

        setTranscript(transcriptResult);
          };

      recognitionRef.current.onerror = (event: any) => {
        console.error('Speech recognition error:', event);
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };
    } catch (err) { console.error('Error initializing speech recognition:', err);
      setError('Failed to initialize speech recognition. Please try reloading the page.');
        }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  const toggleListening = () => {
    console.log('Toggle listening clicked', { isListening, recognitionRef: !!recognitionRef.current     });

    if (!recognitionRef.current) {
      console.error('Speech recognition not initialized');
      setError('Speech recognition is not initialized. Please try reloading the page.');
      return;
    }

    try {
      if (isListening) {
        console.log('Attempting to stop speech recognition');
        recognitionRef.current.stop();
        setIsListening(false);
      } else {
        console.log('Attempting to start speech recognition');
        setTranscript('');
        setFeedback(null);
        setAudioSrc(null);
        setError(null);

        // Show permission dialog when starting
        setShowPermissionDialog(true);
      }
    } catch (err) {
      console.error('Error toggling speech recognition:', err);
      setError(`Failed to toggle speech recognition: ${ err instanceof Error ? err.message : 'Unknown error'    }`);
      setIsListening(false);
    }
  };

  const handlePermissionGranted = () => {
    setShowPermissionDialog(false);
    if (recognitionRef.current) {
      recognitionRef.current.start();
    }
  };

  const handlePermissionDenied = () => {
    setShowPermissionDialog(false);
    setError('Microphone access is required for voice commands. Please allow microphone access and try again.');
  };

  const processCommand = async () => {
    if (!transcript || processing) return;

    try {
      setProcessing(true);
      setFeedback('Processing your request...');

      console.log('Starting voice command processing with text:', transcript);

      // Use the API route to process the voice command
      const response = await fetch('/api/elevenlabs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json'     },
        body: JSON.stringify({ action: 'processVoiceCommand',
          text: transcript
            })
      });

      console.log('Process command response status:', response.status, response.statusText);

      if (!response.ok) { const errorData = await response.json().catch(() => null);
        console.error('Failed to process voice command:', errorData);
        throw new Error(errorData?.error || 'Failed to process voice command');
          }

      const workflow = await response.json();
      console.log('Received workflow data:', workflow);

      if (workflow) {
        // Generate voice feedback using the API route
        const confirmationText = `I've created a workflow based on your request: "${workflow.name}". ${workflow.description}`;
        console.log('Generating voice feedback:', confirmationText);

        const audioResponse = await fetch('/api/elevenlabs', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json'     },
          body: JSON.stringify({ action: 'textToSpeech',
            text: confirmationText
              })
        });

        console.log('Text-to-speech response status:', audioResponse.status, audioResponse.statusText);

        if (audioResponse.ok) {
          const audioData = await audioResponse.json();
          console.log('Received audio data of length:', audioData?.audio?.length || 0);
          setAudioSrc(`data:audio/mpeg;base64,${audioData.audio}`);
        } else { const errorData = await audioResponse.json().catch(() => null);
          console.error('Failed to generate speech:', errorData);
            }

        setFeedback(confirmationText);
        console.log('Calling onWorkflowGenerated with data');
        onWorkflowGenerated(workflow);
      } else {
        setFeedback('Sorry, I could not generate a workflow from your command.');
        console.warn('No workflow data received');
      }
    } catch (err: any) { console.error('Error processing command:', err);
      setError(err.message || 'Failed to process your command');
      setFeedback('Sorry, there was an error processing your request.');
        } finally {
      setProcessing(false);
    }
  };

  useEffect(() => {
    if (audioSrc && audioRef.current) {
      audioRef.current.play().catch(err => { console.error('Failed to play audio:', err);
          });
    }
  }, [audioSrc]) // eslint-disable-line react-hooks/exhaustive-deps

  // Add cleanup function
  useEffect(() => {
    return () => {
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
        } catch (err) { console.error('Error stopping speech recognition during cleanup:', err);
            }
      }
    };
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  // Connect to LiveKit room
  const connectToLiveKit = async () => {
    setLivekitConnecting(true);
    setLivekitError(null);
    try {
      // Get token from API
      const tokenRes = await fetch(`/api/livekit/token?room=${encodeURIComponent(roomName)}&username=${encodeURIComponent(userName)}`);
      if (!tokenRes.ok) throw new Error('Failed to get LiveKit token');
      const { token } = await tokenRes.json();
      // Create room and connect
      const room = new Room();
      await room.connect(LIVEKIT_URL, token, { autoSubscribe: true,
        // Add more options as needed
          } as RoomConnectOptions);
      setLivekitRoom(room);
      setLivekitConnected(true);
      setShowConnectModal(false);
      // Optionally publish local audio
      await room.localParticipant.enableCameraAndMicrophone();
    } catch (err: any) {
      setLivekitError(err.message || 'Failed to connect to LiveKit');
      setLivekitConnected(false);
    } finally {
      setLivekitConnecting(false);
    }
  };

  const disconnectFromLiveKit = async () => {
    if (livekitRoom) {
      await livekitRoom.disconnect();
      setLivekitRoom(null);
      setLivekitConnected(false);
    }
  };

  // Clean up on unmount
  useEffect(() => {
    return () => {
      if (livekitRoom) {
        livekitRoom.disconnect();
      }
    };
  }, [livekitRoom]) // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <>
      {/* Floating button with connection status */}
      <div className="fixed bottom-8 right-8 z-[9999]">
        <button
          onClick={() => setShowConnectModal(true)}
          className={`flex items-center justify-center w-16 h-16 rounded-full shadow-lg transition-all duration-300 transform hover:scale-105 cursor-pointer
            ${ livekitConnected ? 'bg-green-500 shadow-green-500/50' : livekitConnecting ? 'bg-amber-500 shadow-amber-500/50' : 'bg-gradient-to-r from-[#3CDFFF] to-[#4AFFD4] shadow-[#3CDFFF]/50 hover:from-[#4AFFD4] hover:to-[#3CDFFF]'    }`}
          title={ livekitConnected ? 'Connected to LiveKit' : 'Connect to LiveKit'    }
          type="button"
          aria-label="Connect to LiveKit"
        >
          <span className="sr-only">LiveKit Connection</span>
          {/* Status icon */}
          { livekitConnected ? (
            <svg className="h-8 w-8 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor"><circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" fill="#22c55e" /></svg>
          ) : livekitConnecting ? (
            <svg className="h-8 w-8 text-white animate-spin" fill="none" viewBox="0 0 24 24" stroke="currentColor"><circle cx="12" cy="12" r="10" stroke="#f59e42" strokeWidth="2" fill="none" /></svg>
          ) : (
            <svg className="h-8 w-8 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor"><circle cx="12" cy="12" r="10" stroke="#3CDFFF" strokeWidth="2" fill="none" /></svg>
          )    }
        </button>
        {/* Modal for connecting to LiveKit */}
        {showConnectModal && (
          <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-[10000]">
            <div className="bg-white rounded-lg shadow-2xl p-6 w-96 relative">
              <button className="absolute top-2 right-2 text-slate-400 hover:text-slate-700" onClick={() => setShowConnectModal(false)}>&times;</button>
              <h2 className="text-lg font-bold mb-2">Connect to LiveKit Room</h2>
              <div className="mb-2">
                <label className="block text-sm font-medium mb-1">Room Name</label>
                <input type="text" className="w-full border rounded px-2 py-1" value={roomName} onChange={e => setRoomName(e.target.value)} placeholder="Enter room name" title="Room Name" />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-1">User Name</label>
                <input type="text" className="w-full border rounded px-2 py-1" value={userName} onChange={e => setUserName(e.target.value)} placeholder="Enter your name" title="User Name" />
              </div>
              {livekitError && <div className="text-red-600 text-sm mb-2">{livekitError}</div>}
              <button
                className="w-full py-2 rounded bg-gradient-to-r from-[#3CDFFF] to-[#4AFFD4] text-slate-900 font-semibold hover:from-[#4AFFD4] hover:to-[#3CDFFF] disabled:opacity-50"
                onClick={connectToLiveKit}
                disabled={livekitConnecting}
              >
                { livekitConnecting ? 'Connecting...' : 'Connect'    }
              </button>
            </div>
          </div>
        )}
        {/* Connected room info popover */}
        {livekitConnected && (
          <div className="absolute bottom-20 right-0 bg-white rounded-lg shadow-xl p-4 w-80">
            <div className="mb-2 text-slate-800 font-semibold">Connected to <span className="text-[#3CDFFF]">{roomName}</span></div>
            <div className="mb-2 text-slate-600 text-sm">User: <span className="font-mono">{userName}</span></div>
            <button className="px-3 py-1 rounded bg-red-500 text-white text-xs" onClick={disconnectFromLiveKit}>Disconnect</button>
          </div>
        )}
      </div>
      {/* Existing voice agent UI, only enabled when connected */}
      {livekitConnected && (
        <div className="fixed bottom-8 right-32 z-[9999]">
          <div className="fixed bottom-8 right-8 z-[9999]">
            <button
              ref={refs.setReference}
              {...getReferenceProps()}
              onClick={toggleListening}
              className={`flex items-center justify-center w-16 h-16 rounded-full shadow-lg transition-all duration-300 transform hover:scale-105 cursor-pointer ${ isListening 
                  ? 'bg-red-500 animate-pulse shadow-red-500/50' 
                  : processing 
                    ? 'bg-amber-500 shadow-amber-500/50' 
                    : 'bg-gradient-to-r from-[#3CDFFF] to-[#4AFFD4] shadow-[#3CDFFF]/50 hover:from-[#4AFFD4] hover:to-[#3CDFFF]'
                  }`}
              title={ isListening ? 'Stop Recording' : 'Start Recording'    }
              type="button"
              aria-label="Toggle voice recording"
            >
              <span className="sr-only">Voice Assistant</span>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className={`h-8 w-8 text-white transition-transform duration-300 ${ isListening ? 'scale-110' : ''    }`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                aria-hidden="true"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z"
                />
              </svg>
            </button>

            {context.open && (
              <div
                ref={refs.setFloating}
                style={floatingStyles}
                {...getFloatingProps()}
                className="bg-slate-800 p-4 rounded-lg shadow-xl w-80"
              >
                <div className="text-sm text-white">
                  <h3 className="font-medium mb-2">Voice Assistant</h3>
                  <p className="text-slate-300 text-xs mb-4">
                    Describe the workflow you want to create using natural language
                  </p>

                  {error && (
                    <div className="bg-red-900/50 p-2 rounded text-xs text-red-200 mb-3">
                      {error}
                    </div>
                  )}

                  {transcript && (
                    <div className="mb-4">
                      <div className="text-xs text-slate-400 mb-1">Your command:</div>
                      <div className="bg-slate-700 p-2 rounded text-white text-sm max-h-32 overflow-y-auto">
                        {transcript}
                      </div>
                    </div>
                  )}

                  {feedback && (
                    <div className="mb-4">
                      <div className="text-xs text-slate-400 mb-1">Response:</div>
                      <div className="bg-slate-700 p-2 rounded text-white text-sm max-h-32 overflow-y-auto">
                        {feedback}
                      </div>
                    </div>
                  )}

                  <div className="flex gap-2">
                    <button
                      onClick={toggleListening}
                      className={`px-3 py-1 rounded-full text-xs ${ isListening ? 'bg-red-500 text-white' : 'bg-slate-600 text-white'
                          }`}
                    >
                      { isListening ? 'Stop' : 'Record'    }
                    </button>

                    <button
                      onClick={processCommand}
                      disabled={!transcript || processing || isListening}
                      className={`px-3 py-1 rounded-full text-xs ${ !transcript || processing || isListening
                          ? 'bg-slate-600 text-slate-400 cursor-not-allowed'
                          : 'bg-gradient-to-r from-[#3CDFFF] to-[#4AFFD4] text-slate-800'
                          }`}
                    >
                      { processing ? 'Processing...' : 'Create Workflow'    }
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {audioSrc && (
            <audio ref={audioRef} src={audioSrc} className="hidden" />
          )}
          {showPermissionDialog && (
            <MicrophonePermission
              onPermissionGranted={handlePermissionGranted}
              onPermissionDenied={handlePermissionDenied}
            />
          )}
        </div>
      )}
    </>
  );
};

'use client';

import { 
  ArrowDownIcon, 
  ArrowUpIcon,
  FunnelIcon, 
  MagnifyingGlassIcon,
  Squares2X2Icon,
  TableCellsIcon,
  TagIcon
} from '@heroicons/react/24/outline';
import { useMemo, useState } from 'react';

import { TriggerType, Workflow, WorkflowStatus } from '@/types/workflow';

import WorkflowCard from './WorkflowCard';


interface WorkflowListProps {
  workflows: Workflow[];
  onActivate: (id: string) => void;
  onDeactivate: (id: string) => void;
  onDelete: (id: string) => void;
  onDuplicate: (id: string) => void;
}

type SortField = 'name' | 'updatedAt' | 'createdAt' | 'executionCount' | 'averageExecutionTime';
type SortDirection = 'asc' | 'desc';

export default function WorkflowList({ 
  workflows, 
  onActivate, 
  onDeactivate, 
  onDelete, 
  onDuplicate 
}: WorkflowListProps) {
  // State for search and filters
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<WorkflowStatus | 'all'>('all');
  const [triggerFilter, setTriggerFilter] = useState<TriggerType | 'all'>('all');
  const [tagFilter, setTagFilter] = useState<string>('');
  const [viewMode, setViewMode] = useState<'grid' | 'table'>('grid');

  // State for sorting
  const [sortField, setSortField] = useState<SortField>('updatedAt');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');

  // Helper for toggling sort direction or changing sort field
  const handleSort = (field: SortField) => {
    if (field === sortField) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('desc');
    }
  };

  // Get all unique tags from workflows
  const allTags = useMemo(() => {
    const tagSet = new Set<string>();
    workflows.forEach(workflow => {
      workflow.tags.forEach(tag => tagSet.add(tag));
    });
    return Array.from(tagSet).sort();
  }, [workflows]);

  // Filter and sort workflows
  const filteredAndSortedWorkflows = useMemo(() => {
    // First, filter the workflows
    const filtered = workflows.filter(workflow => {
      // Filter by search query
      const matchesSearch = 
        workflow.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        workflow.description.toLowerCase().includes(searchQuery.toLowerCase());

      // Filter by status
      const matchesStatus = statusFilter === 'all' || workflow.status === statusFilter;

      // Filter by trigger type
      const matchesTrigger = triggerFilter === 'all' || workflow.triggerType === triggerFilter;

      // Filter by tag
      const matchesTag = !tagFilter || workflow.tags.includes(tagFilter);

      return matchesSearch && matchesStatus && matchesTrigger && matchesTag;
    });

    // Then, sort the filtered workflows
    return filtered.sort((a, b) => {
      let comparison = 0;

      switch (sortField) {
        case 'name':
          comparison = a.name.localeCompare(b.name);
          break;
        case 'updatedAt':
          comparison = new Date(a.updatedAt).getTime() - new Date(b.updatedAt).getTime();
          break;
        case 'createdAt':
          comparison = new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime();
          break;
        case 'executionCount':
          comparison = a.executionCount - b.executionCount;
          break;
        case 'averageExecutionTime':
          comparison = a.averageExecutionTime - b.averageExecutionTime;
          break;
        default:
          comparison = 0;
      }

      return sortDirection === 'asc' ? comparison : -comparison;
    });
  }, [
    workflows, 
    searchQuery, 
    statusFilter, 
    triggerFilter, 
    tagFilter, 
    sortField, 
    sortDirection
  ]);

  return (
    <div className="space-y-6">
      {/* Filters and Search Bar */}
      <div className="bg-[#18181B] border border-[#374151] rounded-lg p-4">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <div className="relative">
              <input
                type="text"
                placeholder="Search workflows..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full bg-[#111111] border border-[#374151] rounded-md py-2 pl-10 pr-4 text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
              />
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-[#9CA3AF]" />
            </div>
          </div>

          <div className="flex gap-3 flex-wrap lg:flex-nowrap">
            {/* Status Filter */}
            <select
              aria-label="Filter by status"
              title="Status filter"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value as WorkflowStatus | 'all')}
              className="bg-[#111111] border border-[#374151] rounded-md py-2 px-3 text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
            >
              <option value="all">All Statuses</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
              <option value="draft">Draft</option>
              <option value="error">Error</option>
            </select>

            {/* Trigger Filter */}
            <select
              aria-label="Filter by trigger type"
              title="Trigger type filter"
              value={triggerFilter}
              onChange={(e) => setTriggerFilter(e.target.value as TriggerType | 'all')}
              className="bg-[#111111] border border-[#374151] rounded-md py-2 px-3 text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
            >
              <option value="all">All Triggers</option>
              <option value="schedule">Schedule</option>
              <option value="webhook">Webhook</option>
              <option value="event">Event</option>
              <option value="manual">Manual</option>
              <option value="voice">Voice</option>
            </select>

            {/* Tag Filter */}
            <select
              aria-label="Filter by tag"
              title="Tag filter"
              value={tagFilter}
              onChange={(e) => setTagFilter(e.target.value)}
              className="bg-[#111111] border border-[#374151] rounded-md py-2 px-3 text-[#E5E7EB] focus:outline-none focus:ring-2 focus:ring-[#22D3EE] focus:border-transparent"
            >
              <option value="">All Tags</option>
              {allTags.map(tag => (
                <option key={tag} value={tag}>{tag}</option>
              ))}
            </select>

            {/* View Mode Toggle */}
            <div className="flex bg-[#111111] border border-[#374151] rounded-md overflow-hidden">
              <button
                onClick={() => setViewMode('grid')}
                className={`px-3 py-2 flex items-center ${viewMode === 'grid' ? 'bg-[#22D3EE] text-[#111111]' : 'text-[#E5E7EB]'}`}
                aria-label="Grid view"
                title="Grid view"
              >
                <Squares2X2Icon className="h-5 w-5" />
              </button>
              <button
                onClick={() => setViewMode('table')}
                className={`px-3 py-2 flex items-center ${viewMode === 'table' ? 'bg-[#22D3EE] text-[#111111]' : 'text-[#E5E7EB]'}`}
                aria-label="Table view"
                title="Table view"
              >
                <TableCellsIcon className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>

        {/* Sort Controls */}
        <div className="mt-4 flex items-center text-[#9CA3AF]">
          <FunnelIcon className="h-4 w-4 mr-2" />
          <span className="text-sm mr-3">Sort by:</span>

          <div className="flex flex-wrap gap-2">
            <button
              onClick={() => handleSort('name')}
              className={`flex items-center text-xs px-2 py-1 rounded-md ${sortField === 'name' ? 'bg-[#374151] text-[#E5E7EB]' : 'hover:bg-[#374151]'}`}
            >
              Name
              {sortField === 'name' && (
                sortDirection === 'asc' ? <ArrowUpIcon className="h-3 w-3 ml-1" /> : <ArrowDownIcon className="h-3 w-3 ml-1" />
              )}
            </button>

            <button
              onClick={() => handleSort('updatedAt')}
              className={`flex items-center text-xs px-2 py-1 rounded-md ${sortField === 'updatedAt' ? 'bg-[#374151] text-[#E5E7EB]' : 'hover:bg-[#374151]'}`}
            >
              Last Modified
              {sortField === 'updatedAt' && (
                sortDirection === 'asc' ? <ArrowUpIcon className="h-3 w-3 ml-1" /> : <ArrowDownIcon className="h-3 w-3 ml-1" />
              )}
            </button>

            <button
              onClick={() => handleSort('createdAt')}
              className={`flex items-center text-xs px-2 py-1 rounded-md ${sortField === 'createdAt' ? 'bg-[#374151] text-[#E5E7EB]' : 'hover:bg-[#374151]'}`}
            >
              Created Date
              {sortField === 'createdAt' && (
                sortDirection === 'asc' ? <ArrowUpIcon className="h-3 w-3 ml-1" /> : <ArrowDownIcon className="h-3 w-3 ml-1" />
              )}
            </button>

            <button
              onClick={() => handleSort('executionCount')}
              className={`flex items-center text-xs px-2 py-1 rounded-md ${sortField === 'executionCount' ? 'bg-[#374151] text-[#E5E7EB]' : 'hover:bg-[#374151]'}`}
            >
              Execution Count
              {sortField === 'executionCount' && (
                sortDirection === 'asc' ? <ArrowUpIcon className="h-3 w-3 ml-1" /> : <ArrowDownIcon className="h-3 w-3 ml-1" />
              )}
            </button>

            <button
              onClick={() => handleSort('averageExecutionTime')}
              className={`flex items-center text-xs px-2 py-1 rounded-md ${sortField === 'averageExecutionTime' ? 'bg-[#374151] text-[#E5E7EB]' : 'hover:bg-[#374151]'}`}
            >
              Avg. Execution Time
              {sortField === 'averageExecutionTime' && (
                sortDirection === 'asc' ? <ArrowUpIcon className="h-3 w-3 ml-1" /> : <ArrowDownIcon className="h-3 w-3 ml-1" />
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Results Count */}
      <div className="text-[#9CA3AF] text-sm">
        {filteredAndSortedWorkflows.length} workflow{filteredAndSortedWorkflows.length !== 1 ? 's' : ''} found
      </div>

      {/* Workflows List */}
      {filteredAndSortedWorkflows.length === 0 ? (
        <div className="py-12 text-center bg-[#18181B] border border-[#374151] rounded-lg">
          <div className="text-[#9CA3AF] mb-2">No workflows found</div>
          <div className="text-[#E5E7EB] text-sm">Try adjusting your filters or create a new workflow</div>
        </div>
      ) : viewMode === 'grid' ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredAndSortedWorkflows.map(workflow => (
            <WorkflowCard
              key={workflow.id}
              workflow={workflow}
              onActivate={onActivate}
              onDeactivate={onDeactivate}
              onDelete={onDelete}
              onDuplicate={onDuplicate}
            />
          ))}
        </div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-[#18181B] border border-[#374151] rounded-lg">
            <thead>
              <tr className="border-b border-[#374151]">
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Status
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Name
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Trigger
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Modified
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Executions (24h)
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Avg. Time
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Tags
                </th>
                <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-[#9CA3AF] uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-[#374151]">
              {filteredAndSortedWorkflows.map(workflow => (
                <tr key={workflow.id} className="hover:bg-[#1E293B]">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className={`h-3 w-3 rounded-full ${
                      workflow.status === 'active' ? 'bg-green-500' :
                      workflow.status === 'inactive' ? 'bg-gray-500' :
                      workflow.status === 'draft' ? 'bg-blue-500' :
                      'bg-red-500'
                    }`} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <Link 
                      href={`/workflows/${workflow.id}`} 
                      className="text-[#E5E7EB] hover:text-[#22D3EE] transition-colors"
                    >
                      {workflow.name}
                    </Link>
                    <div className="text-xs text-[#9CA3AF] mt-1 line-clamp-1">{workflow.description}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center text-[#E5E7EB] text-sm capitalize">
                      {workflow.triggerType}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-[#9CA3AF]">
                    {new Date(workflow.updatedAt).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm">
                    <span className="text-green-500">{workflow.successCount}</span> / 
                    <span className="text-red-500">{workflow.failureCount}</span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-[#E5E7EB]">
                    {workflow.averageExecutionTime}ms
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex flex-wrap gap-1">
                      {workflow.tags.slice(0, 2).map(tag => (
                        <span 
                          key={tag} 
                          className="px-2 py-0.5 bg-[#374151] text-xs text-[#E5E7EB] rounded-full"
                        >
                          {tag}
                        </span>
                      ))}
                      {workflow.tags.length > 2 && (
                        <span className="px-2 py-0.5 bg-[#374151] text-xs text-[#E5E7EB] rounded-full">
                          +{workflow.tags.length - 2}
                        </span>
                      )}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm">
                    <div className="flex justify-end space-x-2">
                      <Link
                        href={`/workflows/${workflow.id}/edit`}
                        className="p-1 text-[#9CA3AF] hover:text-[#E5E7EB] transition-colors"
                        aria-label="Edit workflow"
                      >
                        <PencilIcon className="h-5 w-5" />
                      </Link>

                      {workflow.status === 'active' ? (
                        <button
                          onClick={() => onDeactivate(workflow.id)}
                          className="p-1 text-[#9CA3AF] hover:text-[#F87171] transition-colors"
                          aria-label="Deactivate workflow"
                        >
                          <StopIcon className="h-5 w-5" />
                        </button>
                      ) : (
                        <button
                          onClick={() => onActivate(workflow.id)}
                          className="p-1 text-[#9CA3AF] hover:text-[#10B981] transition-colors"
                          aria-label="Activate workflow"
                        >
                          <PlayIcon className="h-5 w-5" />
                        </button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useCallback } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  Connection,
  Controls,
  Edge,
  MiniMap,
  Node,
  NodeTypes,
  useEdgesState,
  useNodesState,
} from 'reactflow';
import 'reactflow/dist/style.css';

// Import custom node types
import { ActionNode } from './nodes/ActionNode';
import { ApiNode } from './nodes/ApiNode';
import { ConditionNode } from './nodes/ConditionNode';
import { DatabaseNode } from './nodes/DatabaseNode';
import { TriggerNode } from './nodes/TriggerNode';

// Define node types
const nodeTypes: NodeTypes = {
  trigger: TriggerNode,
  action: ActionNode,
  condition: ConditionNode,
  api: ApiNode,
  database: DatabaseNode,
};

interface WorkflowNodeCreatorProps {
  initialNodes?: Node[];
  initialEdges?: Edge[];
  onNodesChange?: (nodes: Node[]) => void;
  onEdgesChange?: (edges: Edge[]) => void;
  readOnly?: boolean;
}

export const WorkflowNodeCreator: React.FC<WorkflowNodeCreatorProps> = ({
  initialNodes = [],
  initialEdges = [],
  onNodesChange,
  onEdgesChange,
  readOnly = false,
}) => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  // Handle new connections
  const onConnect = useCallback(
    (params: Connection) => {
      // Validate connection
      const sourceNode = nodes.find((n) => n.id === params.source);
      const targetNode = nodes.find((n) => n.id === params.target);

      if (!sourceNode || !targetNode) {
        console.error('Invalid connection: nodes not found');
        return;
      }

      // Check if connection is valid based on node types
      if (!isValidConnection(sourceNode, targetNode)) {
        console.error('Invalid connection: incompatible node types');
        return;
      }

      // Add the new edge
      const newEdge = {
        ...params,
        animated: true,
        style: { stroke: '#6366f1' },
      };
      setEdges((eds) => addEdge(newEdge, eds));

      // Notify parent component
      if (onEdgesChange) {
        onEdgesChange([...edges, newEdge]);
      }
    },
    [nodes, edges, onEdgesChange]
  );

  // Handle node changes
  const handleNodesChange = useCallback(
    (changes: any) => {
      onNodesChange(changes);
      if (onNodesChange) {
        const updatedNodes = nodes.map((node) => {
          const change = changes.find((c: any) => c.id === node.id);
          return change ? { ...node, ...change } : node;
        });
        onNodesChange(updatedNodes);
      }
    },
    [nodes, onNodesChange]
  );

  // Validate connection between nodes
  const isValidConnection = (sourceNode: Node, targetNode: Node) => {
    // Prevent connecting to trigger nodes
    if (targetNode.type === 'trigger') return false;

    // Prevent connecting from output nodes
    if (sourceNode.type === 'output') return false;

    // Prevent circular connections
    if (hasCircularConnection(sourceNode.id, targetNode.id)) return false;

    return true;
  };

  // Check for circular connections
  const hasCircularConnection = (
    sourceId: string,
    targetId: string
  ): boolean => {
    const visited = new Set<string>();

    const dfs = (currentId: string): boolean => {
      if (currentId === targetId) return true;
      if (visited.has(currentId)) return false;

      visited.add(currentId);

      const outgoingEdges = edges.filter((e) => e.source === currentId);
      return outgoingEdges.some((edge) => dfs(edge.target));
    };

    return dfs(sourceId);
  };

  return (
    <div style={{ width: '100%', height: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={handleNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        fitView
        attributionPosition="bottom-right"
        nodesDraggable={!readOnly}
        nodesConnectable={!readOnly}
        elementsSelectable={!readOnly}
      >
        <Background />
        <Controls />
        <MiniMap />
      </ReactFlow>
    </div>
  );
};

