import { useSession } from 'next-auth/react';
import { useCallback, useState } from 'react';
import { toast } from 'react-hot-toast';

import { PipedreamService } from '@/services/PipedreamService';

interface UsePipedreamOptions {
  appName?: string;
  autoConnect?: boolean;
}

interface ConnectionStatus {
  status: 'connected' | 'disconnected' | 'connecting' | 'error';
  error?: string;
}

export function usePipedream(options: UsePipedreamOptions = {}) {
  const { data: session } = useSession();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<ConnectionStatus>({
    status: 'disconnected',
  });

  const pipedreamService = PipedreamService.getInstance({
    environment: process.env.NODE_ENV,
    apiKey: process.env.PIPEDREAM_API_KEY,
  });

  const checkAuth = useCallback(() => {
    if (!session?.user?.id) {
      throw new Error('User must be logged in');
    }
    if (!options.appName) {
      throw new Error('App name is required');
    }
    return session.user.id;
  }, [session, options.appName]);

  const connectToApp = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);
      setConnectionStatus({ status: 'connecting' });

      const userId = checkAuth();
      const result = await pipedreamService.connectToApp(
        options.appName!,
        userId
      );

      setConnectionStatus({ status: result.status });
      toast.success(`Successfully connected to ${options.appName}`);
    } catch (err) {
      const errorMessage =
        err instanceof Error ? err.message : 'Failed to connect to app';
      setError(errorMessage);
      setConnectionStatus({ status: 'error', error: errorMessage });
      toast.error(errorMessage);
    } finally {
      setIsLoading(false);
    }
  }, [options.appName, checkAuth]);

  const makeRequest = useCallback(
    async <T>(endpoint: string, method: string, data?: any): Promise<T> => {
      try {
        setIsLoading(true);
        setError(null);
        const userId = checkAuth();

        const response = await pipedreamService.makeApiRequest<T>(
          options.appName!,
          userId,
          endpoint,
          method,
          data
        );

        return response;
      } catch (err) {
        const errorMessage =
          err instanceof Error ? err.message : 'API request failed';
        setError(errorMessage);
        toast.error(errorMessage);
        throw err;
      } finally {
        setIsLoading(false);
      }
    },
    [options.appName, checkAuth]
  );

  const disconnect = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);
      const userId = checkAuth();

      const result = await pipedreamService.disconnectApp(
        options.appName!,
        userId
      );
      setConnectionStatus({ status: result.status });
      toast.success(`Successfully disconnected from ${options.appName}`);
    } catch (err) {
      const errorMessage =
        err instanceof Error ? err.message : 'Failed to disconnect from app';
      setError(errorMessage);
      setConnectionStatus({ status: 'error', error: errorMessage });
      toast.error(errorMessage);
    } finally {
      setIsLoading(false);
    }
  }, [options.appName, checkAuth]);

  return {
    isLoading,
    error,
    connectionStatus,
    connectToApp,
    makeRequest,
    disconnect,
  };
}

import { useCallback, useEffect, useState } from 'react';

import { useUserStore } from '@/lib/userStore';
import {
  MCPAppInfo,
  MCPConnection,
  PipedreamMCPConfig,
  PipedreamMCPService,
} from '@/services/PipedreamMCPService';

// Default configuration for the Pipedream MCP service - update with your values
const defaultConfig: PipedreamMCPConfig = {
  clientId: process.env.NEXT_PUBLIC_PIPEDREAM_CLIENT_ID || 'your-client-id',
  clientSecret:
    process.env.NEXT_PUBLIC_PIPEDREAM_CLIENT_SECRET || 'your-client-secret',
  projectId: process.env.NEXT_PUBLIC_PIPEDREAM_PROJECT_ID || 'your-project-id',
  redirectUri:
    process.env.NEXT_PUBLIC_PIPEDREAM_REDIRECT_URI || window?.location?.origin
      ? `${window.location.origin}/api/oauth/callback`
      : 'http://localhost:3000/api/oauth/callback',
  environment:
    process.env.NODE_ENV === 'production' ? 'production' : 'development',
};

export interface UsePipedreamMCPOptions {
  config?: Partial<PipedreamMCPConfig>;
  appId?: string;
  autoConnect?: boolean;
}

export function usePipedreamMCP({
  config = {},
  appId,
  autoConnect = false,
}: UsePipedreamMCPOptions = {}) {
  const { currentUser } = useUserStore();
  const userId = currentUser?.id;

  const [mcpApps, setMcpApps] = useState<MCPAppInfo[]>([]);
  const [userConnections, setUserConnections] = useState<MCPConnection[]>([]);
  const [selectedAppId, setSelectedAppId] = useState<string | null>(
    appId || null
  );
  const [connectionStatus, setConnectionStatus] = useState<
    'connected' | 'disconnected' | 'connecting' | 'error' | null
  >(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isConnecting, setIsConnecting] = useState(false);
  const [isDisconnecting, setIsDisconnecting] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  // Initialize service
  const getMCPService = useCallback(() => {
    // Merge default config with provided config
    const mergedConfig: PipedreamMCPConfig = {
      ...defaultConfig,
      ...config,
      redirectUri:
        config.redirectUri ||
        (window?.location?.origin
          ? `${window.location.origin}/api/oauth/callback`
          : defaultConfig.redirectUri),
    };

    return PipedreamMCPService.getInstance(mergedConfig);
  }, [config]);

  // Fetch available MCP apps
  const fetchAvailableApps = useCallback(
    async (forceRefresh = false) => {
      if (!userId) return [];

      try {
        setIsLoading(true);
        setError(null);

        const service = getMCPService();
        const apps = await service.getAvailableApps(forceRefresh);
        setMcpApps(apps);
        return apps;
      } catch (err) {
        console.error('Error fetching available apps:', err);
        setError(
          err instanceof Error
            ? err
            : new Error('Failed to fetch available apps')
        );
        return [];
      } finally {
        setIsLoading(false);
      }
    },
    [userId, getMCPService]
  );

  // Fetch user connections
  const fetchUserConnections = useCallback(async () => {
    if (!userId) return [];

    try {
      setIsLoading(true);

      const service = getMCPService();
      const connections = service.getUserConnections(userId);
      setUserConnections(connections);
      return connections;
    } catch (err) {
      console.error('Error fetching user connections:', err);
      setError(
        err instanceof Error
          ? err
          : new Error('Failed to fetch user connections')
      );
      return [];
    } finally {
      setIsLoading(false);
    }
  }, [userId, getMCPService]);

  // Update connection status
  const updateConnectionStatus = useCallback(() => {
    if (!userId || !selectedAppId) {
      setConnectionStatus(null);
      return;
    }

    try {
      const service = getMCPService();
      const connection = service.getConnectionStatus(selectedAppId, userId);

      if (connection) {
        setConnectionStatus(connection.status);
        if (connection.error) {
          setError(new Error(connection.error));
        }
      } else {
        setConnectionStatus('disconnected');
      }
    } catch (err) {
      console.error('Error updating connection status:', err);
      setConnectionStatus('error');
      setError(
        err instanceof Error
          ? err
          : new Error('Failed to update connection status')
      );
    }
  }, [userId, selectedAppId, getMCPService]);

  // Initialize data on mount and when dependencies change
  useEffect(() => {
    if (userId) {
      // Fetch available apps and user connections
      fetchAvailableApps();
      fetchUserConnections();
    }
  }, [userId, fetchAvailableApps, fetchUserConnections]);

  // Update connection status when selected app or user changes
  useEffect(() => {
    updateConnectionStatus();
  }, [userId, selectedAppId, updateConnectionStatus]);

  // Initiate OAuth flow to connect an app
  const connectOAuthApp = useCallback(
    async (appIdToConnect: string) => {
      if (!userId) {
        setError(new Error('User not logged in'));
        return null;
      }

      try {
        setSelectedAppId(appIdToConnect);
        setIsConnecting(true);
        setError(null);

        const service = getMCPService();

        // Get the OAuth URL
        const oauthUrl = service.getOAuthURL(appIdToConnect, userId);

        // Update status
        setConnectionStatus('connecting');

        // In a real application, you would:
        // 1. Open a popup window with the OAuth URL
        // 2. Wait for the callback to complete the flow
        // For this demo, we'll just log the URL and simulate a connection
        console.log('OAuth URL:', oauthUrl);

        // Open the OAuth URL in a new window
        window.open(oauthUrl, 'PipedreamOAuth', 'width=800,height=600');

        // In a real app, we would listen for a message from the popup window
        // indicating the OAuth flow completed, and then update our state

        return oauthUrl;
      } catch (err) {
        console.error('Error initiating OAuth flow:', err);
        setConnectionStatus('error');
        setError(
          err instanceof Error
            ? err
            : new Error('Failed to initiate OAuth flow')
        );
        return null;
      } finally {
        setIsConnecting(false);
      }
    },
    [userId, getMCPService]
  );

  // Connect with API Key
  const connectWithApiKey = useCallback(
    async (appIdToConnect: string, apiKey: string) => {
      if (!userId) {
        setError(new Error('User not logged in'));
        return false;
      }

      try {
        setSelectedAppId(appIdToConnect);
        setIsConnecting(true);
        setError(null);
        setConnectionStatus('connecting');

        const service = getMCPService();
        const connection = await service.connectWithApiKey(
          appIdToConnect,
          userId,
          apiKey
        );

        if (connection) {
          setConnectionStatus(connection.status);

          // Update user connections list
          await fetchUserConnections();

          return connection.status === 'connected';
        }

        setConnectionStatus('error');
        setError(new Error('Failed to connect with API key'));
        return false;
      } catch (err) {
        console.error('Error connecting with API key:', err);
        setConnectionStatus('error');
        setError(
          err instanceof Error
            ? err
            : new Error('Failed to connect with API key')
        );
        return false;
      } finally {
        setIsConnecting(false);
      }
    },
    [userId, getMCPService, fetchUserConnections]
  );

  // Disconnect an app
  const disconnectApp = useCallback(
    async (appIdToDisconnect: string) => {
      if (!userId) {
        setError(new Error('User not logged in'));
        return false;
      }

      try {
        setSelectedAppId(appIdToDisconnect);
        setIsDisconnecting(true);
        setError(null);

        const service = getMCPService();
        const success = await service.disconnectApp(appIdToDisconnect, userId);

        if (success) {
          setConnectionStatus('disconnected');

          // Update user connections list
          await fetchUserConnections();

          return true;
        }

        return false;
      } catch (err) {
        console.error('Error disconnecting app:', err);
        setError(
          err instanceof Error ? err : new Error('Failed to disconnect app')
        );
        return false;
      } finally {
        setIsDisconnecting(false);
      }
    },
    [userId, getMCPService, fetchUserConnections]
  );

  // Make an API request to a connected app
  const makeApiRequest = useCallback(
    async <T>(
      appIdToUse: string,
      endpoint: string,
      method: 'GET' | 'POST' | 'PUT' | 'DELETE' = 'GET',
      data?: unknown
    ): Promise<T | null> => {
      if (!userId) {
        setError(new Error('User not logged in'));
        return null;
      }

      try {
        setError(null);

        const service = getMCPService();
        const response = await service.makeApiRequest<T>(
          appIdToUse,
          userId,
          endpoint,
          method,
          data
        );

        return response;
      } catch (err) {
        console.error('Error making API request:', err);
        setError(
          err instanceof Error ? err : new Error('Failed to make API request')
        );
        return null;
      }
    },
    [userId, getMCPService]
  );

  // Get connection status for a specific app
  const getConnectionStatusForApp = useCallback(
    (appIdToCheck: string): MCPConnection | null => {
      if (!userId) return null;

      try {
        const service = getMCPService();
        return service.getConnectionStatus(appIdToCheck, userId);
      } catch (err) {
        console.error('Error getting connection status:', err);
        return null;
      }
    },
    [userId, getMCPService]
  );

  // Get app details by ID
  const getAppById = useCallback(
    async (appIdToGet: string): Promise<MCPAppInfo | null> => {
      try {
        const service = getMCPService();
        return await service.getAppById(appIdToGet);
      } catch (err) {
        console.error('Error getting app details:', err);
        return null;
      }
    },
    [getMCPService]
  );

  return {
    // Data
    apps: mcpApps,
    userConnections,
    connectionStatus,
    selectedAppId,
    isLoading,
    isConnecting,
    isDisconnecting,
    error,

    // Actions
    setSelectedAppId,
    fetchAvailableApps,
    fetchUserConnections,
    connectOAuthApp,
    connectWithApiKey,
    disconnectApp,
    makeApiRequest,
    getConnectionStatusForApp,
    getAppById,
  };
}

import { useCallback, useEffect, useRef, useState } from 'react';

import { VoiceRecognitionConfig, VoiceRecognitionState } from '@/types/voice';

const DEFAULT_CONFIG: VoiceRecognitionConfig = {
  language: 'en-US',
  continuous: true,
  interimResults: true,
  maxAlternatives: 1,
  sampleRate: 16000,
  bufferSize: 4096,
};

export const useVoiceRecognition = (
  config: Partial<VoiceRecognitionConfig> = {}
) => {
  const [state, setState] = useState<VoiceRecognitionState>({
    isListening: false,
    isProcessing: false,
    transcript: '',
    error: null,
    interimTranscript: '',
  });

  const mediaStreamRef = useRef<MediaStream | null>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const processorRef = useRef<ScriptProcessorNode | null>(null);
  const socketRef = useRef<WebSocket | null>(null);

  const finalConfig = { ...DEFAULT_CONFIG, ...config };

  const startListening = useCallback(async () => {
    try {
      setState((prev) => ({ ...prev, isListening: true, error: null }));

      // Initialize audio context and stream
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;

      const audioContext = new AudioContext();
      audioContextRef.current = audioContext;

      const source = audioContext.createMediaStreamSource(stream);
      const processor = audioContext.createScriptProcessor(
        finalConfig.bufferSize,
        1,
        1
      );
      processorRef.current = processor;

      // Connect audio processing
      source.connect(processor);
      processor.connect(audioContext.destination);

      // Initialize WebSocket connection to Deepgram
      const socket = new WebSocket('wss://api.deepgram.com/v1/listen', [
        'token',
        process.env.NEXT_PUBLIC_DEEPGRAM_API_KEY || '',
      ]);

      socketRef.current = socket;

      socket.onopen = () => {
        setState((prev) => ({ ...prev, isProcessing: true }));
      };

      socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.channel) {
          const transcript = data.channel.alternatives[0].transcript;
          if (data.is_final) {
            setState((prev) => ({
              ...prev,
              transcript: prev.transcript + ' ' + transcript,
              interimTranscript: '',
            }));
          } else {
            setState((prev) => ({ ...prev, interimTranscript: transcript }));
          }
        }
      };

      socket.onerror = (error) => {
        setState((prev) => ({
          ...prev,
          error: 'WebSocket error occurred',
          isListening: false,
          isProcessing: false,
        }));
      };

      socket.onclose = () => {
        setState((prev) => ({
          ...prev,
          isListening: false,
          isProcessing: false,
        }));
      };

      // Process audio data
      processor.onaudioprocess = (e) => {
        if (socket.readyState === WebSocket.OPEN) {
          const inputData = e.inputBuffer.getChannelData(0);
          const pcmData = new Int16Array(inputData.length);
          for (let i = 0; i < inputData.length; i++) {
            pcmData[i] = Math.max(-1, Math.min(1, inputData[i])) * 0x7fff;
          }
          socket.send(pcmData.buffer);
        }
      };
    } catch (error) {
      setState((prev) => ({
        ...prev,
        error:
          error instanceof Error
            ? error.message
            : 'Failed to start voice recognition',
        isListening: false,
        isProcessing: false,
      }));
    }
  }, [finalConfig.bufferSize]);

  const stopListening = useCallback(() => {
    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach((track) => track.stop());
    }
    if (audioContextRef.current) {
      audioContextRef.current.close();
    }
    if (processorRef.current) {
      processorRef.current.disconnect();
    }
    if (socketRef.current) {
      socketRef.current.close();
    }

    setState((prev) => ({ ...prev, isListening: false, isProcessing: false }));
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const reset = useCallback(() => {
    stopListening();
    setState({
      isListening: false,
      isProcessing: false,
      transcript: '',
      error: null,
      interimTranscript: '',
    });
  }, [stopListening]); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    return () => {
      stopListening();
    };
  }, [stopListening]); // eslint-disable-line react-hooks/exhaustive-deps

  return {
    ...state,
    startListening,
    stopListening,
    reset,
  };
};

import { PrismaAdapter } from "@next-auth/prisma-adapter";
import bcrypt from "bcryptjs";
import { NextAuthOptions } from "next-auth";
import CredentialsProvider from "next-auth/providers/credentials";
import GoogleProvider from "next-auth/providers/google";
import { z } from "zod";

import { prisma } from "./prisma";


// User schema for validation
const userSchema = z.object({ email: z.string().email(),
  password: z.string().min(8),
  name: z.string().optional(),
    });

const authOptions: NextAuthOptions = {
  adapter: PrismaAdapter(prisma),
  debug: process.env.NODE_ENV === 'development',
  logger: {
    error(code, metadata) {
      console.error('[Auth Error]', { code,
        metadata,
        timestamp: new Date().toISOString()
          });
    },
    warn(code) {
      console.warn('[Auth Warning]', { code,
        timestamp: new Date().toISOString()
          });
    },
    debug(code, metadata) {
      console.log('[Auth Debug]', { code,
        metadata,
        timestamp: new Date().toISOString()
          });
    }
  },
  providers: [
    CredentialsProvider({
      name: 'credentials',
      credentials: {
        email: { label: 'Email', type: 'email' },
        password: { label: 'Password', type: 'password' }
      },
      async authorize(credentials) {
        if (!credentials?.email || !credentials?.password) {
          console.log('[Auth] Missing credentials');
          return null;
        }

        try {
          const user = await prisma.user.findUnique({
            where: { email: credentials.email     }
          });

          if (!user || !user.passwordHash) { console.log('[Auth] User not found or no password:', credentials.email);
            return null;
              }

          const isValid = await bcrypt.compare(credentials.password, user.passwordHash);

          if (!isValid) { console.log('[Auth] Invalid password for user:', credentials.email);
            return null;
              }

          console.log('[Auth] Authentication successful:', { email: user.email, id: user.id     });

          return { id: user.id,
            email: user.email,
            name: user.name
              };
        } catch (error) { console.error('[Auth] Authentication error:', error);
          return null;
            }
      }
    }),
    GoogleProvider({ clientId: process.env.GOOGLE_CLIENT_ID || '',
      clientSecret: process.env.GOOGLE_CLIENT_SECRET || '',
        })
  ],
  pages: {
    signIn: '/login',
    error: '/login',
    signOut: '/'
  },
  session: {
    strategy: 'jwt',
    maxAge: 30 * 24 * 60 * 60, // 30 days
    updateAge: 24 * 60 * 60, // 24 hours
  },
  cookies: {
    sessionToken: {
      name: process.env.NODE_ENV === 'production' 
        ? '__Secure-next-auth.session-token' 
        : 'next-auth.session-token',
      options: {
        httpOnly: true,
        sameSite: 'lax',
        path: '/',
        secure: true, // Always use secure cookies
        domain: process.env.NODE_ENV === 'production' 
          ? '.isyncso.com' 
          : undefined
      }
    }
  },
  callbacks: {
    async jwt({ token, user, account, trigger }) {
      console.log('[Auth] JWT Callback:', { event: trigger,
        tokenId: token?.id, 
        userId: user?.id,
        accountType: account?.type,
        expires: token?.exp,
        issued: token?.iat,
        timestamp: new Date().toISOString()
          });

      if (user) {
        token.id = user.id;
        token.email = user.email;
        token.name = user.name;
      }
      return token;
    },
    async session({ session, token, trigger }) {
      console.log('[Auth] Session Callback:', { event: trigger,
        sessionUserId: session.user?.id, 
        tokenId: token.id,
        sessionExpiry: session.expires,
        tokenExpiry: token.exp,
        timestamp: new Date().toISOString()
          });

      if (token && session.user) {
        session.user.id = token.id as string;
        session.user.email = token.email as string;
        session.user.name = token.name as string;
      }
      return session;
    },
    async redirect({ url, baseUrl }) {
      console.log('[Auth] Redirect Callback:', { 
        url, 
        baseUrl,
        nextAuthUrl: process.env.NEXTAUTH_URL,
        timestamp: new Date().toISOString(),
        urlObject: {
          isRelative: url.startsWith('/'),
          isSameOrigin: url.startsWith(baseUrl),
          pathname: new URL(url.startsWith('/') ? `${baseUrl}${url}` : url).pathname
        }
      });

      // Allow relative URLs
      if (url.startsWith('/')) {
        return `${baseUrl}${url}`;
      }
      // Allow URLs from the same origin
      else if (url.startsWith(baseUrl)) {
        return url;
      }

      return baseUrl;
    }
  }
} as const;

// Export authOptions as default and named export
export { authOptions };
export default authOptions;

export type UserCredentials = z.infer<typeof userSchema>;

// Social login types
export type SocialProvider = 'github' | 'gmail';

export interface SocialLoginCredentials {
  provider: SocialProvider;
  providerToken: string;
}

interface RegisterUserParams {
  email: string;
  password: string;
  name?: string;
}

export async function registerUser({ email, password, name }: RegisterUserParams) {
  // Check if user already exists
  const existingUser = await prisma.user.findUnique({
    where: { email },
  });

  if (existingUser) {
    throw new Error('User with this email already exists');
  }

  // Hash password
  const hashedPassword = await bcrypt.hash(password, 10);

  // Create user
  const user = await prisma.user.create({
    data: {
      email,
      passwordHash: hashedPassword,
      name,
      organizations: {
        create: {
          organization: {
            create: {
              name: `${name || email}'s Organization`,
              plan: 'free',
            }
          },
          role: 'OWNER',
        }
      }
    },
    select: {
      id: true,
      email: true,
      name: true,
    }
  });

  return user;
}

export async function verifyPassword(hashedPassword: string, password: string) {
  return bcrypt.compare(password, hashedPassword);
}

export async function authenticateUser(email: string, password: string) {
  console.log('Starting authentication for email:', email);

  try {
    const user = await prisma.user.findUnique({
      where: { email }
    });

    if (!user || !user.passwordHash) { console.log('User not found:', email);
      throw new Error('Invalid credentials');
        }

    console.log('Found user:', { email: user.email, id: user.id     });

    const isValid = await bcrypt.compare(password, user.passwordHash);
    console.log('Password validation result:', isValid);

    if (!isValid) { console.log('Invalid password for user:', email);
      throw new Error('Invalid credentials');
        }

    // Return user without password
    const { passwordHash, ...userWithoutPassword } = user;
    console.log('Authentication successful:', { email: userWithoutPassword.email, id: userWithoutPassword.id     });
    return userWithoutPassword;
  } catch (error) { console.error('Authentication error:', error);
    throw error;
      }
}

export async function getUserById(id: string) {
  const user = await prisma.user.findUnique({
    where: { id }
  });

  if (!user) {
    return null;
  }

  const { passwordHash, ...userWithoutPassword } = user;
  return userWithoutPassword;
}

export async function authenticateWithSocialProvider(provider: SocialProvider, code: string) {
  // For GitHub, we continue to use the mock implementation for now
  if (provider === 'github') {
    // Use the mock profile for GitHub
    const profile = { id: 'github-123456',
      email: 'github-user@example.com',
      name: 'GitHub User',
        };

    // Check if user already exists
    const users = getUsers();
    let user = null;

    // Look for existing user with this provider ID or email
    for (const existingUser of Array.from(users.values())) {
      if (existingUser.email === profile.email) {
        user = existingUser;
        break;
      }
    }

    // If user doesn't exist, create a new one
    if (!user) {
      const userId = `${provider}-${btoa(profile.email).slice(0, 16)}`;
      user = { id: userId,
        email: profile.email,
        name: profile.name,
        provider: provider,
        providerId: profile.id,
          };

      users.set(profile.email, user);
      saveUsers(users);
    }

    // Return user
    return { id: user.id,
      email: user.email,
      name: user.name,
        };
  }

  // For Gmail (Google), implement proper OAuth flow
  if (provider === 'gmail') {
    // Validate the authorization code
    if (!code || code.startsWith('mock-auth-code')) {
      throw new Error('Invalid authorization code. Please authenticate with Google.');
    }

    try {
      // In a real implementation, exchange the code for tokens with Google's OAuth endpoint
      // This would be a server-side operation for security

      // For now, we'll simulate a failure if the code doesn't look like a real Google auth code
      // This prevents the automatic access that was happening before
      if (!code.includes('google') && !code.includes('oauth')) {
        throw new Error('Invalid Google authentication. Please sign in with your Google account.');
      }

      // Simulate fetching the user profile from Google
      // In a real implementation, we would use the access token to get user info from Google
      const email = code.includes('@') ? code.split('@')[0] + '@gmail.com' : 'user@gmail.com';
      const profile = {
        id: `google-${Date.now()}`,
        email: email,
        name: email.split('@')[0],
      };

      // Check if user already exists
      const users = getUsers();
      let user = null;

      // Look for existing user with this email
      for (const existingUser of Array.from(users.values())) {
        if (existingUser.email === profile.email) {
          user = existingUser;
          break;
        }
      }

      // If user doesn't exist, create a new one
      if (!user) {
        const userId = `${provider}-${btoa(profile.email).slice(0, 16)}`;
        user = { id: userId,
          email: profile.email,
          name: profile.name,
          provider: provider,
          providerId: profile.id,
            };

        users.set(profile.email, user);
        saveUsers(users);
      }

      // Return user
      return { id: user.id,
        email: user.email,
        name: user.name,
          };
    } catch (error) { console.error('Google authentication error:', error);
      throw new Error('Failed to authenticate with Google. Please try again.');
        }
  }

  throw new Error(`Unsupported provider: ${provider}`);
}

