import { createFrontendClient } from '@pipedream/sdk/browser';
import { useCallback, useEffect, useState } from 'react';
import { toast } from 'react-hot-toast';

import { useUserStore } from '../userStore';

interface UsePipedreamConnectOptions {
  onSuccess?: (accountId: string) => void;
  onError?: (error: Error) => void;
}

/**
 * React hook for handling Pipedream Connect OAuth integrations
 */
export function usePipedreamConnect(options: UsePipedreamConnectOptions = {}) {
  const { onSuccess, onError } = options;
  const { currentUser } = useUserStore();
  const userId = currentUser?.id || '';

  const [isInitializing, setIsInitializing] = useState(true);
  const [isConnecting, setIsConnecting] = useState(false);
  const [token, setToken] = useState<string | null>(null);
  const [pdClient, setPdClient] = useState<any>(null);
  const [error, setError] = useState<Error | null>(null);
  const [retryCount, setRetryCount] = useState(0);

  // Initialize the Pipedream client
  useEffect(() => {
    async function initialize() {
      if (pdClient) return; // Already initialized

      try {
        setIsInitializing(true);
        setError(null);
        console.log('Initializing Pipedream client...');

        if (!process.env['NEXT_PUBLIC_PIPEDREAM_CLIENT_ID']) {
          throw new Error('Missing Pipedream client ID in environment variables');
        }

        const client = createFrontendClient();
        console.log('Pipedream client initialized successfully');
        setPdClient(client);
      } catch (err) {
        console.error('Error initializing Pipedream client:', err);
        const error = err instanceof Error ? err : new Error('Failed to initialize Pipedream client');
        setError(error);

        if (retryCount < 2) {
          console.log(`Retrying client initialization (attempt ${retryCount + 1})...`);
          setRetryCount(prev => prev + 1);
          setTimeout(() => initialize(), 1000);
        } else if (onError) {
          onError(error);
        }
      } finally {
        setIsInitializing(false);
      }
    }

    initialize();
  }, [onError, pdClient, retryCount]) // eslint-disable-line react-hooks/exhaustive-deps

  // Function to connect to a service
  const connectService = useCallback(async (appSlug: string, oauthAppId?: string) => {
    if (!pdClient) {
      const errorMsg = 'Pipedream client not initialized';
      console.error(errorMsg);
      toast.error(errorMsg);
      setError(new Error(errorMsg));
      if (onError) onError(new Error(errorMsg));
      return;
    }

    if (!userId) {
      const errorMsg = 'User ID not available. Cannot create connection token.';
      console.error(errorMsg);
      toast.error(errorMsg);
      setError(new Error(errorMsg));
      if (onError) onError(new Error(errorMsg));
      return;
    }

    setIsConnecting(true);
    setError(null);
    let fetchedToken: string | null = null;

    try {
      // Step 1: Fetch the Pipedream connection token on demand
      console.log('Requesting Pipedream connection token for connectService...');
      const tokenResponse = await fetch('/api/pipedream/connect', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json',
            },
        body: JSON.stringify({
          external_user_id: userId,
          user_facing_label: `Connection for user ${userId} with ${appSlug}`
        }),
      });

      if (!tokenResponse.ok) {
        const errorData = await tokenResponse.json();
        throw new Error(errorData.details || errorData.error || 'Failed to create Pipedream token for connection');
      }

      const tokenData = await tokenResponse.json();
      if (!tokenData.token) {
        throw new Error('Token response missing token value');
      }
      fetchedToken = tokenData.token;
      setToken(fetchedToken); // Set token in state
      console.log('Pipedream token created successfully for connectService');

      // Step 2: Proceed with Pipedream account connection
      console.log(`Connecting to ${appSlug} using fetched token...`);

      const connectConfig: any = {
        app: appSlug,
        token: fetchedToken, // Use the just-fetched token
        onSuccess: ({ id }: { id: string     }) => {
          setIsConnecting(false);
          console.log(`Successfully connected to ${appSlug}`, id);
          toast.success(`Successfully connected to ${appSlug}`);
          if (onSuccess) onSuccess(id);
        },
        onError: (connectError: any) => {
          setIsConnecting(false);
          const errorMsg = connectError?.message || `Failed to connect to ${appSlug}`;
          console.error('Connection error:', errorMsg, connectError);
          toast.error(errorMsg);
          const newError = new Error(errorMsg);
          setError(newError);
          if (onError) onError(newError);
        }
      };

      if (oauthAppId) {
        console.log(`Using OAuth app ID: ${oauthAppId}`);
        connectConfig.oauthAppId = oauthAppId;
      } else {
        console.warn('No OAuth app ID provided, using default');
      }

      await pdClient.connectAccount(connectConfig);

    } catch (err) {
      setIsConnecting(false);
      const errorMessage = err instanceof Error ? err.message : `Failed to connect to ${appSlug}`;
      console.error('Overall connection error:', errorMessage, err);

      const newError = err instanceof Error ? err : new Error(errorMessage);
      setError(newError);
      toast.error(errorMessage);
      if (onError) onError(newError);
    }
  }, [pdClient, userId, onSuccess, onError]) // eslint-disable-line react-hooks/exhaustive-deps

  return { connectService,
    isInitializing,
    isConnecting,
    isReady: !!pdClient,
    error
      };
}

'use client';

import { User } from '@prisma/client';
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

// Frontend user type matching our session types
export interface FrontendUser {
  id: string;
  email: string | null;
  name: string | null;
  // Add any additional fields from your session type here
}

export interface ToolConnection {
  connected: boolean;
  config?: { apiKey?: string;
    endpoint?: string;
    [key: string]: any;
      };
}

export interface OrchestratorConnection { status: 'connected' | 'error' | 'connecting';
  config?: any;
    }

export interface UserEnvironment {
  userId: string;
  toolConnections: { [toolName: string]: ToolConnection };
  mcpConnections: { [orchestratorId: string]: OrchestratorConnection };
}

export interface UserState {
  currentUser: FrontendUser | null;
  isLoading: boolean;
  environments: { [userId: string]: UserEnvironment };
  lastCheck: number;
  setCurrentUser: (user: FrontendUser | null) => void;
  setLoading: (loading: boolean) => void;
  updateToolConnection: (userId: string, toolName: string, connection: ToolConnection) => void;
  updateMcpConnection: (userId: string, orchestratorId: string, connection: OrchestratorConnection) => void;
  getEnvironment: (userId: string) => UserEnvironment | null;
  setUser: (user: FrontendUser | null) => void;
  logoutUser: () => void;
  clearUser: () => void;
}

export const useUserStore = create<UserState>()(
  persist(
    (set, get) => ({
      currentUser: null,
      isLoading: true,
      environments: {},
      lastCheck: 0,
      setCurrentUser: (user) => {
        console.log('Setting current user:', user ? user.email : 'null');
        set({ currentUser: user, 
          isLoading: false,
          lastCheck: Date.now()
            });
      },
      setLoading: (loading) => set({ isLoading: loading     }),
      updateToolConnection: (userId, toolName, connection) => 
        set((state) => ({
          environments: {
            ...state.environments,
            [userId]: {
              ...state.environments[userId],
              userId,
              toolConnections: { ...state.environments[userId]?.toolConnections,
                [toolName]: connection
                  }
            }
          }
        })),
      updateMcpConnection: (userId, orchestratorId, connection) =>
        set((state) => ({
          environments: {
            ...state.environments,
            [userId]: {
              ...state.environments[userId],
              userId,
              mcpConnections: { ...state.environments[userId]?.mcpConnections,
                [orchestratorId]: connection
                  }
            }
          }
        })),
      getEnvironment: (userId) => {
        const state = get();
        return state.environments[userId] || null;
      },
      setUser: (user) => {
        console.log('Setting user with setUser method:', user ? user.email : 'null');
        set({ currentUser: user, 
          isLoading: false,
          lastCheck: Date.now()
            });
      },
      logoutUser: () => {
        console.log('Logging out user');
        set({ currentUser: null, isLoading: false     });
      },
      clearUser: () => set({ currentUser: null, isLoading: false     }),
    }),
    {
      name: 'user-store',
      partialize: (state) => ({ currentUser: state.currentUser,
        lastCheck: state.lastCheck
          }),
    }
  )
);

/**
 * This file provides polyfills and utilities for working with WebSockets
 * in an isomorphic environment (works in both browser and Node.js)
 */

// Detect environment
export const isBrowser = typeof window !== 'undefined';
export const isNode = !isBrowser;

// Type definitions to help TypeScript understand WebSocket events
declare global {
  interface Window {
    AudioContext: typeof AudioContext;
    webkitAudioContext: typeof AudioContext;
  }
}

/**
 * Creates a WebSocket client configuration that works in both browser and Node.js
 * @param headers Headers to include in the WebSocket connection
 * @returns Client options object suitable for the current environment
 */
export function createWebSocketOptions(headers: Record<string, string> = {}) {
  const options: any = { headers };

  // Node.js specific options
  if (isNode) {
    // Disable perMessageDeflate which can cause issues in Next.js
    options.perMessageDeflate = false;
  }

  return options;
}

/**
 * Safely converts WebSocket message data to string regardless of its format
 * This is the synchronous version - for Blob data in browser, use websocketDataToStringAsync instead
 * @param data The data from a WebSocket message event
 * @returns String representation of the data
 */
export function websocketDataToString(data: any): string {
  if (typeof data === 'string') {
    return data;
  } else if (data instanceof Buffer) {
    return data.toString();
  } else if (data instanceof ArrayBuffer) {
    return new TextDecoder().decode(data);
  } else if (data instanceof Blob) {
    // For Blob data, we can't convert synchronously, so return a placeholder
    // and recommend using the async version instead
    console.warn('Blob data detected. For proper conversion, use websocketDataToStringAsync instead');
    return '[Blob data - use websocketDataToStringAsync for conversion]';
  }

  // Fallback
  try {
    return String(data);
  } catch {
    return '[Unparseable data]';
  }
}

/**
 * Async version of websocketDataToString that properly handles Blob conversion
 * Use this when you need to wait for Blob data to be converted to text
 * @param data The data from a WebSocket message event
 * @returns Promise resolving to string representation of the data
 */
export async function websocketDataToStringAsync(data: any): Promise<string> {
  if (typeof data === 'string') {
    return data;
  } else if (data instanceof Buffer) {
    return data.toString();
  } else if (data instanceof ArrayBuffer) {
    return new TextDecoder().decode(data);
  } else if (data instanceof Blob) {
    // For browsers - need to convert Blob to text
    return new Promise<string>((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        resolve(reader.result as string);
      };
      reader.onerror = () => {
        reject(new Error('Failed to read Blob data'));
      };
      reader.readAsText(data);
    });
  }

  // Fallback
  try {
    return String(data);
  } catch (error) { console.error('Error converting data to string:', error);
    return '[Unparseable data]';
      }
}

/**
 * Safely converts data to a format that can be sent through WebSocket
 * Works in both browser and Node.js environments
 * @param data The data to convert (object, string, etc.)
 * @returns Data in a format ready to send through WebSocket
 */
export function prepareDataForWebSocket(data: any): string {
  if (typeof data === 'string') {
    return data;
  }

  try {
    return JSON.stringify(data);
  } catch (error) { console.error('Failed to stringify data for WebSocket:', error);
    throw new Error('Could not prepare data for WebSocket transmission');
      }
}

