/**
 * Service for orchestrating AI agents that manage flows through Pipedream
 */
export class AgentOrchestratorService {
  private static instance: AgentOrchestratorService;
  private agents: Map<string, Agent>;
  private pipedreamService: PipedreamService;
  private mcpContextService: MCPContextService;

  private constructor() {
    this.agents = new Map(MOCK_AGENTS.map(agent => [agent.id, agent]));
    this.pipedreamService = new PipedreamService();
    this.mcpContextService = MCPContextService.getInstance();
  }

  /**
   * Get the singleton instance of AgentOrchestratorService
   */
  public static getInstance(): AgentOrchestratorService {
    if (!AgentOrchestratorService.instance) {
      AgentOrchestratorService.instance = new AgentOrchestratorService();
    }
    return AgentOrchestratorService.instance;
  }

  /**
   * Get all registered agents
   */
  public getAgents(): Agent[] {
    return Array.from(this.agents.values());
  }

  /**
   * Get a specific agent by ID
   */
  public getAgent(agentId: string): Agent | undefined {
    return this.agents.get(agentId);
  }

  /**
   * Start an agent to manage its flows
   */
  public async startAgent(agentId: string, userId: string): Promise<Agent | undefined> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      toast.error(`Agent ${agentId} not found`);
      return undefined;
    }

    try {
      // Validate connected apps
      for (const appName of agent.connectedApps) {
        const config = await this.pipedreamService.getIntegrationConfig(appName);
        if (!config) {
          throw new Error(`Missing configuration for ${appName}`);
        }
      }

      // Update agent status
      const updatedAgent = { ...agent, status: 'running' as const, lastRun: new Date() };
      this.agents.set(agentId, updatedAgent);

      // Load the model context for this agent
      const modelContext = this.mcpContextService.getModel(agent.modelId);
      if (!modelContext) {
        throw new Error(`Model context for ${agent.modelId} not found`);
      }

      // Simulate agent running its flows
      for (const flow of agent.flows) {
        if (flow.status === 'active') {
          await this.executeFlow(agent.id, flow.id, userId);
        }
      }

      toast.success(`Agent ${agent.name} started successfully`);
      return updatedAgent;
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Failed to start agent';
      const failedAgent = { ...agent, 
        status: 'error' as const, 
        error: errorMessage 
          };
      this.agents.set(agentId, failedAgent);
      toast.error(`Failed to start agent: ${errorMessage}`);
      return failedAgent;
    }
  }

  /**
   * Stop an agent
   */
  public async stopAgent(agentId: string): Promise<Agent | undefined> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      toast.error(`Agent ${agentId} not found`);
      return undefined;
    }

    try {
      // Update agent status
      const updatedAgent = { ...agent, status: 'idle' as const };
      this.agents.set(agentId, updatedAgent);

      toast.success(`Agent ${agent.name} stopped successfully`);
      return updatedAgent;
    } catch (error) { const errorMessage = error instanceof Error ? error.message : 'Failed to stop agent';
      toast.error(errorMessage);
      return agent;
        }
  }

  /**
   * Execute a specific flow managed by an agent
   */
  public async executeFlow(agentId: string, flowId: string, userId: string): Promise<boolean> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      toast.error(`Agent ${agentId} not found`);
      return false;
    }

    const flow = agent.flows.find(f => f.id === flowId);
    if (!flow) {
      toast.error(`Flow ${flowId} not found for agent ${agent.name}`);
      return false;
    }

    try {
      toast.success(`Executing flow: ${flow.name}`);

      // Simulate flow execution
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Check Pipedream connections for all apps in the flow
      const allApps = [flow.triggerApp, ...flow.actionApps];
      for (const appName of allApps) {
        const connectionStatus = await this.pipedreamService.getConnectionStatus(appName, userId);
        if (connectionStatus.status !== 'connected') {
          throw new Error(`App ${appName} is not connected. Flow execution aborted.`);
        }
      }

      // Simulate API request for the trigger app
      await this.pipedreamService.makeApiRequest(
        flow.triggerApp,
        userId,
        `/workflows/${flowId}/trigger`,
        'POST',
        { flowId, timestamp: new Date().toISOString()     }
      );

      // Update flow stats
      const updatedAgent = { ...agent };
      const flowIndex = updatedAgent.flows.findIndex(f => f.id === flowId);
      if (flowIndex !== -1) {
        const executionTime = Math.random() * 3 + 1; // Random execution time between 1-4 seconds
        const updatedFlow = { ...updatedAgent.flows[flowIndex],
          lastExecuted: new Date(),
          executionCount: updatedAgent.flows[flowIndex].executionCount + 1,
          averageExecutionTime: updatedAgent.flows[flowIndex].averageExecutionTime 
            ? (updatedAgent.flows[flowIndex].averageExecutionTime * 0.9 + executionTime * 0.1)
            : executionTime
            };
        updatedAgent.flows[flowIndex] = updatedFlow;
        updatedAgent.lastRun = new Date();
      }

      this.agents.set(agentId, updatedAgent);

      toast.success(`Flow ${flow.name} executed successfully`);
      return true;
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Failed to execute flow';
      toast.error(errorMessage);

      // Update agent with error
      const updatedAgent = { ...agent, 
        error: errorMessage 
          };
      this.agents.set(agentId, updatedAgent);

      return false;
    }
  }

  /**
   * Create a new agent
   */
  public async createAgent(agent: Omit<Agent, 'id'>): Promise<Agent> {
    try {
      // Generate a unique ID
      const id = `agent-${Date.now()}`;
      const newAgent: Agent = { ...agent, id };

      // Store the agent
      this.agents.set(id, newAgent);

      toast.success(`Agent ${agent.name} created successfully`);
      return newAgent;
    } catch (error) { const errorMessage = error instanceof Error ? error.message : 'Failed to create agent';
      toast.error(errorMessage);
      throw new Error(errorMessage);
        }
  }

  /**
   * Create a new flow for an agent
   */
  public async createFlow(agentId: string, flow: Omit<Flow, 'id'>): Promise<Flow> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      const error = `Agent ${agentId} not found`;
      toast.error(error);
      throw new Error(error);
    }

    try {
      // Generate a unique ID
      const id = `flow-${Date.now()}`;
      const newFlow: Flow = { ...flow, id };

      // Add flow to agent
      const updatedAgent = { ...agent, 
        flows: [...agent.flows, newFlow] 
          };
      this.agents.set(agentId, updatedAgent);

      toast.success(`Flow ${flow.name} created for agent ${agent.name}`);
      return newFlow;
    } catch (error) { const errorMessage = error instanceof Error ? error.message : 'Failed to create flow';
      toast.error(errorMessage);
      throw new Error(errorMessage);
        }
  }

  /**
   * Get the context model for an agent
   */
  public async getAgentContext(agentId: string): Promise<ModelContext | undefined> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      toast.error(`Agent ${agentId} not found`);
      return undefined;
    }

    return this.mcpContextService.getModel(agent.modelId);
  }

  /**
   * Update an agent's model
   */
  public async updateAgentModel(agentId: string, modelId: string): Promise<Agent | undefined> {
    const agent = this.agents.get(agentId);
    if (!agent) {
      toast.error(`Agent ${agentId} not found`);
      return undefined;
    }

    // Check if model exists
    const model = this.mcpContextService.getModel(modelId);
    if (!model) {
      toast.error(`Model ${modelId} not found`);
      return undefined;
    }

    try {
      // Update agent
      const updatedAgent = { ...agent, modelId };
      this.agents.set(agentId, updatedAgent);

      toast.success(`Agent ${agent.name} updated to use ${model.name}`);
      return updatedAgent;
    } catch (error) { const errorMessage = error instanceof Error ? error.message : 'Failed to update agent model';
      toast.error(errorMessage);
      return agent;
        }
  }
}

import { Anthropic, Message } from '@anthropic-ai/sdk';

// Define available model names with proper versioning
export const MODEL_MAP = {
  CLAUDE_3_7_SONNET: 'claude-3-7-sonnet-20250219',
  CLAUDE_3_5_SONNET: 'claude-3-5-sonnet-20241022',
  CLAUDE_3_OPUS: 'claude-3-opus-20240229',
  CLAUDE_3_SONNET: 'claude-3-sonnet-20240229',
  CLAUDE_3_HAIKU: 'claude-3-haiku-20240307',
  CLAUDE_INSTANT: 'claude-instant-1.2'
} as const;

export type ModelName = keyof typeof MODEL_MAP;
export type ThinkingConfig = { type: 'enabled'; budget_tokens?: number };

interface GenerateTextOptions {
  maxTokens?: number;
  temperature?: number;
  thinking?: ThinkingConfig;
}

export interface ClaudeResponse {
  content: string;
  model: ModelName;
  id: string;
}

// Proper Claude client with updated API version headers
export class ClaudeWrapper {
  private client: Anthropic;

  constructor(apiKey: string = process.env.ANTHROPIC_API_KEY || '') {
    console.log('[ClaudeWrapper] Initializing with API key length:', apiKey.length);

    // Ensure API key is properly formatted
    if (apiKey && !apiKey.toLowerCase().startsWith('sk-ant-')) {
      console.error('[ClaudeWrapper] Warning: API key does not start with sk-ant-, this may cause authentication issues');
    }

    // Initialize Anthropic client with correct API version
    this.client = new Anthropic({
      apiKey,
      defaultHeaders: {
        'anthropic-version': '2024-02-19',
        'Content-Type': 'application/json'
      }
    });

    // Log initialization for debugging
    console.log('[ClaudeWrapper] Initialized with API key starting with:', apiKey.substring(0, 10) + '...');
    console.log('[ClaudeWrapper] API key format valid:', apiKey.toLowerCase().startsWith('sk-ant-'));
  }

  // Validate model name
  private validateModel(modelName: ModelName | string): string {
    if (!(modelName in MODEL_MAP)) {
      console.warn(`Warning: Using unrecognized model "${modelName}". Falling back to CLAUDE_3_7_SONNET.`);
      return MODEL_MAP.CLAUDE_3_7_SONNET;
    }
    return MODEL_MAP[modelName as ModelName];
  }

  // Generate text using Claude
  async generateText(
    prompt: string,
    modelName: ModelName = 'CLAUDE_3_7_SONNET',
    options: GenerateTextOptions = {}
  ): Promise<string> {
    try {
      const { maxTokens = 4096, temperature = 0.7, thinking } = options;
      const validatedModel = this.validateModel(modelName);

      // Additional logging for debugging
      console.log(`[ClaudeWrapper] API request with model: ${validatedModel}`);
      console.log(`[ClaudeWrapper] API Key starts with: ${this.client.apiKey ? this.client.apiKey.substring(0, 10) + '...' : 'null'}`);

      // Make API request with proper parameters
      const response = await this.client.messages.create({
        model: validatedModel,
        max_tokens: maxTokens,
        temperature,
        messages: [{ role: 'user', content: prompt }],
        system: "You are a workflow automation assistant helping users create and refine workflows.",
        ...(thinking && { thinking })
      });

      console.log(`[ClaudeWrapper] Response received successfully from model: ${response.model}`);

      // Extract text and thinking content
      let finalText = '';
      for (const content of response.content) {
        if (content.type === 'text') {
          finalText += content.text;
        } else if (content.type === 'thinking') {
          finalText += `\nThinking: ${content.text}\n`;
        }
      }

      return finalText.trim();
    } catch (error) {
      console.error('[ClaudeWrapper] Error generating text:', error);
      throw error;
    }
  }

  // Generate a structured response from Claude
  async generateJson<T>(
    prompt: string,
    modelName: ModelName = 'CLAUDE_3_5_SONNET'
  ): Promise<T> {
    const textResponse = await this.generateText(prompt, modelName);

    try {
      // Extract JSON from the response
      const jsonMatch = textResponse.match(/```json\n([\s\S]*?)\n```/) || 
                       textResponse.match(/```\n([\s\S]*?)\n```/) ||
                       textResponse.match(/{[\s\S]*}/);

      if (jsonMatch) {
        const jsonStr = jsonMatch[1] || jsonMatch[0];
        return JSON.parse(jsonStr) as T;
      }

      throw new Error('Could not extract JSON from Claude response');
    } catch (error) {
      console.error('[ClaudeWrapper] Error parsing JSON:', error);
      throw error;
    }
  }

  async generateWithThinking(
    prompt: string,
    modelName: ModelName = 'CLAUDE_3_7_SONNET',
    budgetTokens = 4000,
    options: Omit<GenerateTextOptions, 'thinking'> = {}
  ): Promise<{ text: string; thinking: string }> {
    try {
      const response = await this.generateText(prompt, modelName, {
        ...options,
        thinking: { type: 'enabled', budget_tokens: budgetTokens }
      });

      const [thinking, ...textParts] = response.split('\nThinking: ');
      return {
        text: thinking,
        thinking: textParts.join('\nThinking: ')
      };
    } catch (error) {
      console.error('[ClaudeWrapper] Error generating with thinking:', error);
      throw error;
    }
  }
}

import { Workflow, WorkflowEdge, WorkflowNode } from '@prisma/client';

import { prisma } from '@/lib/prisma';
import { ConversationMessage, VoiceCommandContext, VoiceCommandResponse } from '@/types/voice';

interface CommandAnalysis {
  intent: string;
  entities: CommandEntities;
}

interface CommandEntities {
  name?: string;
  description?: string;
  type?: string;
  source?: string;
  target?: string;
  config?: Record<string, unknown>;
}

export class ConversationService {
  private context: VoiceCommandContext;
  private readonly maxConversationLength: number;

  constructor(initialContext: Partial<VoiceCommandContext> = {}) {
    this.context = {
      conversationHistory: [],
      pendingQuestions: [],
      ...initialContext,
    };
    this.maxConversationLength = 10;
  }

  async processCommand(command: string): Promise<VoiceCommandResponse> {
    try {
      // Add user message to conversation history
      this.addMessage('user', command);

      // Analyze command for intent and entities
      const { intent, entities } = await this.analyzeCommand(command);

      // Process based on intent
      switch (intent) {
        case 'create_workflow':
          return await this.handleCreateWorkflow(entities);
        case 'add_node':
          return await this.handleAddNode(entities);
        case 'connect_nodes':
          return await this.handleConnectNodes(entities);
        case 'configure_node':
          return await this.handleConfigureNode(entities);
        case 'start_workflow':
          return await this.handleStartWorkflow(entities);
        default:
          return this.handleUnknownIntent();
      }
    } catch (error) {
      console.error('Error processing command:', error);
      return {
        success: false,
        message: 'Sorry, I encountered an error processing your request. Please try again.',
      };
    }
  }

  private async analyzeCommand(command: string): Promise<CommandAnalysis> {
    // TODO: Implement actual NLP analysis using a service like OpenAI or similar
    // This is a simplified version for demonstration
    const lowerCommand = command.toLowerCase();

    if (lowerCommand.includes('create') && lowerCommand.includes('workflow')) {
      return {
        intent: 'create_workflow',
        entities: { name: this.extractEntity(command, 'named', 'workflow') },
      };
    }

    if (lowerCommand.includes('add') && lowerCommand.includes('node')) {
      return {
        intent: 'add_node',
        entities: { type: this.extractEntity(command, 'type', 'node') },
      };
    }

    if (lowerCommand.includes('connect')) {
      return {
        intent: 'connect_nodes',
        entities: {
          source: this.extractEntity(command, 'from', 'node'),
          target: this.extractEntity(command, 'to', 'node'),
        },
      };
    }

    return {
      intent: 'unknown',
      entities: {},
    };
  }

  private extractEntity(command: string, preposition: string, type: string): string | undefined {
    const regex = new RegExp(`${preposition}\\s+([\\w\\s]+)\\s+${type}`, 'i');
    const match = command.match(regex);
    return match ? match[1].trim() : undefined;
  }

  private async handleCreateWorkflow(entities: CommandEntities): Promise<VoiceCommandResponse> {
    if (!entities.name) {
      return {
        success: false,
        message: 'What would you like to name this workflow?',
        followUpQuestion: 'Please provide a name for the workflow.',
      };
    }

    try {
      const workflow = await prisma.workflow.create({
        data: {
          name: entities.name,
          description: entities.description || '',
          organizationId: this.context.currentWorkflow?.organizationId || '',
          createdById: this.context.currentWorkflow?.createdById || '',
        },
      });

      this.context.currentWorkflow = workflow;

      return {
        success: true,
        message: `I've created a new workflow named "${entities.name}". What would you like to add to it?`,
        workflowUpdates: workflow,
      };
    } catch (error) {
      console.error('Error creating workflow:', error);
      return {
        success: false,
        message: "Sorry, I couldn't create the workflow. Please try again.",
      };
    }
  }

  private async handleAddNode(entities: CommandEntities): Promise<VoiceCommandResponse> {
    if (!this.context.currentWorkflow) {
      return {
        success: false,
        message: 'Please create a workflow first before adding nodes.',
      };
    }

    if (!entities.type) {
      return {
        success: false,
        message: 'What type of node would you like to add?',
        followUpQuestion: 'Please specify the node type (e.g., HubSpot, Gmail, Slack).',
      };
    }

    try {
      const node = await prisma.workflowNode.create({
        data: {
          workflowId: this.context.currentWorkflow.id,
          type: entities.type,
          positionX: 0, // Default position
          positionY: 0, // Default position
          config: {},
        },
      });

      this.context.currentNode = node;

      return {
        success: true,
        message: `I've added a ${entities.type} node to your workflow. Would you like to configure it?`,
        nodeUpdates: [node],
      };
    } catch (error) {
      console.error('Error adding node:', error);
      return {
        success: false,
        message: "Sorry, I couldn't add the node. Please try again.",
      };
    }
  }

  private async handleConnectNodes(entities: CommandEntities): Promise<VoiceCommandResponse> {
    if (!this.context.currentWorkflow) {
      return {
        success: false,
        message: 'Please create a workflow first before connecting nodes.',
      };
    }

    if (!entities.source || !entities.target) {
      return {
        success: false,
        message: 'Which nodes would you like to connect?',
        followUpQuestion: 'Please specify the source and target nodes.',
      };
    }

    try {
      const edge = await prisma.workflowEdge.create({
        data: {
          workflowId: this.context.currentWorkflow.id,
          sourceNodeId: entities.source,
          targetNodeId: entities.target,
        },
      });

      this.context.currentEdge = edge;

      return {
        success: true,
        message: "I've connected the nodes. What would you like to do next?",
        edgeUpdates: [edge],
      };
    } catch (error) {
      console.error('Error connecting nodes:', error);
      return {
        success: false,
        message: "Sorry, I couldn't connect the nodes. Please try again.",
      };
    }
  }

  private async handleConfigureNode(entities: CommandEntities): Promise<VoiceCommandResponse> {
    if (!this.context.currentNode) {
      return {
        success: false,
        message: 'Please select a node to configure first.',
      };
    }

    if (!entities.config) {
      return {
        success: false,
        message: 'What configuration would you like to apply?',
        followUpQuestion: 'Please specify the configuration details.',
      };
    }

    try {
      const updatedNode = await prisma.workflowNode.update({
        where: { id: this.context.currentNode.id },
        data: { config: entities.config },
      });

      return {
        success: true,
        message: 'Node configuration updated successfully.',
        nodeUpdates: [updatedNode],
      };
    } catch (error) {
      console.error('Error configuring node:', error);
      return {
        success: false,
        message: "Sorry, I couldn't update the node configuration. Please try again.",
      };
    }
  }

  private async handleStartWorkflow(entities: CommandEntities): Promise<VoiceCommandResponse> {
    if (!this.context.currentWorkflow) {
      return {
        success: false,
        message: 'Please select a workflow to start first.',
      };
    }

    try {
      // Implement workflow start logic here
      return {
        success: true,
        message: 'Workflow started successfully.',
      };
    } catch (error) {
      console.error('Error starting workflow:', error);
      return {
        success: false,
        message: "Sorry, I couldn't start the workflow. Please try again.",
      };
    }
  }

  private handleUnknownIntent(): VoiceCommandResponse {
    return {
      success: false,
      message: "I'm not sure what you want to do. Could you please rephrase that?",
      followUpQuestion: 'You can try commands like "create workflow", "add node", or "connect nodes".',
    };
  }

  private addMessage(role: 'user' | 'assistant' | 'system', content: string): void {
    const message: ConversationMessage = {
      role,
      content,
      timestamp: new Date(),
    };

    this.context.conversationHistory.push(message);

    // Keep conversation history within limit
    if (this.context.conversationHistory.length > this.maxConversationLength) {
      this.context.conversationHistory.shift();
    }
  }

  getContext(): VoiceCommandContext {
    return this.context;
  }
}

import { WebSocket } from 'ws';

import { websocketDataToStringAsync } from '../utils/websocket-polyfill';

interface ConversationalOptions { agentId?: string;
  modelParams?: Record<string, any>;
  voiceParams?: Record<string, any>;
  timeout?: number;
    }

interface WorkflowStep {
  id: string;
  type: string;
  title: string;
  description: string;
  edges: any[];
  parameters: Record<string, any>;
}

interface WorkflowResponse {
  steps: WorkflowStep[];
  parameters: Record<string, any>;
  name: string;
  description: string;
  conversation: Array<{
    role: "user" | "assistant";
    content: string;
    timestamp: number;
  }>;
  isComplete: boolean;
  audioResponses?: Uint8Array[];
}

interface WebSocketMessage {
  type: string;
  data: any;
}

// Add new interface for transcription events
export interface TranscriptionEvent {
  type: 'partial' | 'final';
  text: string;
  timestamp: number;
}

export class ElevenLabsConversationalService {
  private apiKey: string;
  private ws: WebSocket | null = null;
  private audioResponses: Uint8Array[] = [];
  private conversationHistory: Array<{
    role: 'user' | 'assistant';
    content: string;
    timestamp: number;
  }> = [];
  private isInitialized = false;
  private hasReceivedResponse = false;
  private timeoutId: NodeJS.Timeout | undefined = undefined;
  private resolvePromise!: (value: WorkflowResponse | PromiseLike<WorkflowResponse>) => void;
  private rejectPromise!: (reason?: any) => void;
  private audioData = '';
  private fallbackWorkflow: WorkflowResponse | null = null;
  private transcriptionCallback?: (event: TranscriptionEvent) => void;

  constructor() {
    this.apiKey = process.env.ELEVENLABS_API_KEY || '';

    if (!this.apiKey) {
      console.warn('ELEVENLABS_API_KEY environment variable is not set. WebSocket connections will likely fail.');
    } else if (this.apiKey.length < 10) {
      console.warn('ELEVENLABS_API_KEY appears to be invalid (too short). WebSocket connections may fail.');
    }
  }

  async processAudioForWorkflow(
    audioData: string, 
    options: ConversationalOptions = {}, 
    onTranscription?: (event: TranscriptionEvent) => void
  ): Promise<WorkflowResponse> {
    this.audioData = audioData;
    this.fallbackWorkflow = null;
    this.transcriptionCallback = onTranscription;

    return new Promise((resolve, reject) => {
      // Clear previous state
      this.audioResponses = [];
      this.conversationHistory = [];
      this.hasReceivedResponse = false;
      this.isInitialized = false;

      // Use a longer timeout for complex workflows
      const timeout = options.timeout || 60000; // 60 seconds default
      this.timeoutId = setTimeout(() => {
        if (this.ws) {
          console.error('WebSocket connection timed out after', timeout, 'ms');
          this.ws.close();
          if (!this.hasReceivedResponse) {
            reject(new Error(`Audio processing timed out after ${timeout}ms. The ElevenLabs API did not respond in time.`));
          }
        }
      }, timeout);

      this.resolvePromise = resolve;
      this.rejectPromise = reject;

      // Generate WebSocket URL with agent ID
      const wsUrl = `wss://api.elevenlabs.io/v1/convai/conversation?agent_id=${options.agentId || 'default'}`;
      console.log(`Connecting to WebSocket at: ${wsUrl}`);

      // Create WebSocket connection
      this.ws = new WebSocket(wsUrl);

      this.ws.onopen = () => {
        console.log('WebSocket connection established');

        // Send configuration message with API key and other parameters
        const configMessage: WebSocketMessage = {
          type: 'configuration',
          data: {
            api_key: this.apiKey,
            model_params: options.modelParams || {},
            voice_params: options.voiceParams || {}
          }
        };

        if (this.ws && this.ws.readyState === WebSocket.OPEN) {
          console.log('Sending configuration message');
          this.ws.send(JSON.stringify(configMessage));
        } else {
          console.error('WebSocket not open when trying to send configuration');
          reject(new Error('WebSocket connection failed to open properly'));
          this.clearWebSocketTimeout();
        }
      };

      this.ws.onmessage = (event) => {
        try {
          let messageText: string;
          if (typeof event.data === 'string') {
            messageText = event.data;
          } else if (event.data instanceof ArrayBuffer) {
            messageText = new TextDecoder().decode(event.data);
          } else if (event.data instanceof Blob) {
            // Handle blob data asynchronously if needed
            console.log('Received blob data, converting to text');
            const reader = new FileReader();
            reader.onload = (e) => {
              if (e.target && e.target.result) {
                this.handleWebSocketMessage(JSON.parse(e.target.result as string));
              }
            };
            reader.readAsText(event.data);
            return; // Exit early as we'll handle the message in the onload callback
          } else { console.error('Unsupported WebSocket data format:', typeof event.data);
            return;
              }

          const message: WebSocketMessage = JSON.parse(messageText);
          console.log('Received message type:', message.type);

          this.handleWebSocketMessage(message);
        } catch (err) { console.error('Error processing WebSocket message:', err);
            }
      };

      this.ws.onclose = (event) => {
        console.log(`WebSocket closed with code: ${event.code}, reason: ${event.reason}`);

        this.clearWebSocketTimeout();

        // If we haven't received a response yet, provide a fallback
        if (!this.hasReceivedResponse) {
          const closeCodeMessages: Record<number, string> = { 1000: 'Normal closure',
            1001: 'Going away',
            1002: 'Protocol error - The API may have rejected the connection format or headers',
            1003: 'Unsupported data',
            1005: 'No status received',
            1006: 'Abnormal closure',
            1007: 'Invalid frame payload data',
            1008: 'Policy violation/Invalid message - The API rejected a message format',
            1009: 'Message too big',
            1010: 'Mandatory extension',
            1011: 'Internal server error',
            1012: 'Service restart',
            1013: 'Try again later',
            1014: 'Bad gateway',
            1015: 'TLS handshake'
              };

          const reason = closeCodeMessages[event.code] || 'Unknown reason';
          console.error(`WebSocket closed before receiving a complete response. Code: ${event.code} (${reason})`);

          // If WebSocket closed before initialization, reject with an error
          if (!this.isInitialized) {
            reject(new Error(`WebSocket connection closed before initialization with code ${event.code} (${reason}). Check your API key and agent ID.`));
          } else {
            // For code 1008 and other codes after initialization, resolve with a fallback workflow
            console.log(`Using fallback workflow due to WebSocket closure with code ${event.code}`);

            // If we have conversation history, use it to generate a fallback
            if (this.conversationHistory.length > 0) {
              // If user message exists but no assistant message, add a generic one
              if (this.conversationHistory.some(msg => msg.role === 'user') && 
                  !this.conversationHistory.some(msg => msg.role === 'assistant')) {
                this.conversationHistory.push({ role: 'assistant',
                  content: 'I understand your request. Based on this, I can help create a workflow that meets your needs.',
                  timestamp: Date.now()
                    });
              }

              // Use fallback workflow if available, otherwise create a new one
              if (this.fallbackWorkflow) {
                resolve(this.fallbackWorkflow);
              } else {
                resolve(this.generateWorkflowFromConversation(this.conversationHistory));
              }
            } else {
              // No conversation history, add placeholder messages
              this.conversationHistory.push({ role: 'user',
                content: 'Audio message (audio data not stored)',
                timestamp: Date.now()
                  });

              this.conversationHistory.push({ role: 'assistant',
                content: 'I understand you want to create a workflow. Could you please describe what you want to accomplish in more detail?',
                timestamp: Date.now()
                  });

              resolve(this.generateWorkflowFromConversation(this.conversationHistory));
            }
          }
        }
      };

      this.ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        reject(new Error('WebSocket connection error. Please check your network connection and API credentials.'));

        if (this.ws) {
          this.ws.close();
        }

        this.clearWebSocketTimeout();
      };

      // Add the user message to the conversation history immediately
      this.conversationHistory.push({ role: 'user',
        content: 'Audio message (audio data not stored)',
        timestamp: Date.now()
          });
    });
  }

  private parseWorkflowResponse(data: any): WorkflowResponse {
    // Create a new response object with defaults
    const response: WorkflowResponse = {
      steps: data.nodes || [],
      parameters: data.parameters || {},
      name: data.name || 'Generated Workflow',
      description: data.description || 'A workflow generated from conversation',
      conversation: this.conversationHistory.map(msg => ({ role: msg.role,
        content: msg.content,
        timestamp: msg.timestamp
          })),
      isComplete: data.isComplete || false
    };

    // If we have audio responses, add them to the response
    if (this.audioResponses.length > 0) {
      response.audioResponses = this.audioResponses;
    }

    return response;
  }

  private generateWorkflowFromConversation(conversation: Array<{ role: "user" | "assistant"; content: string; timestamp: number     }>): WorkflowResponse {
    // Extract workflow requirements from the conversation
    const requirements = conversation
      .filter(msg => msg.role === 'assistant')
      .map(msg => msg.content)
      .join('\n');

    // Create a basic workflow step
    const workflowStep: WorkflowStep = {
      id: 'workflow-design',
      type: 'workflow',
      title: 'Workflow Design',
      description: 'Design a workflow based on the conversation',
      edges: [],
      parameters: {
        requirements: requirements
      }
    };

    return {
      steps: [workflowStep],
      parameters: {},
      name: 'Generated Workflow',
      description: 'Workflow generated from conversation',
      conversation: conversation,
      isComplete: true,
      audioResponses: this.audioResponses
    };
  }

  private base64ToUint8Array(base64: string): Uint8Array {
    const binaryString = atob(base64);
    const bytes = new Uint8Array(binaryString.length);
    for (let i = 0; i < binaryString.length; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes;
  }

  async convertBrowserAudioToBase64(stream: MediaStream): Promise<string> {
    if (typeof window === 'undefined') {
      throw new Error('This method can only be called in a browser environment');
    }

    // Create an audio context
    const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    const source = audioContext.createMediaStreamSource(stream);
    const destination = audioContext.createMediaStreamDestination();
    source.connect(destination);

    // Convert to LINEAR16 format
    const processor = audioContext.createScriptProcessor(4096, 1, 1);
    const chunks: Float32Array[] = [];

    processor.onaudioprocess = (e) => {
      const input = e.inputBuffer.getChannelData(0);
      chunks.push(new Float32Array(input));
    };

    source.connect(processor);
    processor.connect(audioContext.destination);

    // Wait for audio processing
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Combine chunks and convert to base64
    const combined = new Float32Array(chunks.reduce((acc, chunk) => acc + chunk.length, 0));
    let offset = 0;
    for (const chunk of chunks) {
      combined.set(chunk, offset);
      offset += chunk.length;
    }

    // Convert to 16-bit PCM
    const pcm = new Int16Array(combined.length);
    for (let i = 0; i < combined.length; i++) {
      pcm[i] = Math.max(-32768, Math.min(32767, combined[i] * 32768));
    }

    // Convert to base64
    const buffer = pcm.buffer;
    const uint8Array = new Uint8Array(buffer);
    const base64 = btoa(String.fromCharCode.apply(null, Array.from(uint8Array)));
    return base64;
  }

  private handleWebSocketMessage(message: WebSocketMessage) {
    try {
      console.log('Received message type:', message.type);

      if (message.type === 'conversation_initiation_metadata') {
        console.log('Received metadata, sending audio data');
        this.isInitialized = true;

        // Send audio data after receiving metadata
        if (this.ws && this.ws.readyState === WebSocket.OPEN) {
          const audioMessage: WebSocketMessage = {
            type: 'audio',
            data: {
              audio: this.audioData,
              encoding: 'BASE64'
            }
          };
          console.log('Sending audio data');
          this.ws.send(JSON.stringify(audioMessage));
        }
      } else if (message.type === 'ping') {
        // Respond to ping messages
        if (this.ws && this.ws.readyState === WebSocket.OPEN) {
          console.log('Received ping, sending pong');
          const pongMessage: WebSocketMessage = {
            type: 'pong',
            data: {}
          };
          this.ws.send(JSON.stringify(pongMessage));
        }
      } else if (message.type === 'transcription') {
        // Handle real-time transcription results
        if (this.transcriptionCallback && message.data && message.data.text) {
          const isPartial = message.data.is_partial === true;
          this.transcriptionCallback({ type: isPartial ? 'partial' : 'final',
            text: message.data.text,
            timestamp: Date.now()
              });

          // If this is a final transcription, update the conversation history
          if (!isPartial) {
            // Check if we already have a user message in the history
            const lastUserMessage = this.conversationHistory.findIndex(msg => msg.role === 'user');
            if (lastUserMessage >= 0) {
              // Update existing message
              this.conversationHistory[lastUserMessage].content = message.data.text;
            } else {
              // Add new message
              this.conversationHistory.push({ role: 'user',
                content: message.data.text,
                timestamp: Date.now()
                  });
            }
          }
        }
      } else if (message.type === 'audio') {
        // Check if audio data is valid with proper null/undefined checks
        if (message.data && typeof message.data === 'object' && 'audio' in message.data && message.data.audio) {
          // Store audio response
          const audioChunk = this.base64ToUint8Array(message.data.audio);
          this.audioResponses.push(audioChunk);
        } else {
          console.warn('Received audio message without audio data');
          // Still count it as a valid response to avoid error flow
          this.hasReceivedResponse = true;
        }
      } else if (message.type === 'agent_response') {
        this.hasReceivedResponse = true;

        // Handle possible different formats of agent response
        let agentMessage = "";

        if (message.data && typeof message.data === 'object') {
          // Check each possible structure with explicit null/undefined checks
          if (typeof message.data.message === 'string') {
            agentMessage = message.data.message;
          } else if (message.data.text && typeof message.data.text === 'string') {
            agentMessage = message.data.text;
          } else if (message.data.response) { agentMessage = typeof message.data.response === 'string' 
              ? message.data.response 
              : JSON.stringify(message.data.response);
              } else {
            // If we can't find any text content, convert the whole data to string
            try {
              agentMessage = JSON.stringify(message.data);
            } catch (e) {
              agentMessage = "Received agent response with unparseable data";
            }
          }
        } else {
          agentMessage = "Received agent response with no data";
        }

        // Add the agent message to the conversation history
        this.conversationHistory.push({ role: 'assistant',
          content: agentMessage,
          timestamp: Date.now()
            });

        console.log('Agent response:', agentMessage);

        // If we've received the agent's response, we can consider this successful
        // even if we don't get a workflow response later
        // Create a fallback workflow even if we have no audio responses
        this.fallbackWorkflow = this.generateWorkflowFromConversation(this.conversationHistory);
      } else if (message.type === 'workflow') {
        this.hasReceivedResponse = true;
        console.log('Received workflow response');

        // Ensure message.data exists
        if (!message.data) {
          console.warn('Received workflow message with no data, using fallback');
          if (this.fallbackWorkflow) {
            this.resolvePromise(this.fallbackWorkflow);
          } else {
            this.resolvePromise(this.generateWorkflowFromConversation(this.conversationHistory));
          }
          return;
        }

        // Parse the workflow response
        const response = this.parseWorkflowResponse(message.data);

        // Close WebSocket
        if (this.ws) {
          this.ws.close();
        }

        // Clear timeout
        this.clearWebSocketTimeout();

        // Resolve with the response
        this.resolvePromise(response);
      } else if (message.type === 'error') {
        console.error('Received error from WebSocket:', message.data);
        this.rejectPromise(new Error(`ElevenLabs API error: ${message.data?.message || 'Unknown error'}`));

        if (this.ws) {
          this.ws.close();
        }

        this.clearWebSocketTimeout();
      }
    } catch (err) { console.error('Error processing WebSocket message:', err);
      // If we encounter an error processing a message, don't crash the whole flow
      // Just log it and continue
        }
  }

  private clearWebSocketTimeout() {
    if (this.timeoutId) {
      clearTimeout(this.timeoutId);
      this.timeoutId = undefined;
    }
  }
}

