import { OrganizationMember, Team, TeamMember, User } from '@prisma/client';
import { z } from 'zod';

import { prisma } from '@/lib/prisma';

// Validation schemas
const createTeamSchema = z.object({ name: z.string().min(1).max(100),
  organizationId: z.string().uuid(),
  description: z.string().optional(),
    });

const updateTeamSchema = z.object({ name: z.string().min(1).max(100).optional(),
  description: z.string().optional(),
    });

const addTeamMemberSchema = z.object({ teamId: z.string().uuid(),
  organizationMemberId: z.string().uuid(),
  role: z.enum(['member', 'admin']).default('member'),
    });

export class TeamService {
  /**
   * Create a new team
   */
  async createTeam(data: z.infer<typeof createTeamSchema>, userId: string): Promise<Team> {
    // Validate input
    const validatedData = createTeamSchema.parse(data);

    // Check if user has permission to create teams in the organization
    const orgMember = await prisma.organizationMember.findFirst({
      where: { organizationId: validatedData.organizationId,
        userId: userId,
          },
    });

    if (!orgMember) {
      throw new Error('User is not a member of the organization');
    }

    // Create the team
    const team = await prisma.team.create({
      data: { name: validatedData.name,
        organizationId: validatedData.organizationId,
        description: validatedData.description,
          },
    });

    // Add the creator as an admin of the team
    await prisma.teamMember.create({
      data: { teamId: team.id,
        organizationMemberId: orgMember.id,
        role: 'admin',
          },
    });

    return team;
  }

  /**
   * Update a team
   */
  async updateTeam(teamId: string, data: z.infer<typeof updateTeamSchema>, userId: string): Promise<Team> {
    // Validate input
    const validatedData = updateTeamSchema.parse(data);

    // Check if user has permission to update the team
    const team = await this.getTeamWithMembers(teamId);
    if (!team) {
      throw new Error('Team not found');
    }

    const hasPermission = await this.checkUserTeamPermission(userId, teamId, 'admin');
    if (!hasPermission) {
      throw new Error('User does not have permission to update the team');
    }

    // Update the team
    return prisma.team.update({
      where: { id: teamId     },
      data: validatedData,
    });
  }

  /**
   * Delete a team
   */
  async deleteTeam(teamId: string, userId: string): Promise<void> {
    // Check if user has permission to delete the team
    const team = await this.getTeamWithMembers(teamId);
    if (!team) {
      throw new Error('Team not found');
    }

    const hasPermission = await this.checkUserTeamPermission(userId, teamId, 'admin');
    if (!hasPermission) {
      throw new Error('User does not have permission to delete the team');
    }

    // Delete the team
    await prisma.team.delete({
      where: { id: teamId     },
    });
  }

  /**
   * Get a team by ID
   */
  async getTeam(teamId: string): Promise<Team | null> {
    return prisma.team.findUnique({
      where: { id: teamId     },
    });
  }

  /**
   * Get a team with its members
   */
  async getTeamWithMembers(teamId: string) {
    return prisma.team.findUnique({
      where: { id: teamId     },
      include: {
  members: {
  include: {
  organizationMember: {
  include: { user: true,
                  },
            },
          },
        },
      },
    });
  }

  /**
   * Get all teams in an organization
   */
  async getTeamsByOrganization(organizationId: string) {
    return prisma.team.findMany({
      where: { organizationId },
      include: {
  members: {
  include: {
  organizationMember: {
  include: { user: true,
                  },
            },
          },
        },
      },
    });
  }

  /**
   * Add a member to a team
   */
  async addTeamMember(data: z.infer<typeof addTeamMemberSchema>, userId: string): Promise<TeamMember> {
    // Validate input
    const validatedData = addTeamMemberSchema.parse(data);

    // Check if user has permission to add members to the team
    const hasPermission = await this.checkUserTeamPermission(userId, validatedData.teamId, 'admin');
    if (!hasPermission) {
      throw new Error('User does not have permission to add members to the team');
    }

    // Check if the organization member exists
    const orgMember = await prisma.organizationMember.findUnique({
      where: { id: validatedData.organizationMemberId     },
    });

    if (!orgMember) {
      throw new Error('Organization member not found');
    }

    // Check if the organization member is already in the team
    const existingMember = await prisma.teamMember.findUnique({
      where: { teamId_organizationMemberId: { teamId: validatedData.teamId,
          organizationMemberId: validatedData.organizationMemberId,
            },
      },
    });

    if (existingMember) {
      throw new Error('Member is already in the team');
    }

    // Add the member to the team
    return prisma.teamMember.create({
      data: { teamId: validatedData.teamId,
        organizationMemberId: validatedData.organizationMemberId,
        role: validatedData.role,
          },
    });
  }

  /**
   * Remove a member from a team
   */
  async removeTeamMember(teamId: string, organizationMemberId: string, userId: string): Promise<void> {
    // Check if user has permission to remove members from the team
    const hasPermission = await this.checkUserTeamPermission(userId, teamId, 'admin');
    if (!hasPermission) {
      throw new Error('User does not have permission to remove members from the team');
    }

    // Remove the member from the team
    await prisma.teamMember.delete({
      where: { teamId_organizationMemberId: {
          teamId,
          organizationMemberId,
        },
      },
    });
  }

  /**
   * Update a team member's role
   */
  async updateTeamMemberRole(teamId: string, organizationMemberId: string, role: string, userId: string): Promise<TeamMember> {
    // Check if user has permission to update member roles
    const hasPermission = await this.checkUserTeamPermission(userId, teamId, 'admin');
    if (!hasPermission) {
      throw new Error('User does not have permission to update member roles');
    }

    // Update the member's role
    return prisma.teamMember.update({
      where: { teamId_organizationMemberId: {
          teamId,
          organizationMemberId,
        },
      },
      data: { role },
    });
  }

  /**
   * Check if a user has a specific permission in a team
   */
  async checkUserTeamPermission(userId: string, teamId: string, requiredRole: string): Promise<boolean> {
    // Get the team
    const team = await this.getTeamWithMembers(teamId);
    if (!team) {
      return false;
    }

    // Find the user's organization member record
    const orgMember = await prisma.organizationMember.findFirst({
      where: { organizationId: team.organizationId,
        userId,
          },
    });

    if (!orgMember) {
      return false;
    }

    // Find the user's team member record
    const teamMember = await prisma.teamMember.findUnique({
      where: { teamId_organizationMemberId: { teamId,
          organizationMemberId: orgMember.id,
            },
      },
    });

    if (!teamMember) {
      return false;
    }

    // Check if the user has the required role
    if (requiredRole === 'admin') {
      return teamMember.role === 'admin';
    }

    // For other roles, any team member has permission
    return true;
  }

  /**
   * Get all teams a user is a member of
   */
  async getUserTeams(userId: string) {
    // Find the user's organization memberships
    const orgMembers = await prisma.organizationMember.findMany({
      where: { userId },
    });

    const orgMemberIds = orgMembers.map(member => member.id);

    // Find all teams the user is a member of
    return prisma.team.findMany({
      where: {
  members: {
  some: {
  organizationMemberId: { in: orgMemberIds,
                },
          },
        },
      },
      include: {
  members: {
  include: {
  organizationMember: {
  include: { user: true,
                  },
            },
          },
        },
      },
    });
  }
}

import { ElevenLabsTranscription } from '../types/elevenlabs';

type CommandType = 'create_step' | 'connect_steps' | 'configure_step' | 'delete_step';
type StepType = 'email' | 'qualification' | 'sql' | 'notification' | 'unknown';

interface WorkflowCommand {
  type: CommandType;
  params: WorkflowCommandParams;
}

interface WorkflowCommandParams {
  stepType?: StepType;
  config?: Record<string, unknown>;
  sourceId?: string;
  targetId?: string;
  stepId?: string;
}

type CommandHandler = (text: string) => Promise<WorkflowCommand>;

export class VoiceCommandProcessor {
  private commands: Record<string, CommandHandler> = {
    'create step': this.handleCreateStep.bind(this),
    'connect steps': this.handleConnectSteps.bind(this),
    'configure step': this.handleConfigureStep.bind(this),
    'delete step': this.handleDeleteStep.bind(this)
  };

  async processCommand(transcription: ElevenLabsTranscription): Promise<WorkflowCommand | null> {
    if (!transcription.isFinal) {
      return null;
    }

    const command = this.identifyCommand(transcription.text);
    if (command && this.commands[command]) {
      return this.commands[command](transcription.text);
    }

    return null;
  }

  private identifyCommand(text: string): keyof typeof this.commands | null {
    const lowerText = text.toLowerCase();

    if (lowerText.includes('create') && lowerText.includes('step')) {
      return 'create step';
    }

    if (lowerText.includes('connect') && lowerText.includes('step')) {
      return 'connect steps';
    }

    if (lowerText.includes('configure') && lowerText.includes('step')) {
      return 'configure step';
    }

    if (lowerText.includes('delete') && lowerText.includes('step')) {
      return 'delete step';
    }

    return null;
  }

  private async handleCreateStep(text: string): Promise<WorkflowCommand> {
    // Parse step type and configuration from text
    const stepType = this.extractStepType(text);
    const config = this.extractStepConfig(text);

    return {
      type: 'create_step',
      params: {
        stepType,
        config
      }
    };
  }

  private async handleConnectSteps(text: string): Promise<WorkflowCommand> {
    // Parse source and target step IDs from text
    const [sourceId, targetId] = this.extractStepIds(text);

    return {
      type: 'connect_steps',
      params: {
        sourceId,
        targetId
      }
    };
  }

  private async handleConfigureStep(text: string): Promise<WorkflowCommand> {
    // Parse step ID and new configuration from text
    const stepId = this.extractStepId(text);
    const config = this.extractStepConfig(text);

    return {
      type: 'configure_step',
      params: {
        stepId,
        config
      }
    };
  }

  private async handleDeleteStep(text: string): Promise<WorkflowCommand> {
    // Parse step ID from text
    const stepId = this.extractStepId(text);

    return {
      type: 'delete_step',
      params: {
        stepId
      }
    };
  }

  private extractStepType(text: string): StepType {
    // Implement step type extraction logic
    // Example: "create an email step" -> "email"
    const stepTypes: StepType[] = ['email', 'qualification', 'sql', 'notification'];
    const lowerText = text.toLowerCase();

    for (const type of stepTypes) {
      if (lowerText.includes(type)) {
        return type;
      }
    }

    return 'unknown';
  }

  private extractStepConfig(text: string): Record<string, unknown> {
    // Implement configuration extraction logic
    // This would parse specific configuration parameters from the text
    return {};
  }

  private extractStepIds(text: string): [string, string] {
    // Implement step ID extraction logic
    // This would parse step IDs from the text
    return ['', ''];
  }

  private extractStepId(text: string): string {
    // Implement single step ID extraction logic
    return '';
  }
}

import { ElevenLabsTranscription } from '../types/elevenlabs';

interface WorkflowStep {
  id: string;
  type: 'email' | 'qualification' | 'sql' | 'notification';
  config: Record<string, any>;
  nextSteps: string[];
}

interface Workflow {
  id: string;
  name: string;
  description: string;
  steps: WorkflowStep[];
}

export class WorkflowBuilder {
  private workflows: Map<string, Workflow> = new Map();
  private currentWorkflow: Workflow | null = null;

  async generateFromVoice(transcription: ElevenLabsTranscription): Promise<Workflow | null> {
    if (!transcription.isFinal) {
      return null;
    }

    // Parse the transcription to identify workflow components
    const components = this.parseWorkflowIntent(transcription.text);

    // Generate a new workflow or update existing one
    if (!this.currentWorkflow) {
      this.currentWorkflow = this.createNewWorkflow(components);
    } else {
      this.updateWorkflow(components);
    }

    return this.currentWorkflow;
  }

  private parseWorkflowIntent(text: string): {
    name?: string;
    description?: string;
    steps: Array<{
      type: string;
      config: Record<string, any>;
    }>;
  } {
    // Implement intent parsing logic
    // This would use NLP or pattern matching to extract workflow components
    return { steps: []
    };
  }

  private createNewWorkflow(components: any): Workflow {
    const workflow: Workflow = {
      id: this.generateId(),
      name: components.name || 'New Workflow',
      description: components.description || '',
      steps: components.steps.map((step: any) => ({ id: this.generateId(),
        type: step.type,
        config: step.config,
        nextSteps: []
          }))
    };

    this.workflows.set(workflow.id, workflow);
    return workflow;
  }

  private updateWorkflow(components: any): void {
    if (!this.currentWorkflow) {
      return;
    }

    // Update workflow properties
    if (components.name) {
      this.currentWorkflow.name = components.name;
    }
    if (components.description) {
      this.currentWorkflow.description = components.description;
    }

    // Add new steps
    for (const step of components.steps) {
      this.currentWorkflow.steps.push({ id: this.generateId(),
        type: step.type,
        config: step.config,
        nextSteps: []
          });
    }

    // Update the workflow in the map
    this.workflows.set(this.currentWorkflow.id, this.currentWorkflow);
  }

  private generateId(): string {
    return Math.random().toString(36).substring(2, 15);
  }

  getWorkflow(id: string): Workflow | undefined {
    return this.workflows.get(id);
  }

  getAllWorkflows(): Workflow[] {
    return Array.from(this.workflows.values());
  }

  deleteWorkflow(id: string): void {
    this.workflows.delete(id);
    if (this.currentWorkflow?.id === id) {
      this.currentWorkflow = null;
    }
  }

  setCurrentWorkflow(id: string): void {
    const workflow = this.workflows.get(id);
    if (workflow) {
      this.currentWorkflow = workflow;
    }
  }
}

