import { ClayService } from '../integrations/ClayService';
import { HubspotService } from '../integrations/HubspotService';
import { mcpService } from '../mcp/MCPService';
import { Logger } from '../utils/Logger';
import { RateLimiter } from '../utils/RateLimiter';

import { ClaudeService } from './ClaudeService';

interface AoraConfig { claudeApiKey: string;
  clayApiKey?: string;
  hubspotApiKey?: string;
  maxConcurrentConnections?: number;
  retryAttempts?: number;
    }

interface ConnectionStatus {
  id: string;
  status: 'connected' | 'disconnected' | 'error';
  lastCheck: Date;
  errorMessage?: string;
  recommendations?: string[];
  rateLimitRemaining?: number;
  resetTime?: Date;
}

export class AoraAgent {
  private config: AoraConfig;
  private connectionStatuses: Map<string, ConnectionStatus>;
  private isRunning: boolean;
  private checkInterval: NodeJS.Timeout | null;
  private claude: ClaudeService;
  private clay: ClayService | null = null;
  private hubspot: HubspotService | null = null;
  private rateLimiter: RateLimiter;
  private logger: Logger;

  constructor(config: AoraConfig) {
    if (!config.claudeApiKey) {
      throw new Error('Claude API key is required for AORA agent');
    }

    this.config = { maxConcurrentConnections: 5,
      retryAttempts: 3,
      ...config
        };

    this.claude = new ClaudeService({ apiKey: config.claudeApiKey,
      model: 'claude-3-sonnet-20240229'
        });

    if (config.clayApiKey) {
      this.clay = new ClayService({ apiKey: config.clayApiKey     });
    }

    if (config.hubspotApiKey) {
      this.hubspot = new HubspotService({ apiKey: config.hubspotApiKey     });
    }

    this.rateLimiter = new RateLimiter({ maxRequests: 50,
      windowMs: 60000  // 1 minute
        });

    this.logger = Logger.getInstance();
    this.connectionStatuses = new Map();
    this.isRunning = false;
    this.checkInterval = null;
  }

  async start() {
    if (this.isRunning) return;
    this.isRunning = true;

    this.logger.info('Starting AORA agent', { config: this.config     });

    this.checkInterval = setInterval(() => {
      this.monitorConnections();
    }, 120000); // Check every 2 minutes

    await this.monitorConnections();
  }

  async stop() {
    this.isRunning = false;
    if (this.checkInterval) {
      clearInterval(this.checkInterval);
      this.checkInterval = null;
    }
    this.logger.info('AORA agent stopped');
  }

  private async monitorConnections() {
    try {
      const connections = await mcpService.getActiveConnections();
      this.logger.debug('Monitoring connections', { count: connections.length     });

      for (const connection of connections) {
        try {
          await this.rateLimiter.withRateLimit(`monitor-${connection.id}`, async () => {
            // Use Claude to analyze connection health
            const analysis = await this.claude.analyzeConnection(connection);

            // Get service-specific status
            const serviceStatus = await this.getServiceStatus(connection.type);

            // Update connection status with combined insights
            this.connectionStatuses.set(connection.id, { id: connection.id,
              status: analysis.isHealthy ? 'connected' : 'error',
              lastCheck: new Date(),
              errorMessage: analysis.error,
              recommendations: analysis.recommendations,
              rateLimitRemaining: serviceStatus?.rateLimitRemaining,
              resetTime: serviceStatus?.resetTime
                });

            // If there's an error, use Claude to suggest fixes
            if (!analysis.isHealthy) {
              await this.handleConnectionError(connection.id, analysis.error);
            }
          });
        } catch (error: any) {
          this.logger.error(`Error monitoring connection ${connection.id}`, error as Error);
          this.connectionStatuses.set(connection.id, { id: connection.id,
            status: 'error',
            lastCheck: new Date(),
            errorMessage: error instanceof Error ? error.message : 'Unknown error'
              });
        }
      }
    } catch (error: any) {
      this.logger.error('Error in connection monitoring', error as Error);
    }
  }

  private async getServiceStatus(type: string) {
    try {
      switch (type) {
        case 'clay':
          if (this.clay) {
            return await this.clay.getApiStatus();
          }
          break;
        case 'hubspot':
          if (this.hubspot) {
            return await this.hubspot.getApiStatus();
          }
          break;
      }
    } catch (error: any) {
      this.logger.error(`Error getting service status for ${type}`, error as Error);
    }
    return null;
  }

  private async handleConnectionError(connectionId: string, error: string | undefined) {
    try {
      const connection = await mcpService.getConnection(connectionId);

      // Get fix suggestions from Claude
      const suggestions = await this.claude.suggestFixes(connection.type, {
        error,
        connection
      });

      // Apply the first suggested fix if available
      if (suggestions.length > 0) {
        const firstSuggestion = suggestions[0];
        this.logger.info(`Applying fix for ${connectionId}`, { suggestion: firstSuggestion     });

        await this.rateLimiter.withRateLimit(`fix-${connectionId}`, async () => {
          if (firstSuggestion.includes('refresh')) {
            await mcpService.refreshConnection(connectionId);
          } else if (firstSuggestion.includes('restart')) {
            await mcpService.restartConnection(connectionId);
          }
        });
      }

      // Store the suggestions for UI display
      const status = this.connectionStatuses.get(connectionId);
      if (status) {
        this.connectionStatuses.set(connectionId, { ...status,
          recommendations: suggestions
            });
      }

    } catch (error: any) {
      this.logger.error(`Error handling connection error for ${connectionId}`, error as Error);
    }
  }

  private async validateClayConnection(connection: any) {
    if (!this.clay) return false;

    try {
      return await this.rateLimiter.withRateLimit('clay-validate', async () => {
        const analysis = await this.claude.analyzeConnection({ type: 'clay',
          config: connection.config,
          status: await this.clay!.validateConnection()
            });
        return analysis.isHealthy;
      });
    } catch (error: any) {
      this.logger.error('Clay validation error', error as Error);
      return false;
    }
  }

  private async validateHubspotConnection(connection: any) {
    if (!this.hubspot) return false;

    try {
      return await this.rateLimiter.withRateLimit('hubspot-validate', async () => {
        const analysis = await this.claude.analyzeConnection({ type: 'hubspot',
          config: connection.config,
          status: await this.hubspot!.validateConnection()
            });
        return analysis.isHealthy;
      });
    } catch (error: any) {
      this.logger.error('HubSpot validation error', error as Error);
      return false;
    }
  }

  // Public methods
  getConnectionStatus(connectionId: string): ConnectionStatus | undefined {
    return this.connectionStatuses.get(connectionId);
  }

  getAllConnectionStatuses(): ConnectionStatus[] {
    return Array.from(this.connectionStatuses.values());
  }

  isAgentRunning(): boolean {
    return this.isRunning;
  }

  getRateLimitInfo(key: string) {
    return { remaining: this.rateLimiter.getRemainingRequests(key),
      resetTime: this.rateLimiter.getResetTime(key)
        };
  }

  getRecentLogs(filter?: Parameters<Logger['getHistory']>[0]) {
    return this.logger.getHistory(filter);
  }
}

import axios from 'axios';

import { ClaudeWrapper, MODEL_MAP } from '../ClaudeWrapper';

interface ClaudeConfig {
  apiKey: string;
  model: string;
  maxTokens?: number;
  temperature?: number;
}

type MessageRole = 'user' | 'assistant';

interface ClaudeMessage {
  role: MessageRole;
  content: string;
}

interface ClaudeResponse {
  id: string;
  content: string;
  stop_reason: string | null;
  model: string;
}

interface Position {
  x: number;
  y: number;
}

interface NodeData {
  label: string;
  description: string;
  config: Record<string, unknown>;
}

interface WorkflowNode {
  id: string;
  type: 'trigger' | 'action' | 'conditional' | 'wait';
  position: Position;
  data: NodeData;
}

interface WorkflowEdge {
  id: string;
  source: string;
  target: string;
  label: string;
}

interface GeneratedWorkflow {
  name: string;
  description: string;
  nodes: WorkflowNode[];
  edges: WorkflowEdge[];
}

interface WorkflowGenerationResult {
  workflow: GeneratedWorkflow;
  message: string;
  suggestions: string[];
}

interface ConnectionAnalysis {
  isHealthy: boolean;
  recommendations?: string[];
  error?: string;
}

interface AIModelConfig {
  model: string;
  maxTokens: number;
  temperature: number;
  topP?: number;
  presencePenalty?: number;
  frequencyPenalty?: number;
}

interface AIRequestMetadata {
  requestId: string;
  timestamp: Date;
  model: string;
  prompt: string;
}

interface AIResponseMetadata {
  requestId: string;
  timestamp: Date;
  model: string;
  processingTime: number;
  tokenCount?: number;
  finishReason?: string;
}

interface AIResponse<T = unknown> {
  data: T;
  metadata: AIResponseMetadata;
  error?: {
    code: string;
    message: string;
    details?: Record<string, unknown>;
  };
}

interface WorkflowValidation {
  isValid: boolean;
  errors: Array<{
    code: string;
    message: string;
    nodeId?: string;
    edgeId?: string;
  }>;
  warnings: Array<{
    code: string;
    message: string;
    nodeId?: string;
    edgeId?: string;
  }>;
}

export class ClaudeService {
  private config: Required<AIModelConfig>;
  private claudeWrapper: ClaudeWrapper;
  private requestHistory: Map<string, AIResponse[]> = new Map();

  constructor(config: ClaudeConfig) {
    this.config = {
      model: config.model,
      maxTokens: config.maxTokens || 4096,
      temperature: config.temperature || 0.7,
      topP: 0.95,
      presencePenalty: 0,
      frequencyPenalty: 0
    };
    this.claudeWrapper = new ClaudeWrapper(config.apiKey);
  }

  private createRequestMetadata(prompt: string): AIRequestMetadata {
    return {
      requestId: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      model: this.config.model,
      prompt
    };
  }

  private trackRequest<T>(response: AIResponse<T>): void {
    const history = this.requestHistory.get(response.metadata.requestId) || [];
    history.push(response);

    // Keep only last 50 requests
    if (history.length > 50) {
      history.shift();
    }

    this.requestHistory.set(response.metadata.requestId, history);
  }

  async chat(messages: ClaudeMessage[]): Promise<AIResponse<ClaudeResponse>> {
    const metadata = this.createRequestMetadata(messages[messages.length - 1].content);
    const startTime = Date.now();

    try {
      console.log(`[ClaudeService] Making API call with model: ${this.config.model}`);

      let response: ClaudeResponse;

      // For a single message, use the wrapper directly
      if (messages.length === 1 && messages[0].role === 'user') {
        const text = await this.claudeWrapper.generateText(
          messages[0].content,
          this.config.model,
          this.config.maxTokens,
          this.config.temperature
        );

        response = {
          id: metadata.requestId,
          content: text,
          stop_reason: 'end_turn',
          model: this.config.model
        };
      } else {
        // For conversation threads, use axios
        const axiosResponse = await axios.post<ClaudeResponse>(
          'https://api.anthropic.com/v1/messages',
          {
            model: this.config.model,
            messages,
            max_tokens: this.config.maxTokens,
            temperature: this.config.temperature
          },
          {
            headers: {
              'Content-Type': 'application/json',
              'x-api-key': this.claudeWrapper.apiKey,
              'anthropic-version': '2023-06-01'
            }
          }
        );
        response = axiosResponse.data;
      }

      const aiResponse: AIResponse<ClaudeResponse> = {
        data: response,
        metadata: {
          requestId: metadata.requestId,
          timestamp: new Date(),
          model: this.config.model,
          processingTime: Date.now() - startTime,
          finishReason: response.stop_reason
        }
      };

      this.trackRequest(aiResponse);
      return aiResponse;
    } catch (error) {
      console.error('Claude API error:', error);
      return {
        data: null as unknown as ClaudeResponse,
        metadata: {
          requestId: metadata.requestId,
          timestamp: new Date(),
          model: this.config.model,
          processingTime: Date.now() - startTime
        },
        error: {
          code: 'API_ERROR',
          message: error instanceof Error ? error.message : 'Unknown error in Claude API call',
          details: { error }
        }
      };
    }
  }

  async generateWorkflow(
    description: string,
    existingWorkflow?: GeneratedWorkflow
  ): Promise<AIResponse<WorkflowGenerationResult>> {
    const metadata = this.createRequestMetadata(description);
    const startTime = Date.now();

    try {
      const contextSection = existingWorkflow 
        ? `\nExisting workflow to modify:\n${JSON.stringify(existingWorkflow, null, 2)}\n` 
        : '';

      const prompt = `
        Create a workflow based on the following description:

        ${description}
        ${contextSection}

        The workflow should include appropriate components where relevant:
        1. Data Source nodes
        2. Data Processing nodes
        3. Conditional Logic nodes
        4. Action nodes

        Each node should have:
        - A descriptive name
        - Appropriate configuration
        - Clear connections to other nodes
        - Proper positioning for visual clarity
      `;

      const workflow = await this.claudeWrapper.generateJson<GeneratedWorkflow>(prompt, this.config.model);

      if (!workflow.nodes || !workflow.edges) {
        throw new Error('Invalid workflow structure: missing nodes or edges');
      }

      // Validate the generated workflow
      const validation = await this.validateWorkflowStructure(workflow);

      const result: WorkflowGenerationResult = {
        workflow,
        message: validation.isValid ? 'Workflow generated successfully' : 'Workflow generated with warnings',
        suggestions: validation.warnings.map(w => w.message)
      };

      const aiResponse: AIResponse<WorkflowGenerationResult> = {
        data: result,
        metadata: {
          requestId: metadata.requestId,
          timestamp: new Date(),
          model: this.config.model,
          processingTime: Date.now() - startTime
        }
      };

      this.trackRequest(aiResponse);
      return aiResponse;
    } catch (error) {
      console.error('Error generating workflow:', error);
      return {
        data: {
          workflow: { nodes: [], edges: [], name: '', description: '' },
          message: 'Failed to generate workflow',
          suggestions: []
        },
        metadata: {
          requestId: metadata.requestId,
          timestamp: new Date(),
          model: this.config.model,
          processingTime: Date.now() - startTime
        },
        error: {
          code: 'WORKFLOW_GENERATION_ERROR',
          message: error instanceof Error ? error.message : 'Failed to generate workflow from description',
          details: { error }
        }
      };
    }
  }

  private async validateWorkflowStructure(workflow: GeneratedWorkflow): Promise<WorkflowValidation> {
    const errors: WorkflowValidation['errors'] = [];
    const warnings: WorkflowValidation['warnings'] = [];

    // Check for isolated nodes
    const connectedNodeIds = new Set(workflow.edges.flatMap(e => [e.source, e.target]));
    workflow.nodes.forEach(node => {
      if (!connectedNodeIds.has(node.id)) {
        warnings.push({
          code: 'ISOLATED_NODE',
          message: `Node "${node.data.label}" is not connected to any other nodes`,
          nodeId: node.id
        });
      }
    });

    // Check for valid node references in edges
    const nodeIds = new Set(workflow.nodes.map(n => n.id));
    workflow.edges.forEach(edge => {
      if (!nodeIds.has(edge.source)) {
        errors.push({
          code: 'INVALID_EDGE_SOURCE',
          message: `Edge references non-existent source node: ${edge.source}`,
          edgeId: edge.id
        });
      }
      if (!nodeIds.has(edge.target)) {
        errors.push({
          code: 'INVALID_EDGE_TARGET',
          message: `Edge references non-existent target node: ${edge.target}`,
          edgeId: edge.id
        });
      }
    });

    return {
      isValid: errors.length === 0,
      errors,
      warnings
    };
  }

  async getRequestHistory(requestId: string): Promise<AIResponse[]> {
    return this.requestHistory.get(requestId) || [];
  }

  async analyzeConnection(
    connectionData: Record<string, unknown>
  ): Promise<ConnectionAnalysis> {
    try {
      const message: ClaudeMessage = {
        role: 'user',
        content: `Analyze this connection data and provide health status and recommendations:
          ${JSON.stringify(connectionData, null, 2)}`
      };

      const response = await this.chat([message]);

      // Parse Claude's response to extract insights
      // This is a simple implementation - you might want to add more structure
      const analysis: ConnectionAnalysis = {
        isHealthy: response.data.content.toLowerCase().includes('healthy'),
        recommendations: response.data.content
          .split('\n')
          .filter(line => line.startsWith('- '))
          .map(line => line.substring(2)),
        error: response.data.content.toLowerCase().includes('error') 
          ? response.data.content.split('\n')[0] 
          : undefined
      };

      return analysis;
    } catch (error) {
      console.error('Connection analysis error:', error);
      return {
        isHealthy: false,
        error: 'Failed to analyze connection'
      };
    }
  }

  async suggestFixes(
    connectionType: string,
    errorData: Record<string, unknown>
  ): Promise<string[]> {
    try {
      const message: ClaudeMessage = {
        role: 'user',
        content: `Suggest fixes for this ${connectionType} connection error:
          ${JSON.stringify(errorData, null, 2)}`
      };

      const response = await this.chat([message]);

      // Extract suggestions from Claude's response
      return response.data.content
        .split('\n')
        .filter(line => line.startsWith('- '))
        .map(line => line.substring(2));
    } catch (error) {
      console.error('Error suggesting fixes:', error);
      return ['Unable to generate suggestions at this time'];
    }
  }
}

import axios from 'axios';

interface ClayConfig { apiKey: string;
  baseUrl?: string;
    }

interface ClayContact {
  id: string;
  email: string;
  firstName?: string;
  lastName?: string;
  company?: string;
  title?: string;
  phone?: string;
  linkedin?: string;
  [key: string]: any;
}

export class ClayService {
  private config: ClayConfig;
  private client: ReturnType<typeof axios.create>;

  constructor(config: ClayConfig) {
    this.config = { baseUrl: 'https://api.clay.com/v1',  // Replace with actual Clay API URL
      ...config
        };

    this.client = axios.create({
      baseURL: this.config.baseUrl,
      headers: {
        'Authorization': `Bearer ${this.config.apiKey}`,
        'Content-Type': 'application/json'
      }
    });
  }

  async validateConnection(): Promise<boolean> {
    try {
      await this.client.get('/auth/test');
      return true;
    } catch (error) { console.error('Clay connection validation failed:', error);
      return false;
        }
  }

  async findProspects(query: { company?: string;
    title?: string;
    location?: string;
    industry?: string;
    limit?: number;
      }): Promise<ClayContact[]> {
    try {
      const response = await this.client.post('/prospects/search', query);
      return response.data.prospects;
    } catch (error) { console.error('Clay prospect search failed:', error);
      throw new Error(error instanceof Error ? error.message : 'Failed to search prospects');
        }
  }

  async enrichContact(contact: Partial<ClayContact>): Promise<ClayContact> {
    try {
      const response = await this.client.post('/enrich', contact);
      return response.data;
    } catch (error) { console.error('Clay contact enrichment failed:', error);
      throw new Error(error instanceof Error ? error.message : 'Failed to enrich contact');
        }
  }

  async createList(name: string, contacts: ClayContact[]): Promise<{ id: string; name: string     }> {
    try {
      const response = await this.client.post('/lists', { name,
        contacts: contacts.map(c => c.id)
          });
      return response.data;
    } catch (error) { console.error('Clay list creation failed:', error);
      throw new Error(error instanceof Error ? error.message : 'Failed to create list');
        }
  }

  async getApiStatus(): Promise<{ status: 'healthy' | 'degraded' | 'down';
    rateLimitRemaining?: number;
    resetTime?: Date;
      }> {
    try {
      const response = await this.client.get('/status');
      return response.data;
    } catch (error) {
      console.error('Clay status check failed:', error);
      return { status: 'down'     };
    }
  }

  async refreshToken(): Promise<boolean> {
    try {
      const response = await this.client.post('/auth/refresh');

      // Update client with new token
      this.client = axios.create({
        baseURL: this.config.baseUrl,
        headers: {
          'Authorization': `Bearer ${response.data.token}`,
          'Content-Type': 'application/json'
        }
      });

      return true;
    } catch (error) { console.error('Clay token refresh failed:', error);
      return false;
        }
  }
}

