interface ProcessingRule {
  transform?: 'uppercase' | 'lowercase' | 'trim';
  format?: 'date' | 'number' | 'currency';
}

interface ProcessingRules {
  [key: string]: ProcessingRule;
}

export class DataProcessorServer extends MCPServer {
  constructor() {
    super(
      {
        id: 'app1',
        name: 'Data Processor',
        capabilities: ['process_data', 'transform_data'],
        endpoint: 'http://localhost:3001',
      },
      3001
    );
  }

  protected async handleRequest(request: MCPRequest): Promise<MCPResponse> {
    switch (request.method) {
      case 'process_data':
        return this.processData(request);
      case 'transform_data':
        return this.transformData(request);
      default:
        throw new Error(`Method ${request.method} not supported`);
    }
  }

  private async processData(request: MCPRequest): Promise<MCPResponse> {
    const { data, rules } = request.params as {
      data: Record<string, any>;
      rules: ProcessingRules;
    };

    // Simulate data processing
    const processedData = { ...data };

    if (rules) {
      for (const [field, rule] of Object.entries(rules)) {
        if (rule.transform) {
          switch (rule.transform) {
            case 'uppercase':
              if (typeof processedData[field] === 'string') {
                processedData[field] = processedData[field].toUpperCase();
              }
              break;
            case 'lowercase':
              if (typeof processedData[field] === 'string') {
                processedData[field] = processedData[field].toLowerCase();
              }
              break;
            case 'trim':
              if (typeof processedData[field] === 'string') {
                processedData[field] = processedData[field].trim();
              }
              break;
          }
        }
      }
    }

    return {
      jsonrpc: '2.0',
      result: { data: processedData },
      id: request.id,
    };
  }

  private async transformData(request: MCPRequest): Promise<MCPResponse> {
    const { data, format } = request.params as {
      data: Record<string, any>;
      format: string;
    };

    // Simulate data transformation
    const transformedData = { ...data };

    switch (format) {
      case 'json':
        // Data is already in JSON format
        break;
      case 'xml':
        // Simple XML transformation simulation
        transformedData._format = 'xml';
        break;
      default:
        throw new Error(`Unsupported format: ${format}`);
    }

    return {
      jsonrpc: '2.0',
      result: { data: transformedData },
      id: request.id,
    };
  }
}

import { MCPRequest, MCPResponse } from '../types';

import { MCPServer } from './MCPServer';

interface ValidationRule { required?: boolean;
    }

interface ValidationRules { [key: string]: ValidationRule;
    }

interface ValidationError { field: string;
  message: string;
    }

interface ValidationResults { isValid: boolean;
  errors: ValidationError[];
  warnings: ValidationError[];
    }

interface CleaningRule { trim?: boolean;
  lowercase?: boolean;
    }

interface CleaningRules { [key: string]: CleaningRule;
    }

export class DataValidatorServer extends MCPServer {
  constructor() {
    super(
      { id: 'app2',
        name: 'Data Validator',
        capabilities: ['validate_data', 'clean_data'],
        endpoint: 'http://localhost:3002',
          },
      3002
    );
  }

  protected async handleRequest(request: MCPRequest): Promise<MCPResponse> {
    switch (request.method) {
      case 'validate_data':
        return this.validateData(request);
      case 'clean_data':
        return this.cleanData(request);
      default:
        throw new Error(`Method ${request.method} not supported`);
    }
  }

  private async validateData(request: MCPRequest): Promise<MCPResponse> {
    const { data, rules } = request.params as { data: Record<string, any>, rules: ValidationRules     };

    // Simulate data validation
    const validationResults: ValidationResults = { isValid: true,
      errors: [],
      warnings: [],
        };

    // Simple validation rules
    if (rules) {
      for (const [field, rule] of Object.entries(rules)) {
        if (rule.required && !data[field]) {
          validationResults.isValid = false;
          validationResults.errors.push({
            field,
            message: `${field} is required`,
          });
        }
      }
    }

    return {
      jsonrpc: '2.0',
      result: { validation: validationResults     },
      id: request.id,
    };
  }

  private async cleanData(request: MCPRequest): Promise<MCPResponse> {
    const { data, cleaningRules } = request.params as { data: Record<string, any>, cleaningRules: CleaningRules     };

    // Simulate data cleaning
    const cleanedData = { ...data };

    if (cleaningRules) {
      for (const [field, rule] of Object.entries(cleaningRules)) {
        if (rule.trim && typeof cleanedData[field] === 'string') {
          cleanedData[field] = cleanedData[field].trim();
        }
        if (rule.lowercase && typeof cleanedData[field] === 'string') {
          cleanedData[field] = cleanedData[field].toLowerCase();
        }
      }
    }

    return {
      jsonrpc: '2.0',
      result: { data: cleanedData     },
      id: request.id,
    };
  }
}

