import { prisma } from '@/lib/prisma';
import { VoiceCommand, VoiceCommandResponse, VoiceInteraction } from '@/types/voice';

interface VoiceMetrics {
  totalCommands: number;
  successfulCommands: number;
  failedCommands: number;
  averageProcessingTime: number;
  commonIntents: Array<{ intent: string; count: number }>;
  commonErrors: Array<{ error: string; count: number }>;
}

export class VoiceAnalyticsService {
  static async logVoiceInteraction(
    interaction: VoiceInteraction,
    response: VoiceCommandResponse,
    processingTime: number
  ) {
    try {
      // Log detailed analytics
      await prisma.voiceAnalytics.create({
        data: {
          interactionId: interaction.id,
          userId: interaction.userId,
          workflowId: interaction.workflowId,
          transcript: interaction.transcript,
          intent: interaction.intent || 'unknown',
          success: response.success,
          processingTime,
          errorMessage: !response.success ? response.message : null,
          metadata: {
            deepgramConfidence: interaction.metadata?.confidence,
            entities: interaction.entities,
            workflowUpdates: response.workflowUpdates ? true : false,
            nodeUpdates: response.nodeUpdates?.length || 0,
            edgeUpdates: response.edgeUpdates?.length || 0,
          },
        },
      });

      // Update aggregate metrics
      await this.updateAggregateMetrics(interaction.userId);
    } catch (error) { console.error('Error logging voice analytics:', error); }
  }

  static async getVoiceMetrics(userId: string, timeRange?: { start: Date; end: Date }): Promise<VoiceMetrics> {
    const whereClause = {
      userId,
      ...(timeRange && {
        createdAt: {
          gte: timeRange.start,
          lte: timeRange.end,
        },
      }),
    };

    const [
      totalCommands,
      successfulCommands,
      failedCommands,
      avgProcessingTime,
      intents,
      errors,
    ] = await Promise.all([
      prisma.voiceAnalytics.count({ where: whereClause }),
      prisma.voiceAnalytics.count({ where: { ...whereClause, success: true } }),
      prisma.voiceAnalytics.count({ where: { ...whereClause, success: false } }),
      prisma.voiceAnalytics.aggregate({
        where: whereClause,
        _avg: { processingTime: true },
      }),
      prisma.voiceAnalytics.groupBy({
        by: ['intent'],
        where: whereClause,
        _count: true,
        orderBy: { _count: { intent: 'desc' } },
        take: 10,
      }),
      prisma.voiceAnalytics.groupBy({
        by: ['errorMessage'],
        where: { ...whereClause, success: false },
        _count: true,
        orderBy: { _count: { errorMessage: 'desc' } },
        take: 10,
      }),
    ]);

    return {
      totalCommands,
      successfulCommands,
      failedCommands,
      averageProcessingTime: avgProcessingTime._avg.processingTime || 0,
      commonIntents: intents.map(i => ({ intent: i.intent, count: i._count.intent })),
      commonErrors: errors.map(e => ({ error: e.errorMessage || 'Unknown error', count: e._count.errorMessage })),
    };
  }

  private static async updateAggregateMetrics(userId: string) {
    const today = new Date();
    const startOfDay = new Date(today.setHours(0, 0, 0, 0));

    await prisma.userVoiceMetrics.upsert({
      where: { userId_date: { userId, date: startOfDay } },
      create: { userId,
        date: startOfDay,
        totalCommands: 1,
        successfulCommands: 0,
        failedCommands: 0,
      },
      update: {
        totalCommands: { increment: 1 },
      },
    });
  }

  static async trackRecognitionAccuracy(
    transcript: string,
    confidence: number,
    correctedText?: string
  ) {
    if (correctedText) {
      await prisma.voiceRecognitionAccuracy.create({
        data: { transcript,
          confidence,
          correctedText,
          wasCorrect: transcript.toLowerCase() === correctedText.toLowerCase(),
        },
      });
    }
  }
}

/**
 * Workflow Executor Service
 * 
 * Handles the execution of workflow nodes with support for:
 * - Safe mode (manual approval of each step)
 * - Execution timeouts
 * - Concurrency control
 * - Memory integration
 * - RAG integration
 */

import { Edge, Node } from 'reactflow';

import { WorkflowSettings } from '@/components/workflow/WorkflowSettings.tsx';

import ragService, { RetrievalRequest } from './ragService';
import WorkflowMemory, { getWorkflowMemory, MemoryItem } from './workflowMemory';


export type NodeStatus = 'idle' | 'running' | 'completed' | 'error' | 'waiting-approval';

export interface NodeExecutionResult { nodeId: string;
  status: NodeStatus;
  output?: any;
  error?: string;
  executionTimeMs: number;
  memoryIds?: string[];
}

export interface WorkflowExecutionState { workflowId: string;
  status: 'idle' | 'running' | 'paused' | 'completed' | 'error';
  currentNodes: string[];
  nodeStatus: Record<string, NodeStatus>;
  results: Record<string, NodeExecutionResult>;
  pendingApprovals: string[];
  startTime?: number;
  endTime?: number;
  error?: string;
}

export class WorkflowExecutor {
  private workflowId: string;
  private settings: WorkflowSettings;
  private nodes: Node[];
  private edges: Edge[];
  private state: WorkflowExecutionState;
  private memory: WorkflowMemory;
  private timeoutId?: NodeJS.Timeout;
  private listeners: ((state: WorkflowExecutionState) => void)[] = [];

  constructor(workflowId: string, nodes: Node[], edges: Edge[], settings: WorkflowSettings) {
    this.workflowId = workflowId;
    this.nodes = nodes;
    this.edges = edges;
    this.settings = settings;

    // Initialize state
    this.state = {
      workflowId,
      status: 'idle',
      currentNodes: [],
      nodeStatus: {},
      results: {},
      pendingApprovals: [],
    };

    // Initialize memory if enabled
    if (settings.memoryEnabled) {
      this.memory = getWorkflowMemory({ workflowId,
        memoryType: settings.memoryType,
        memorySize: settings.memorySize,
        contextWindowSize: settings.contextWindowSize,
      });
    } else {
      // Create in-memory instance anyway for uniformity
      this.memory = getWorkflowMemory({ workflowId,
        memoryType: 'buffer',
        memorySize: 1024,
        contextWindowSize: 10,
      });
    }
  }

  /**
   * Add a listener for state changes
   */
  addListener(listener: (state: WorkflowExecutionState) => void): void {
    this.listeners.push(listener);
  }

  /**
   * Remove a listener
   */
  removeListener(listener: (state: WorkflowExecutionState) => void): void {
    this.listeners = this.listeners.filter(l => l !== listener);
  }

  /**
   * Start workflow execution
   */
  async start(): Promise<void> {
    if (this.state.status === 'running') {
      throw new Error('Workflow is already running');
    }

    // Reset state
    this.state = {
      workflowId: this.workflowId,
      status: 'running',
      currentNodes: [],
      nodeStatus: {},
      results: {},
      pendingApprovals: [],
      startTime: Date.now(),
    };

    // Set timeout if configured
    if (this.settings.executionTimeout > 0) {
      this.timeoutId = setTimeout(() => {
        this.handleError(new Error(`Workflow execution timed out after ${this.settings.executionTimeout} seconds`));
      }, this.settings.executionTimeout * 1000);
    }

    // Find starting nodes (nodes with no incoming edges)
    const startingNodes = this.nodes.filter(node => {
      return !this.edges.some(edge => edge.target === node.id);
    });

    if (startingNodes.length === 0) {
      this.handleError(new Error('No starting nodes found in workflow'));
      return;
    }

    // Queue starting nodes for execution
    for (const node of startingNodes) {
      this.queueNode(node.id);
    }

    // Start execution
    await this.processQueue();
  }

  /**
   * Pause workflow execution
   */
  pause(): void {
    if (this.state.status !== 'running') {
      return;
    }

    this.state.status = 'paused';
    this.notifyListeners();
  }

  /**
   * Resume workflow execution
   */
  async resume(): Promise<void> {
    if (this.state.status !== 'paused') {
      return;
    }

    this.state.status = 'running';
    this.notifyListeners();

    await this.processQueue();
  }

  /**
   * Stop workflow execution
   */
  stop(): void {
    if (this.state.status !== 'running' && this.state.status !== 'paused') {
      return;
    }

    if (this.timeoutId) {
      clearTimeout(this.timeoutId);
    }

    this.state.status = 'completed';
    this.state.endTime = Date.now();
    this.notifyListeners();
  }

  /**
   * Approve a node in safe mode
   */
  async approveNode(nodeId: string): Promise<void> {
    if (!this.state.pendingApprovals.includes(nodeId)) {
      throw new Error(`Node ${nodeId} is not pending approval`);
    }

    // Remove from pending approvals
    this.state.pendingApprovals = this.state.pendingApprovals.filter(id => id !== nodeId);

    // Update status
    this.state.nodeStatus[nodeId] = 'running';
    this.notifyListeners();

    // Execute node
    await this.executeNode(nodeId);
  }

  /**
   * Reject a node in safe mode
   */
  rejectNode(nodeId: string): void {
    if (!this.state.pendingApprovals.includes(nodeId)) {
      throw new Error(`Node ${nodeId} is not pending approval`);
    }

    // Remove from pending approvals
    this.state.pendingApprovals = this.state.pendingApprovals.filter(id => id !== nodeId);

    // Update status
    this.state.nodeStatus[nodeId] = 'error';
    this.state.results[nodeId] = { nodeId,
      status: 'error',
      error: 'Execution rejected by user',
      executionTimeMs: 0,
    };

    this.notifyListeners();

    // Check if we need to continue with other nodes
    this.checkWorkflowCompletion();
  }

  /**
   * Queue a node for execution
   */
  private queueNode(nodeId: string): void {
    if (
      this.state.nodeStatus[nodeId] === 'running' ||
      this.state.nodeStatus[nodeId] === 'completed' ||
      this.state.nodeStatus[nodeId] === 'waiting-approval' ||
      this.state.currentNodes.includes(nodeId)
    ) {
      return;
    }

    // Add to current nodes queue
    this.state.currentNodes.push(nodeId);

    // Set initial status
    this.state.nodeStatus[nodeId] = 'idle';

    this.notifyListeners();
  }

  /**
   * Process the execution queue
   */
  private async processQueue(): Promise<void> {
    if (this.state.status !== 'running' || this.state.currentNodes.length === 0) {
      return;
    }

    // Limit concurrent executions
    const availableSlots = this.settings.maxConcurrentNodes - 
      Object.values(this.state.nodeStatus).filter(status => status === 'running').length;

    if (availableSlots <= 0) {
      return; // Max concurrency reached
    }

    // Get next nodes to execute
    const nodesToRun = this.state.currentNodes
      .filter(nodeId => this.state.nodeStatus[nodeId] === 'idle')
      .slice(0, availableSlots);

    if (nodesToRun.length === 0) {
      return;
    }

    // Start executions
    const executions = nodesToRun.map(nodeId => {
      if (this.settings.safeMode) {
        // In safe mode, request approval first
        this.state.nodeStatus[nodeId] = 'waiting-approval';
        this.state.pendingApprovals.push(nodeId);
        this.notifyListeners();
        return Promise.resolve();
      } else {
        // Execute directly
        return this.executeNode(nodeId);
      }
    });

    // Wait for executions to complete
    await Promise.all(executions);

    // Continue with remaining nodes
    await this.processQueue();
  }

  /**
   * Execute a single node
   */
  private async executeNode(nodeId: string): Promise<void> {
    if (this.state.status !== 'running') {
      return;
    }

    // Update status
    this.state.nodeStatus[nodeId] = 'running';
    this.notifyListeners();

    // Remove from current nodes queue
    this.state.currentNodes = this.state.currentNodes.filter(id => id !== nodeId);

    try {
      // Track execution time
      const startTime = Date.now();

      // Find the node
      const node = this.nodes.find(n => n.id === nodeId);
      if (!node) {
        throw new Error(`Node ${nodeId} not found`);
      }

      // Get input from previous nodes
      const inputs = this.getNodeInputs(nodeId);

      // Store input in memory if enabled
      let inputMemoryId: string | undefined;
      if (this.settings.memoryEnabled) {
        inputMemoryId = `${nodeId}-input-${Date.now()}`;
        this.memory.store({ id: inputMemoryId,
          nodeId,
          timestamp: Date.now(),
          type: 'input',
          content: inputs,
        });
      }

      // Prepare RAG context if enabled
      let ragContext = '';
      if (this.settings.ragEnabled && this.settings.knowledgeBase) {
        // Generate a query based on node data and inputs
        const query = this.generateRagQuery(node, inputs);

        // Retrieve documents
        const retrievalResult = await ragService.retrieveDocuments({
          query,
          options: {
            knowledgeBase: this.settings.knowledgeBase,
            similarityThreshold: this.settings.similarityThreshold,
            maxDocuments: this.settings.maxDocuments,
          }
        });

        // Generate context
        ragContext = ragService.generateContext(retrievalResult);
      }

      // Execute the node (mock implementation)
      // In a real implementation, this would call the specific node type's execution logic
      const output = await this.mockExecuteNode(node, inputs, ragContext);

      // Store output in memory if enabled
      let outputMemoryId: string | undefined;
      if (this.settings.memoryEnabled) {
        outputMemoryId = `${nodeId}-output-${Date.now()}`;
        this.memory.store({ id: outputMemoryId,
          nodeId,
          timestamp: Date.now(),
          type: 'output',
          content: output,
        });
      }

      // Update status
      const executionTimeMs = Date.now() - startTime;
      this.state.nodeStatus[nodeId] = 'completed';
      this.state.results[nodeId] = { nodeId,
        status: 'completed',
        output,
        executionTimeMs,
        memoryIds: [inputMemoryId, outputMemoryId].filter(Boolean) as string[],
      };

      this.notifyListeners();

      // Execute auto-save if enabled
      if (this.settings.autoSave) {
        this.saveWorkflowState();
      }

      // Find next nodes to execute
      this.queueNextNodes(nodeId);

      // Check if workflow is complete
      this.checkWorkflowCompletion();

      // Continue processing queue
      await this.processQueue();

    } catch (error) {
      // Handle node execution error
      this.state.nodeStatus[nodeId] = 'error';
      this.state.results[nodeId] = { nodeId,
        status: 'error',
        error: error instanceof Error ? error.message : String(error),
        executionTimeMs: 0,
      };

      this.notifyListeners();

      // Check if workflow should continue or stop on error
      // For now, we just continue with other branches
      this.checkWorkflowCompletion();
      await this.processQueue();
    }
  }

  /**
   * Get inputs for a node from previous nodes
   */
  private getNodeInputs(nodeId: string): any {
    const incomingEdges = this.edges.filter(edge => edge.target === nodeId);

    if (incomingEdges.length === 0) {
      return {}; // No inputs
    }

    // Collect outputs from source nodes
    const inputs: Record<string, any> = {};

    for (const edge of incomingEdges) {
      const sourceNodeId = edge.source;
      const sourceNodeResult = this.state.results[sourceNodeId];

      if (sourceNodeResult?.status === 'completed' && sourceNodeResult.output !== undefined) {
        // If edge has a label, use it as the key, otherwise use the source node ID
        const key = edge.data?.label || sourceNodeId;
        inputs[key] = sourceNodeResult.output;
      }
    }

    return inputs;
  }

  /**
   * Queue next nodes for execution
   */
  private queueNextNodes(nodeId: string): void {
    const outgoingEdges = this.edges.filter(edge => edge.source === nodeId);

    for (const edge of outgoingEdges) {
      const targetNodeId = edge.target;

      // Check if all incoming edges to the target node have completed source nodes
      const incomingEdges = this.edges.filter(e => e.target === targetNodeId);
      const allSourcesCompleted = incomingEdges.every(e => 
        this.state.nodeStatus[e.source] === 'completed'
      );

      if (allSourcesCompleted) {
        this.queueNode(targetNodeId);
      }
    }
  }

  /**
   * Check if workflow execution is complete
   */
  private checkWorkflowCompletion(): void {
    if (this.state.status !== 'running') {
      return;
    }

    // Check if all nodes have a final status (completed or error)
    const allNodesProcessed = this.nodes.every(node => 
      this.state.nodeStatus[node.id] === 'completed' || 
      this.state.nodeStatus[node.id] === 'error'
    );

    // Check if there are no pending nodes
    const noPendingNodes = this.state.currentNodes.length === 0;

    // Check if there are no nodes waiting for approval
    const noWaitingApprovals = this.state.pendingApprovals.length === 0;

    if (allNodesProcessed && noPendingNodes && noWaitingApprovals) {
      // Workflow is complete
      this.state.status = 'completed';
      this.state.endTime = Date.now();

      // Clear timeout if set
      if (this.timeoutId) {
        clearTimeout(this.timeoutId);
      }

      // Send notifications if enabled
      if (this.settings.notifyOnCompletion) {
        this.sendCompletionNotification();
      }

      this.notifyListeners();
    }
  }

  /**
   * Handle workflow error
   */
  private handleError(error: Error): void {
    this.state.status = 'error';
    this.state.error = error.message;
    this.state.endTime = Date.now();

    // Clear timeout if set
    if (this.timeoutId) {
      clearTimeout(this.timeoutId);
    }

    // Send notifications if enabled
    if (this.settings.notifyOnError) {
      this.sendErrorNotification(error);
    }

    this.notifyListeners();
  }

  /**
   * Save workflow state (for auto-save feature)
   */
  private saveWorkflowState(): void {
    // In a real implementation, this would save to a database or API
    // For this example, we just log to console
    if (this.settings.debugMode) {
      console.log(`[Workflow ${this.workflowId}] Auto-saving state:`, JSON.stringify(this.state, null, 2));
    }
  }

  /**
   * Send completion notification
   */
  private sendCompletionNotification(): void {
    // In a real implementation, this would send a notification via API
    // For this example, we just log to console
    console.log(`[Workflow ${this.workflowId}] Completed successfully`);
  }

  /**
   * Send error notification
   */
  private sendErrorNotification(error: Error): void {
    // In a real implementation, this would send a notification via API
    // For this example, we just log to console
    console.error(`[Workflow ${this.workflowId}] Error:`, error.message);
  }

  /**
   * Notify all listeners of state changes
   */
  private notifyListeners(): void {
    for (const listener of this.listeners) {
      try {
        listener(this.state);
      } catch (error) { console.error('Error in workflow state listener:', error);
      }
    }
  }

  /**
   * Generate a RAG query based on node data and inputs
   */
  private generateRagQuery(node: Node, inputs: any): string {
    // Build a query based on node type and data
    const nodeType = node.data.type || 'default';
    const nodeDescription = node.data.description || '';

    let query = `For a ${nodeType} node described as "${nodeDescription}", `;

    // Add context based on node settings
    if (node.data.settings) {
      const settingsStr = Object.entries(node.data.settings)
        .map(([key, value]) => `${key}: ${value}`)
        .join(', ');

      query += `with settings (${settingsStr}), `;
    }

    // Add context based on inputs
    const inputStr = Object.keys(inputs).join(', ');
    if (inputStr) {
      query += `using inputs: ${inputStr}, `;
    }

    query += 'provide relevant information and context.';

    return query;
  }

  /**
   * Mock node execution (for demonstration purposes)
   */
  private async mockExecuteNode(node: Node, inputs: any, ragContext: string): Promise<any> {
    // Simulate processing time
    await new Promise(resolve => setTimeout(resolve, 500 + Math.random() * 1000));

    const nodeType = node.data.type || 'default';

    // Mock outputs based on node type
    switch (nodeType) {
      case 'extract-webpage':
        return { title: 'Example Website',
          content: 'This is example content extracted from a webpage.',
          timestamp: new Date().toISOString()
        };

      case 'process-data':
        return {
          processedData: inputs.data ? `Processed: ${JSON.stringify(inputs.data)}` : 'No input data',
          fields: ['name', 'email', 'phone'],
          metadata: { processingTime: '0.35s'     }
        };

      case 'conditional':
        // Evaluate the condition if possible
        const condition = node.data.settings?.condition;
        if (condition && typeof condition === 'string') {
          // Very simple evaluation for demo purposes
          try {
            // Create a safe evaluation context with inputs
            const context = { ...inputs, result: inputs.result || {} };
            const result = eval(`with (context) { ${condition} }`);
            return { result, 
              path: result ? 'true' : 'false', 
              condition 
            };
          } catch (error) {
            return { error: 'Condition evaluation failed', condition     };
          }
        }
        return { result: Math.random() > 0.5, condition: 'Random outcome'     };

      case 'send-email':
        return { sent: true,
          to: node.data.settings?.to || 'example@example.com',
          subject: node.data.settings?.subject || 'No subject',
          timestamp: new Date().toISOString()
        };

      case 'database':
        return { operation: node.data.settings?.queryType || 'select',
          affected: Math.floor(Math.random() * 10) + 1,
          success: true
        };

      default:
        return { executed: true,
          nodeType,
          timestamp: new Date().toISOString()
        };
    }
  }
}

// Factory function to create a workflow executor
export function createWorkflowExecutor(
  workflowId: string,
  nodes: Node[],
  edges: Edge[],
  settings: WorkflowSettings
): WorkflowExecutor {
  return new WorkflowExecutor(workflowId, nodes, edges, settings);
}

export default WorkflowExecutor;

/**
 * Workflow Memory Service
 * 
 * Manages memory for workflow execution with different storage strategies:
 * - Buffer: In-memory only, cleared on page refresh
 * - Session: Uses sessionStorage, persists until browser is closed
 * - Persistent: Uses localStorage + optional server sync, persists across sessions
 */

export interface MemoryItem {
  id: string;
  nodeId: string;
  timestamp: number;
  type: 'input' | 'output' | 'state' | 'error';
  content: unknown;
}

export interface WorkflowMemoryOptions {
  workflowId: string;
  memoryType: 'buffer' | 'session' | 'persistent';
  memorySize: number; // In KB,
  contextWindowSize: number; // Number of items
}

class WorkflowMemory {
  private workflowId: string;
  private memoryType: 'buffer' | 'session' | 'persistent';
  private memorySize: number;
  private contextWindowSize: number;
  private bufferMemory: Map<string, MemoryItem[]> = new Map();

  constructor(options: WorkflowMemoryOptions) {
    this.workflowId = options.workflowId;
    this.memoryType = options.memoryType;
    this.memorySize = options.memorySize;
    this.contextWindowSize = options.contextWindowSize;

    // Load existing memory if using session or persistent storage
    if (this.memoryType !== 'buffer') {
      this.loadFromStorage();
    }
  }

  /**
   * Update memory options
   */
  updateOptions(options: Partial<WorkflowMemoryOptions>) {
    if (options.memoryType && options.memoryType !== this.memoryType) {
      // If changing memory type, migrate the data
      const currentMemory = this.getAll();
      this.memoryType = options.memoryType;
      this.setAll(currentMemory);
    }

    if (options.memorySize) {
      this.memorySize = options.memorySize;
    }

    if (options.contextWindowSize) {
      this.contextWindowSize = options.contextWindowSize;
    }

    if (options.workflowId) {
      this.workflowId = options.workflowId;
    }
  }

  /**
   * Store a memory item
   */
  store(item: MemoryItem): void {
    const memoryKey = this.getMemoryKey();
    let memoryItems: MemoryItem[] = [];

    // Get existing items based on storage type
    switch (this.memoryType) {
      case 'buffer':
        memoryItems = this.bufferMemory.get(memoryKey) || [];
        break;
      case 'session':
        const sessionData = sessionStorage.getItem(memoryKey);
        memoryItems = sessionData ? JSON.parse(sessionData) : [];
        break;
      case 'persistent':
        const localData = localStorage.getItem(memoryKey);
        memoryItems = localData ? JSON.parse(localData) : [];
        break;
    }

    // Add new item
    memoryItems.push(item);

    // Trim to context window size
    if (memoryItems.length > this.contextWindowSize) {
      memoryItems = memoryItems.slice(-this.contextWindowSize);
    }

    // Check memory size limit (only for session/persistent)
    if (this.memoryType !== 'buffer') {
      const memoryString = JSON.stringify(memoryItems);
      const memorySizeInKB = new Blob([memoryString]).size / 1024;

      if (memorySizeInKB > this.memorySize) {
        // If over limit, remove oldest items until under limit
        while (memoryItems.length > 1) {
          memoryItems.shift();
          const newMemoryString = JSON.stringify(memoryItems);
          const newMemorySizeInKB = new Blob([newMemoryString]).size / 1024;

          if (newMemorySizeInKB <= this.memorySize) {
            break;
          }
        }
      }
    }

    // Store based on memory type
    switch (this.memoryType) {
      case 'buffer':
        this.bufferMemory.set(memoryKey, memoryItems);
        break;
      case 'session':
        try {
          sessionStorage.setItem(memoryKey, JSON.stringify(memoryItems));
        } catch (error) {
          console.error('Failed to store in session storage:', error);
          // Fallback to buffer memory
          this.bufferMemory.set(memoryKey, memoryItems);
        }
        break;
      case 'persistent':
        try {
          localStorage.setItem(memoryKey, JSON.stringify(memoryItems));
        } catch (error) {
          console.error('Failed to store in local storage:', error);
          // Fallback to buffer memory
          this.bufferMemory.set(memoryKey, memoryItems);
        }
        break;
    }
  }

  /**
   * Get all memory items for the workflow
   */
  getAll(): MemoryItem[] {
    const memoryKey = this.getMemoryKey();

    switch (this.memoryType) {
      case 'buffer':
        return this.bufferMemory.get(memoryKey) || [];
      case 'session':
        const sessionData = sessionStorage.getItem(memoryKey);
        return sessionData ? JSON.parse(sessionData) : [];
      case 'persistent':
        const localData = localStorage.getItem(memoryKey);
        return localData ? JSON.parse(localData) : [];
      default:
        return [];
    }
  }

  /**
   * Get items for a specific node
   */
  getByNodeId(nodeId: string): MemoryItem[] {
    const allItems = this.getAll();
    return allItems.filter(item => item.nodeId === nodeId);
  }

  /**
   * Get items by type
   */
  getByType(type: MemoryItem['type']): MemoryItem[] {
    const allItems = this.getAll();
    return allItems.filter(item => item.type === type);
  }

  /**
   * Get the most recent context window of items
   */
  getContextWindow(): MemoryItem[] {
    const allItems = this.getAll();
    return allItems.slice(-this.contextWindowSize);
  }

  /**
   * Set all memory items (replaces existing)
   */
  setAll(items: MemoryItem[]): void {
    const memoryKey = this.getMemoryKey();
    const limitedItems = items.slice(-this.contextWindowSize);

    switch (this.memoryType) {
      case 'buffer':
        this.bufferMemory.set(memoryKey, limitedItems);
        break;
      case 'session':
        try {
          sessionStorage.setItem(memoryKey, JSON.stringify(limitedItems));
        } catch (error) {
          console.error('Failed to store in session storage:', error);
          this.bufferMemory.set(memoryKey, limitedItems);
        }
        break;
      case 'persistent':
        try {
          localStorage.setItem(memoryKey, JSON.stringify(limitedItems));
        } catch (error) {
          console.error('Failed to store in local storage:', error);
          this.bufferMemory.set(memoryKey, limitedItems);
        }
        break;
    }
  }

  /**
   * Clear all memory for this workflow
   */
  clear(): void {
    const memoryKey = this.getMemoryKey();

    switch (this.memoryType) {
      case 'buffer':
        this.bufferMemory.delete(memoryKey);
        break;
      case 'session':
        sessionStorage.removeItem(memoryKey);
        break;
      case 'persistent':
        localStorage.removeItem(memoryKey);
        break;
    }
  }

  /**
   * Migrate memory from one type to another
   */
  migrateMemory(targetType: 'buffer' | 'session' | 'persistent'): void {
    if (targetType === this.memoryType) return;

    const currentMemory = this.getAll();
    const prevType = this.memoryType;
    this.memoryType = targetType;
    this.setAll(currentMemory);

    // Clear previous storage if different
    if (prevType !== 'buffer') {
      const memoryKey = this.getMemoryKey(prevType);
      switch (prevType) {
        case 'session':
          sessionStorage.removeItem(memoryKey);
          break;
        case 'persistent':
          localStorage.removeItem(memoryKey);
          break;
      }
    }
  }

  /**
   * Load memory from storage (for session/persistent)
   */
  private loadFromStorage(): void {
    const memoryKey = this.getMemoryKey();

    switch (this.memoryType) {
      case 'session':
        const sessionData = sessionStorage.getItem(memoryKey);
        if (sessionData) {
          try {
            const memoryItems = JSON.parse(sessionData);
            this.bufferMemory.set(memoryKey, memoryItems);
          } catch (error) {
            console.error('Failed to parse session storage data:', error);
          }
        }
        break;
      case 'persistent':
        const localData = localStorage.getItem(memoryKey);
        if (localData) {
          try {
            const memoryItems = JSON.parse(localData);
            this.bufferMemory.set(memoryKey, memoryItems);
          } catch (error) {
            console.error('Failed to parse local storage data:', error);
          }
        }
        break;
    }
  }

  /**
   * Generate storage key for the workflow
   */
  private getMemoryKey(type?: 'buffer' | 'session' | 'persistent'): string {
    const memType = type || this.memoryType;
    return `workflow_memory_${memType}_${this.workflowId}`;
  }
}

/**
 * Create a memory manager for a workflow
 */
export function createWorkflowMemory(options: WorkflowMemoryOptions): WorkflowMemory {
  return new WorkflowMemory(options);
}

/**
 * Get or create a memory instance for a workflow
 */
const memoryInstances: Map<string, WorkflowMemory> = new Map();

export function getWorkflowMemory(options: WorkflowMemoryOptions): WorkflowMemory {
  const key = `${options.workflowId}_${options.memoryType}`;

  if (!memoryInstances.has(key)) {
    memoryInstances.set(key, new WorkflowMemory(options));
  } else {
    // Update options if instance exists
    const instance = memoryInstances.get(key)!;
    instance.updateOptions(options);
  }

  return memoryInstances.get(key)!;
}

